// src/App.js (Updated with PrivateRoute)

import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// Import Components and Pages
import Header from "./components/Header";
import PrivateRoute from "./components/PrivateRoute"; // <-- NEW IMPORT
import Register from "./pages/Register";
import DonorProfile from "./pages/DonorProfile";
import Login from "./pages/Login";

const App = () => {
  return (
    <Router>
      <Header />

      <div style={{ padding: "20px" }}>
        <Routes>
          {/* Default/Home Route */}
          <Route
            path="/"
            element={
              <div style={{ textAlign: "center", marginTop: "50px" }}>
                <h2>Welcome to the Blood Donation Network</h2>
                <p>
                  Your action saves lives. Register, Login, or check your
                  Profile.
                </p>
                <p style={{ marginTop: "20px", color: "#d32f2f" }}>
                  **TEST NOTE:** The Profile link is now protected and requires
                  a successful login.
                </p>
              </div>
            }
          />

          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />

          {/* PROTECTED ROUTE: Use PrivateRoute wrapper for the DonorProfile page */}
          <Route
            path="/profile"
            element={<PrivateRoute element={DonorProfile} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
