// src/pages/SearchDonors.js (FULL CODE)

import React, { useState } from "react";
import { searchDonors } from "../services/donorService";
import FormInput from "../components/FormInput"; // Assuming FormInput will be used for consistency

// --- Component Styles ---
const styles = {
  container: {
    maxWidth: "900px",
    margin: "40px auto",
    padding: "30px",
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 6px 20px rgba(0, 0, 0, 0.1)",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
  },
  heading: {
    color: "#d32f2f",
    borderBottom: "2px solid #e0e0e0",
    paddingBottom: "10px",
    marginBottom: "25px",
  },
  searchForm: {
    display: "flex",
    gap: "15px",
    marginBottom: "30px",
    alignItems: "center",
  },
  searchButton: {
    padding: "12px 20px",
    backgroundColor: "#00796b", // Teal/Green for actions
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background-color 0.3s",
    minWidth: "150px",
  },
  resultsTable: {
    width: "100%",
    borderCollapse: "collapse",
    marginTop: "20px",
  },
  tableHeader: {
    backgroundColor: "#f7f9fc",
    borderBottom: "2px solid #e0e0e0",
  },
  th: {
    padding: "15px",
    textAlign: "left",
    color: "#546e7a",
    textTransform: "uppercase",
    fontSize: "0.9em",
  },
  td: {
    padding: "15px",
    borderBottom: "1px solid #f0f0f0",
    color: "#333",
  },
  bloodGroupBadge: {
    backgroundColor: "#d32f2f",
    color: "white",
    padding: "4px 8px",
    borderRadius: "4px",
    fontWeight: "bold",
    fontSize: "0.9em",
  },
  message: {
    textAlign: "center",
    padding: "20px",
    borderRadius: "8px",
    color: "#ff9800",
    backgroundColor: "#fff3e0",
    margin: "20px 0",
  },
};

const SearchDonors = () => {
  const [searchParams, setSearchParams] = useState({
    bloodGroup: "",
    city: "",
  });
  const [donors, setDonors] = useState([]);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setSearchParams({ ...searchParams, [e.target.name]: e.target.value });
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    // Basic validation: must provide at least one search parameter
    if (!searchParams.bloodGroup && !searchParams.city) {
      setMessage("Please enter a Blood Group or City to search.");
      setDonors([]);
      return;
    }

    setMessage("");
    setLoading(true);
    setDonors([]);

    try {
      const results = await searchDonors(searchParams);
      setDonors(results);
      setMessage(`Found ${results.length} available donor(s).`);
    } catch (err) {
      const errorMsg =
        err.message ||
        err.error ||
        "An unexpected error occurred during search.";
      // The API returns 404 with a specific message when no donors are found
      if (errorMsg.includes("No available donors")) {
        setMessage(errorMsg);
      } else {
        setMessage(`Error: ${errorMsg}`);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Find Available Donors ðŸ©¸</h2>

      <form style={styles.searchForm} onSubmit={handleSearch}>
        <div style={{ flex: 1, minWidth: "150px" }}>
          <FormInput
            type="text"
            name="bloodGroup"
            placeholder="Blood Group (e.g., O+)"
            value={searchParams.bloodGroup}
            onChange={handleChange}
          />
        </div>
        <div style={{ flex: 1, minWidth: "150px" }}>
          <FormInput
            type="text"
            name="city"
            placeholder="City"
            value={searchParams.city}
            onChange={handleChange}
          />
        </div>
        <button type="submit" style={styles.searchButton} disabled={loading}>
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {message && (
        <p
          style={{
            ...styles.message,
            backgroundColor:
              donors.length > 0 ? "#e8f5e9" : styles.message.backgroundColor,
            color: donors.length > 0 ? "#388e3c" : styles.message.color,
          }}
        >
          {message}
        </p>
      )}

      {donors.length > 0 && (
        <table style={styles.resultsTable}>
          <thead style={styles.tableHeader}>
            <tr>
              <th style={styles.th}>Blood Group</th>
              <th style={styles.th}>Name</th>
              <th style={styles.th}>City</th>
              <th style={styles.th}>Phone</th>
            </tr>
          </thead>
          <tbody>
            {donors.map((donor) => (
              <tr key={donor._id}>
                <td style={styles.td}>
                  <span style={styles.bloodGroupBadge}>{donor.bloodGroup}</span>
                </td>
                <td style={styles.td}>{donor.name}</td>
                <td style={styles.td}>{donor.city}</td>
                <td style={styles.td}>{donor.phone}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default SearchDonors;
