import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import {
  getAllDonorsAdmin,
  deleteDonorAdmin,
  toggleVerifyDonorAdmin,
} from "../services/donorService";

const AdminDashboard = () => {
  const { user, token } = useAuth();
  const [donors, setDonors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load donors on component mount
  useEffect(() => {
    if (token && user?.role === "admin") {
      fetchData();
    }
  }, [token, user]);

  const fetchData = async () => {
    try {
      setLoading(true);
      const data = await getAllDonorsAdmin(token);
      setDonors(data);
      setError(null);
    } catch (err) {
      console.error("Admin Fetch Error:", err);
      setError(err.message || "Failed to fetch donors list.");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Permanently delete this donor account?")) {
      try {
        await deleteDonorAdmin(id, token);
        setDonors(donors.filter((d) => d._id !== id));
        alert("Donor removed successfully.");
      } catch (err) {
        alert("Action failed: " + (err.message || "Unauthorized"));
      }
    }
  };

  const handleToggleVerify = async (id, currentStatus) => {
    try {
      await toggleVerifyDonorAdmin(id, !currentStatus, token);
      setDonors(
        donors.map((d) =>
          d._id === id ? { ...d, isVerified: !currentStatus } : d
        )
      );
    } catch (err) {
      alert("Update failed: " + (err.message || "Unauthorized"));
    }
  };

  // Security check: If not admin, block UI
  if (user?.role !== "admin") {
    return (
      <div style={{ textAlign: "center", padding: "100px" }}>
        <h2 style={{ color: "#cf1322" }}>ðŸš« Restricted Area</h2>
        <p>Admin privileges required to access this dashboard.</p>
      </div>
    );
  }

  return (
    <div style={{ maxWidth: "1100px", margin: "40px auto", padding: "20px" }}>
      <header
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "30px",
        }}
      >
        <div>
          <h1 style={{ color: "#1F2937", margin: 0 }}>Admin Management</h1>
          <p style={{ color: "#666" }}>
            Control donor verification and database integrity
          </p>
        </div>
        <button
          onClick={fetchData}
          style={{
            padding: "10px 20px",
            cursor: "pointer",
            borderRadius: "8px",
            border: "1px solid #ddd",
          }}
        >
          ðŸ”„ Refresh List
        </button>
      </header>

      {error && (
        <div
          style={{
            padding: "15px",
            background: "#fee2e2",
            color: "#b91c1c",
            borderRadius: "8px",
            marginBottom: "20px",
          }}
        >
          {error}
        </div>
      )}

      {loading ? (
        <p>Loading database records...</p>
      ) : (
        <div
          style={{
            background: "white",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(0,0,0,0.05)",
            overflow: "hidden",
          }}
        >
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead style={{ background: "#f9fafb" }}>
              <tr>
                <th style={thStyle}>Name / Email</th>
                <th style={thStyle}>Blood</th>
                <th style={thStyle}>City</th>
                <th style={thStyle}>Status</th>
                <th style={thStyle}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {donors.map((donor) => (
                <tr key={donor._id} style={{ borderTop: "1px solid #edf2f7" }}>
                  <td style={tdStyle}>
                    <div style={{ fontWeight: "600" }}>{donor.name}</div>
                    <div style={{ fontSize: "0.85rem", color: "#718096" }}>
                      {donor.email}
                    </div>
                  </td>
                  <td style={tdStyle}>
                    <span style={{ color: "#e53e3e", fontWeight: "bold" }}>
                      {donor.bloodGroup}
                    </span>
                  </td>
                  <td style={tdStyle}>{donor.city}</td>
                  <td style={tdStyle}>
                    {donor.isVerified ? (
                      <span
                        style={{
                          color: "#059669",
                          background: "#d1fae5",
                          padding: "4px 8px",
                          borderRadius: "4px",
                          fontSize: "0.8rem",
                        }}
                      >
                        Verified âœ…
                      </span>
                    ) : (
                      <span
                        style={{
                          color: "#d97706",
                          background: "#fef3c7",
                          padding: "4px 8px",
                          borderRadius: "4px",
                          fontSize: "0.8rem",
                        }}
                      >
                        Pending
                      </span>
                    )}
                  </td>
                  <td style={tdStyle}>
                    <button
                      onClick={() =>
                        handleToggleVerify(donor._id, donor.isVerified)
                      }
                      style={{
                        marginRight: "10px",
                        padding: "6px 12px",
                        borderRadius: "6px",
                        border: "1px solid #3b82f6",
                        color: "#3b82f6",
                        background: "white",
                        cursor: "pointer",
                      }}
                    >
                      {donor.isVerified ? "Revoke" : "Verify"}
                    </button>
                    {donor.role !== "admin" && (
                      <button
                        onClick={() => handleDelete(donor._id)}
                        style={{
                          padding: "6px 12px",
                          borderRadius: "6px",
                          border: "none",
                          background: "#ef4444",
                          color: "white",
                          cursor: "pointer",
                        }}
                      >
                        Delete
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

const thStyle = {
  textAlign: "left",
  padding: "15px",
  color: "#4a5568",
  fontSize: "0.9rem",
};
const tdStyle = { padding: "15px", fontSize: "0.95rem" };

export default AdminDashboard;
