// src/pages/Home.js (PROFESSIONAL HOSPITAL-GRADE DESIGN)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Professional Medical Color Palette
const colors = {
  primary: "#0052CC", // Deep Professional Blue (Trust, Authority)
  secondary: "#00A86B", // Medical Green (Health, Safety)
  accent: "#D32F2F", // Emergency Red (only for CTAs)
  neutral: "#2C3E50", // Dark Gray (Professional text)
  lightGray: "#F8F9FA", // Clean background
  white: "#FFFFFF",
  border: "#E0E0E0",
  success: "#00A86B",
};

// Professional Medical Icons
const MedicalCrossIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 2v20M2 12h20" />
  </svg>
);

const DropletProfessionalIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2.5"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const HospitalBuildingIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <rect x="2" y="9" width="20" height="13" rx="2" />
    <path d="M9 22V9M15 22V9M2 13h20M2 17h20M12 2v7M8 7h8" />
  </svg>
);

const ShieldCheckIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
    <path d="M9 12l2 2 4-4" />
  </svg>
);

const UsersMedicalIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M16 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="8.5" cy="7" r="4" />
    <path d="M20 8v6M23 11h-6" />
    <path d="M18 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2.5"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

const CheckCircleIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2.5"
  >
    <path d="M22 11.08V12a10 10 0 11-5.93-9.14" />
    <path d="M22 4L12 14.01l-3-3" />
  </svg>
);

// Styles - Professional Hospital Grade
const styles = {
  container: {
    fontFamily:
      "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
    color: colors.neutral,
    lineHeight: 1.6,
  },

  // === HERO SECTION ===
  heroSection: {
    background: `linear-gradient(135deg, ${colors.primary} 0%, #1A365D 100%)`,
    color: colors.white,
    padding: "120px 20px",
    position: "relative",
    overflow: "hidden",
  },

  heroBackgroundPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M45 45H20V55H45V80H55V55H80V45H55V20H45V45Z' fill='white' opacity='0.03'/%3E%3C/svg%3E")`,
    backgroundSize: "80px 80px",
  },

  heroContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    position: "relative",
    zIndex: 2,
    textAlign: "center",
  },

  heroTitle: {
    fontSize: "clamp(2.8rem, 5vw, 3.8rem)",
    fontWeight: "700",
    marginBottom: "24px",
    lineHeight: "1.1",
    letterSpacing: "-0.5px",
  },

  heroSubtitle: {
    fontSize: "1.25rem",
    maxWidth: "700px",
    margin: "0 auto 48px",
    opacity: "0.9",
    fontWeight: "400",
  },

  heroStats: {
    display: "flex",
    justifyContent: "center",
    gap: "60px",
    marginBottom: "60px",
    flexWrap: "wrap",
  },

  heroStat: {
    textAlign: "center",
  },

  heroStatNumber: {
    fontSize: "2.5rem",
    fontWeight: "700",
    marginBottom: "8px",
    color: colors.secondary,
  },

  heroStatLabel: {
    fontSize: "0.95rem",
    opacity: "0.8",
    textTransform: "uppercase",
    letterSpacing: "1px",
  },

  heroButtons: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    flexWrap: "wrap",
  },

  // === BUTTONS ===
  buttonPrimary: {
    backgroundColor: colors.accent,
    color: colors.white,
    padding: "18px 40px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: "none",
    transition: "all 0.2s cubic-bezier(0.4, 0, 0.2, 1)",
    boxShadow: `0 4px 14px ${colors.accent}40`,
  },

  buttonSecondary: {
    backgroundColor: "transparent",
    color: colors.white,
    padding: "18px 40px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: `2px solid rgba(255, 255, 255, 0.3)`,
    transition: "all 0.2s cubic-bezier(0.4, 0, 0.2, 1)",
    backdropFilter: "blur(10px)",
  },

  // === FEATURES SECTION ===
  featuresSection: {
    padding: "100px 20px",
    backgroundColor: colors.lightGray,
  },

  sectionContainer: {
    maxWidth: "1200px",
    margin: "0 auto",
  },

  sectionHeader: {
    textAlign: "center",
    marginBottom: "60px",
  },

  sectionTitle: {
    fontSize: "2.5rem",
    fontWeight: "700",
    color: colors.neutral,
    marginBottom: "16px",
  },

  sectionSubtitle: {
    fontSize: "1.1rem",
    color: "#666",
    maxWidth: "700px",
    margin: "0 auto",
  },

  featuresGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(350px, 1fr))",
    gap: "30px",
  },

  featureCard: {
    backgroundColor: colors.white,
    borderRadius: "12px",
    padding: "40px",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.04)",
    border: `1px solid ${colors.border}`,
    transition: "all 0.2s ease",
  },

  featureIcon: {
    width: "56px",
    height: "56px",
    borderRadius: "12px",
    backgroundColor: `${colors.primary}10`,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "24px",
  },

  featureTitle: {
    fontSize: "1.4rem",
    fontWeight: "600",
    color: colors.neutral,
    marginBottom: "16px",
  },

  featureDescription: {
    fontSize: "1rem",
    color: "#666",
    lineHeight: "1.6",
  },

  // === HOW IT WORKS ===
  processSection: {
    padding: "100px 20px",
    backgroundColor: colors.white,
  },

  processSteps: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "40px",
    marginTop: "60px",
  },

  processStep: {
    textAlign: "center",
    position: "relative",
  },

  stepNumber: {
    width: "60px",
    height: "60px",
    backgroundColor: colors.primary,
    color: colors.white,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.5rem",
    fontWeight: "700",
    margin: "0 auto 24px",
    position: "relative",
    zIndex: 2,
  },

  stepTitle: {
    fontSize: "1.3rem",
    fontWeight: "600",
    color: colors.neutral,
    marginBottom: "12px",
  },

  stepDescription: {
    fontSize: "1rem",
    color: "#666",
  },

  // === CTA SECTION ===
  ctaSection: {
    padding: "120px 20px",
    backgroundColor: colors.neutral,
    color: colors.white,
    textAlign: "center",
    position: "relative",
    overflow: "hidden",
  },

  ctaContent: {
    maxWidth: "800px",
    margin: "0 auto",
    position: "relative",
    zIndex: 2,
  },

  ctaTitle: {
    fontSize: "2.8rem",
    fontWeight: "700",
    marginBottom: "24px",
    lineHeight: "1.1",
  },

  ctaDescription: {
    fontSize: "1.2rem",
    marginBottom: "48px",
    opacity: "0.9",
    maxWidth: "600px",
    margin: "0 auto 48px",
  },

  ctaButton: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "20px 48px",
    borderRadius: "8px",
    fontSize: "1.2rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    transition: "all 0.2s ease",
    boxShadow: `0 4px 20px ${colors.secondary}40`,
  },

  // === FOOTER BANNER ===
  footerBanner: {
    backgroundColor: colors.lightGray,
    padding: "60px 20px",
    textAlign: "center",
    borderTop: `1px solid ${colors.border}`,
  },

  footerText: {
    color: colors.neutral,
    fontSize: "1rem",
    maxWidth: "800px",
    margin: "0 auto",
  },
};

const Home = () => {
  const [hoveredButton, setHoveredButton] = useState(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  const handleButtonHover = (type) => {
    setHoveredButton(type);
  };

  const handleCardHover = (index) => {
    setHoveredCard(index);
  };

  return (
    <div style={styles.container}>
      {/* === PROFESSIONAL HERO SECTION === */}
      <section style={styles.heroSection}>
        <div style={styles.heroBackgroundPattern} />
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            BloodLink: Hospital-Grade Donor Network
          </h1>
          <p style={styles.heroSubtitle}>
            Trusted by leading hospitals nationwide. Connecting verified donors
            with critical blood supply needs through secure, medical-grade
            protocols.
          </p>

          {/* Hero Stats */}
          <div style={styles.heroStats}>
            <div style={styles.heroStat}>
              <div style={styles.heroStatNumber}>98.7%</div>
              <div style={styles.heroStatLabel}>Match Accuracy</div>
            </div>
            <div style={styles.heroStat}>
              <div style={styles.heroStatNumber}>42min</div>
              <div style={styles.heroStatLabel}>Avg. Response Time</div>
            </div>
            <div style={styles.heroStat}>
              <div style={styles.heroStatNumber}>500+</div>
              <div style={styles.heroStatLabel}>Partner Hospitals</div>
            </div>
          </div>

          {/* Hero Buttons */}
          <div style={styles.heroButtons}>
            <Link
              to="/register"
              style={{
                ...styles.buttonPrimary,
                transform:
                  hoveredButton === "primary"
                    ? "translateY(-2px)"
                    : "translateY(0)",
                boxShadow:
                  hoveredButton === "primary"
                    ? `0 8px 20px ${colors.accent}60`
                    : `0 4px 14px ${colors.accent}40`,
              }}
              onMouseEnter={() => handleButtonHover("primary")}
              onMouseLeave={() => handleButtonHover(null)}
            >
              <DropletProfessionalIcon size={22} />
              Register as Medical Donor
              <ArrowRightIcon size={22} />
            </Link>

            <Link
              to="/search"
              style={{
                ...styles.buttonSecondary,
                backgroundColor:
                  hoveredButton === "secondary"
                    ? "rgba(255, 255, 255, 0.1)"
                    : "transparent",
                borderColor:
                  hoveredButton === "secondary"
                    ? "rgba(255, 255, 255, 0.5)"
                    : "rgba(255, 255, 255, 0.3)",
              }}
              onMouseEnter={() => handleButtonHover("secondary")}
              onMouseLeave={() => handleButtonHover(null)}
            >
              <HospitalBuildingIcon size={22} />
              Hospital Access Portal
            </Link>
          </div>
        </div>
      </section>

      {/* === KEY FEATURES SECTION === */}
      <section style={styles.featuresSection}>
        <div style={styles.sectionContainer}>
          <div style={styles.sectionHeader}>
            <h2 style={styles.sectionTitle}>Medical-Grade Platform</h2>
            <p style={styles.sectionSubtitle}>
              Designed for healthcare professionals and donors with precision,
              security, and reliability.
            </p>
          </div>

          <div style={styles.featuresGrid}>
            {[
              {
                icon: <ShieldCheckIcon size={28} color={colors.primary} />,
                title: "HIPAA Compliant Security",
                description:
                  "End-to-end encrypted platform meeting all healthcare privacy regulations and data protection standards.",
              },
              {
                icon: <HospitalBuildingIcon size={28} color={colors.primary} />,
                title: "Hospital Integration Ready",
                description:
                  "Seamless API integration with existing hospital systems for real-time inventory management.",
              },
              {
                icon: <MedicalCrossIcon size={28} color={colors.primary} />,
                title: "Verified Medical Screening",
                description:
                  "Comprehensive donor screening following FDA and WHO guidelines for blood safety.",
              },
              {
                icon: <UsersMedicalIcon size={28} color={colors.primary} />,
                title: "Emergency Response Network",
                description:
                  "24/7 coordination with trauma centers and emergency departments nationwide.",
              },
            ].map((feature, index) => (
              <div
                key={index}
                style={{
                  ...styles.featureCard,
                  transform:
                    hoveredCard === index
                      ? "translateY(-8px)"
                      : "translateY(0)",
                  boxShadow:
                    hoveredCard === index
                      ? "0 8px 30px rgba(0, 0, 0, 0.08)"
                      : "0 2px 8px rgba(0, 0, 0, 0.04)",
                }}
                onMouseEnter={() => handleCardHover(index)}
                onMouseLeave={() => handleCardHover(null)}
              >
                <div style={styles.featureIcon}>{feature.icon}</div>
                <h3 style={styles.featureTitle}>{feature.title}</h3>
                <p style={styles.featureDescription}>{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* === PROCESS SECTION === */}
      <section style={styles.processSection}>
        <div style={styles.sectionContainer}>
          <div style={styles.sectionHeader}>
            <h2 style={styles.sectionTitle}>Streamlined Donation Process</h2>
            <p style={styles.sectionSubtitle}>
              From registration to life-saving donation - precision-engineered
              for reliability.
            </p>
          </div>

          <div style={styles.processSteps}>
            {[
              {
                number: "01",
                title: "Medical Registration",
                description:
                  "Complete verified medical profile with health screening and documentation.",
              },
              {
                number: "02",
                title: "Intelligent Matching",
                description:
                  "AI-powered matching with hospital needs based on blood type, location, and urgency.",
              },
              {
                number: "03",
                title: "Certified Donation",
                description:
                  "Visit accredited medical facilities for supervised, sterile collection procedures.",
              },
            ].map((step, index) => (
              <div key={index} style={styles.processStep}>
                <div style={styles.stepNumber}>{step.number}</div>
                <h3 style={styles.stepTitle}>{step.title}</h3>
                <p style={styles.stepDescription}>{step.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* === PROFESSIONAL CTA SECTION === */}
      <section style={styles.ctaSection}>
        <div style={styles.ctaContent}>
          <h2 style={styles.ctaTitle}>Join the Professional Donor Network</h2>
          <p style={styles.ctaDescription}>
            Partner with leading hospitals and medical institutions. Your
            commitment ensures critical blood supply continuity for emergency
            and scheduled procedures.
          </p>
          <Link
            to="/register"
            style={{
              ...styles.ctaButton,
              transform:
                hoveredButton === "cta"
                  ? "translateY(-2px) scale(1.02)"
                  : "translateY(0) scale(1)",
              boxShadow:
                hoveredButton === "cta"
                  ? `0 8px 25px ${colors.secondary}60`
                  : `0 4px 20px ${colors.secondary}40`,
            }}
            onMouseEnter={() => handleButtonHover("cta")}
            onMouseLeave={() => handleButtonHover(null)}
          >
            <CheckCircleIcon size={24} />
            Begin Medical Registration
            <ArrowRightIcon size={24} />
          </Link>
        </div>
      </section>

      {/* === PROFESSIONAL FOOTER === */}
      <footer style={styles.footerBanner}>
        <p style={styles.footerText}>
          BloodLink is a registered medical service provider. All donations are
          processed through certified medical facilities following FDA, WHO, and
          local health authority guidelines.
        </p>
      </footer>
    </div>
  );
};

export default Home;
