// src/pages/DonorProfile.js (Stylized and Professional)

import React, { useState, useEffect } from "react";
import axios from "axios";

// --- Theme and Styles ---
const styles = {
  container: {
    maxWidth: "800px",
    margin: "40px auto",
    padding: "30px",
    backgroundColor: "#ffffff", // White background
    borderRadius: "12px",
    boxShadow: "0 6px 20px rgba(0, 0, 0, 0.1)",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    borderLeft: "5px solid #d32f2f", // Red accent border (Blood/Urgency)
  },
  heading: {
    color: "#00796b", // Teal/Medical Green
    borderBottom: "2px solid #e0e0e0",
    paddingBottom: "10px",
    marginBottom: "25px",
  },
  sectionTitle: {
    color: "#d32f2f", // Red for Update Section
    marginTop: "30px",
    marginBottom: "15px",
    borderBottom: "1px dashed #d32f2f50",
    paddingBottom: "5px",
  },
  infoRow: {
    display: "flex",
    padding: "10px 0",
    borderBottom: "1px dashed #e0e0e0",
    alignItems: "center",
  },
  label: {
    fontWeight: "bold",
    color: "#546e7a", // Muted grey
    width: "150px",
  },
  value: {
    color: "#333",
    flexGrow: 1,
  },
  bloodGroupBadge: {
    backgroundColor: "#d32f2f", // Deep Red
    color: "white",
    padding: "5px 10px",
    borderRadius: "4px",
    fontWeight: "bold",
    fontSize: "1.1em",
  },
  inputGroup: {
    display: "flex",
    gap: "15px",
    marginBottom: "20px",
    flexWrap: "wrap",
  },
  input: {
    padding: "10px 15px",
    border: "1px solid #b0bec5",
    borderRadius: "6px",
    fontSize: "16px",
    flex: 1,
    minWidth: "150px",
  },
  button: {
    padding: "12px 20px",
    backgroundColor: "#00796b", // Teal/Green for actions
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background-color 0.3s",
    textTransform: "uppercase",
  },
  message: {
    textAlign: "center",
    padding: "15px",
    borderRadius: "8px",
    marginTop: "20px",
  },
};

const API_URL = "http://localhost:5000/api/donors";

const DonorProfile = () => {
  const [donor, setDonor] = useState(null);
  const [updates, setUpdates] = useState({});
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(true);

  // NOTE: Token logic is correct, but we'll simulate a logged-in state
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      setMessage("Please log in to view your profile.");
      setIsLoading(false);
      setDonor({}); // Set an empty object to render the error state
      return;
    }

    const fetchProfile = async () => {
      try {
        const res = await axios.get(`${API_URL}/profile`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setDonor(res.data);
        setMessage("");
      } catch (err) {
        setMessage(
          err.response?.data?.message ||
            "Failed to fetch profile. Session expired?"
        );
        console.error("Profile fetch error:", err);
        setDonor({}); // Clear donor on error
      } finally {
        setIsLoading(false);
      }
    };

    fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    setUpdates({ ...updates, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    if (Object.keys(updates).length === 0) {
      setMessage("No changes detected.");
      return;
    }

    try {
      const res = await axios.put(`${API_URL}/profile`, updates, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Update the main donor state with the new data from the backend
      setDonor(res.data);
      setUpdates({}); // Clear the updates form
      setMessage("Profile updated successfully! Thank you.");
    } catch (err) {
      setMessage(err.response?.data?.message || "Error updating profile.");
      console.error("Profile update error:", err);
    }
  };

  if (isLoading) {
    return (
      <div
        style={{
          ...styles.container,
          textAlign: "center",
          backgroundColor: "#e8f5e9",
        }}
      >
        Loading Profile...
      </div>
    );
  }

  if (!donor || !donor.name) {
    // Renders if token is missing or fetching failed
    return (
      <div
        style={{
          ...styles.container,
          backgroundColor: "#ffebee",
          borderLeft: "5px solid #f44336",
        }}
      >
        <p
          style={{ textAlign: "center", color: "#f44336", fontWeight: "bold" }}
        >
          Authorization Required: {message || "Could not load donor data."}
        </p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>
        Donor Profile üßë‚Äç‚öïÔ∏è{" "}
        <small
          style={{
            fontWeight: 400,
            fontSize: "0.6em",
            display: "block",
            color: "#546e7a",
          }}
        >
          Manage your vital information
        </small>
      </h2>

      {/* --- Current Information Section --- */}
      <div style={styles.infoRow}>
        <span style={styles.label}>Name:</span>
        <span style={styles.value}>{donor.name}</span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>Email:</span>
        <span style={styles.value}>{donor.email}</span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>Blood Group:</span>
        <span style={styles.value}>
          <span style={styles.bloodGroupBadge}>{donor.bloodGroup}</span>
        </span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>City:</span>
        <span style={styles.value}>{donor.city}</span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>Phone:</span>
        <span style={styles.value}>{donor.phone}</span>
      </div>

      {/* --- Update Form Section --- */}
      <h3 style={styles.sectionTitle}>Update Contact & Location</h3>
      <div style={styles.inputGroup}>
        <input
          placeholder={`Update City (Current: ${donor.city})`}
          name="city"
          onChange={handleChange}
          style={styles.input}
          value={updates.city || ""}
        />
        <input
          placeholder={`Update Phone (Current: ${donor.phone})`}
          name="phone"
          onChange={handleChange}
          style={styles.input}
          value={updates.phone || ""}
        />
        <input
          placeholder={`Update Blood Group (Current: ${donor.bloodGroup})`}
          name="bloodGroup"
          onChange={handleChange}
          style={styles.input}
          value={updates.bloodGroup || ""}
        />
      </div>

      <button onClick={handleUpdate} style={styles.button}>
        Save Changes
      </button>

      {/* --- Feedback Message --- */}
      {message && (
        <p
          style={{
            ...styles.message,
            backgroundColor: message.includes("successfully")
              ? "#e8f5e9"
              : "#fff3e0",
            color: message.includes("successfully") ? "#388e3c" : "#ff9800",
          }}
        >
          {message}
        </p>
      )}
    </div>
  );
};

export default DonorProfile;
