// src/pages/Home.js (FINALIZED DESIGN - Responsive and Thematic Background)

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// Modern Color Palette (Warm & Urgent)
const colors = {
  primary: "#D32F2F", // Deep Red (Urgency/Accent)
  secondary: "#FF9800", // Warm Orange (Energy)
  dark: "#1A237E", // Deep Navy Blue (Trust/Professional)
  lightBg: "#FCE4EC", // Very light Pink/Red for background
  white: "#FFFFFF",
  darkText: "#263238",
  grayText: "#546E7A",
};

// --- SIMPLIFIED SVG ICONS (Kept the same for simplicity) ---
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);
const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);
const ChevronRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

// --- HELPER FOR RESPONSIVENESS (Simulating Media Queries) ---
const getResponsiveStyles = (viewportWidth) => {
  const isMobile = viewportWidth < 768;

  return {
    statsGrid: {
      flexDirection: isMobile ? "column" : "row",
      alignItems: isMobile ? "center" : "stretch",
    },
    statCard: {
      width: isMobile ? "100%" : "auto",
      maxWidth: isMobile ? "400px" : "none",
    },
    heroWrapper: {
      padding: isMobile ? "60px 15px" : "100px 20px",
    },
  };
};

// --- STYLES ---
const styles = {
  container: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: colors.lightBg,
    paddingBottom: "80px",
  },

  // Hero Section (New Gradient: Red to Orange)
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
    position: "relative", // For absolute positioning of pattern
    overflow: "hidden",
  },

  // NEW STYLE: Subtle repeating medical pattern overlay
  heroPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    // Repeating medical cross pattern in white, highly transparent
    backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M45 45H20V55H45V80H55V55H80V45H55V20H45V45Z' fill='white' opacity='0.1'/%3E%3C/svg%3E")`,
    backgroundSize: "150px 150px",
    opacity: 0.3,
    zIndex: 5,
  },

  heroContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    textAlign: "center",
    position: "relative", // Ensures content sits above the pattern
    zIndex: 10,
  },

  heroTitle: {
    fontSize: "clamp(2.5rem, 5vw, 4rem)",
    fontWeight: 800,
    color: colors.white,
    marginBottom: "20px",
    lineHeight: 1.1,
  },
  heroSubtitle: {
    fontSize: "clamp(1.1rem, 2vw, 1.4rem)",
    color: "rgba(255, 255, 255, 0.95)",
    marginBottom: "40px",
    lineHeight: 1.6,
    maxWidth: "800px",
    margin: "0 auto 40px",
  },

  // CTA Buttons
  ctaContainer: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "20px",
  },
  ctaButton: {
    padding: "16px 30px",
    borderRadius: "50px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.3s",
    display: "inline-flex",
    alignItems: "center",
    gap: "10px",
    border: "2px solid transparent",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
    minWidth: "220px",
    justifyContent: "center",
  },
  primaryButton: {
    backgroundColor: colors.dark,
    color: colors.white,
    border: `2px solid ${colors.dark}`,
  },
  secondaryButton: {
    backgroundColor: colors.white,
    color: colors.primary,
    border: `2px solid ${colors.white}`,
  },

  // Stats Section
  statsSection: {
    maxWidth: "1200px",
    margin: "-60px auto 40px",
    padding: "0 20px",
    position: "relative",
    zIndex: 10,
  },
  statsGrid: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "20px",
  },
  statCard: {
    flex: "1 1 280px",
    backgroundColor: colors.white,
    borderRadius: "16px",
    padding: "30px",
    boxShadow: "0 20px 40px rgba(0, 0, 0, 0.1)",
    transition: "all 0.3s ease",
    textAlign: "center",
  },
  statCardHover: {
    transform: "translateY(-10px)",
    boxShadow: "0 30px 60px rgba(0, 0, 0, 0.25)",
  },
  statNumber: {
    fontSize: "3rem",
    fontWeight: 800,
    color: colors.primary,
    lineHeight: 1,
    marginBottom: "10px",
  },
  statLabel: {
    fontSize: "1.05rem",
    color: colors.grayText,
    fontWeight: 500,
  },

  // Final CTA Banner
  finalCta: {
    maxWidth: "1000px",
    margin: "80px auto 40px",
    padding: "40px 30px",
    background: colors.white,
    borderRadius: "16px",
    textAlign: "center",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.05)",
  },
  finalCtaTitle: {
    fontSize: "2rem",
    color: colors.darkText,
    marginBottom: "15px",
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = useState(null);
  const [viewportWidth, setViewportWidth] = useState(window.innerWidth);

  // Hook to track window size for responsive styles
  useEffect(() => {
    const handleResize = () => setViewportWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const responsiveStyles = getResponsiveStyles(viewportWidth);

  // General button hover handler
  const handleHover = (e, isHovered, isPrimary) => {
    const target = e.currentTarget;
    target.style.transform = isHovered ? "translateY(-3px)" : "translateY(0)";
    target.style.boxShadow = isHovered
      ? "0 8px 20px rgba(0, 0, 0, 0.4)"
      : "0 4px 15px rgba(0, 0, 0, 0.2)";

    if (isPrimary) {
      target.style.backgroundColor = isHovered ? colors.primary : colors.dark;
      target.style.color = isHovered ? colors.white : colors.white;
    } else {
      target.style.backgroundColor = isHovered ? colors.primary : colors.white;
      target.style.color = isHovered ? colors.white : colors.primary;
    }
  };

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <div style={{ ...styles.heroWrapper, ...responsiveStyles.heroWrapper }}>
        {/* THEMATIC BACKGROUND PATTERN */}
        <div style={styles.heroPattern} />

        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>Your Immediate Link to Life-Savers.</h1>
          <p style={styles.heroSubtitle}>
            A responsive, secure network connecting those in urgent need with
            verified, nearby blood donors. Fast, reliable, and crucial support
            when it matters most.
          </p>
          <div style={styles.ctaContainer}>
            <Link
              to="/register"
              style={{ ...styles.ctaButton, ...styles.primaryButton }}
              onMouseOver={(e) => handleHover(e, true, true)}
              onMouseOut={(e) => handleHover(e, false, true)}
            >
              <DropletIcon size={20} color={colors.white} />
              Register to Donate
            </Link>

            <Link
              to="/search"
              style={{ ...styles.ctaButton, ...styles.secondaryButton }}
              onMouseOver={(e) => handleHover(e, true, false)}
              onMouseOut={(e) => handleHover(e, false, false)}
            >
              <UsersIcon size={20} color={colors.primary} />
              Find Donors Instantly
              <ChevronRightIcon size={20} color={colors.primary} />
            </Link>
          </div>
        </div>
      </div>

      {/* --- STATS SECTION (Responsive Grid) --- */}
      <section style={styles.statsSection}>
        <div style={{ ...styles.statsGrid, ...responsiveStyles.statsGrid }}>
          {[
            { number: "24/7", label: "Emergency Support" },
            { number: "15K+", label: "Verified Donors" },
            { number: "98%", label: "Response Rate" },
          ].map((stat, index) => (
            <div
              key={index}
              style={{
                ...styles.statCard,
                ...responsiveStyles.statCard,
                ...(hoveredStat === index ? styles.statCardHover : {}),
              }}
              onMouseOver={() => setHoveredStat(index)}
              onMouseOut={() => setHoveredStat(null)}
            >
              <div style={styles.statNumber}>{stat.number}</div>
              <div style={styles.statLabel}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- FINAL CTA BANNER --- */}
      <section style={styles.finalCta}>
        <h2 style={styles.finalCtaTitle}>Ready to be a Lifeline?</h2>
        <p style={{ color: colors.grayText, marginBottom: "30px" }}>
          Your profile helps save lives. Login or Register to keep your
          availability up to date.
        </p>
        <Link
          to="/login"
          style={{
            ...styles.ctaButton,
            backgroundColor: colors.secondary,
            color: colors.white,
            boxShadow: `0 4px 15px rgba(255, 152, 0, 0.4)`,
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.backgroundColor = colors.primary;
            e.currentTarget.style.boxShadow = `0 8px 20px rgba(211, 47, 47, 0.4)`;
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.backgroundColor = colors.secondary;
            e.currentTarget.style.boxShadow = `0 4px 15px rgba(255, 152, 0, 0.4)`;
          }}
        >
          Access My Profile
        </Link>
      </section>
    </div>
  );
};

export default Home;
