// src/pages/Home.js (MODERN DESIGN - No External Icons Dependency)

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// Modern Color Palette - Sophisticated & Trustworthy
const colors = {
  primary: "#3B82F6", // Professional Blue
  secondary: "#8B5CF6", // Elegant Purple
  accent: "#10B981", // Life-affirming Emerald
  dark: "#1F2937", // Deep Charcoal
  light: "#F8FAFC", // Clean Off-white
  gray: "#6B7280", // Medium Gray
  gradientStart: "#6366F1",
  gradientEnd: "#3B82F6",
};

// SVG Icons as React Components
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ChevronRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

const ClockIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <circle cx="12" cy="12" r="10" />
    <polyline points="12 6 12 12 16 14" />
  </svg>
);

const MapPinIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0118 0z" />
    <circle cx="12" cy="10" r="3" />
  </svg>
);

const AwardIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <circle cx="12" cy="8" r="7" />
    <polyline points="8.21 13.89 7 23 12 20 17 23 15.79 13.88" />
  </svg>
);

// Modern Design System
const styles = {
  // Main Container
  container: {
    fontFamily:
      "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
    color: colors.dark,
    overflowX: "hidden",
  },

  // --- MODERN HERO SECTION ---
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.gradientStart} 0%, ${colors.gradientEnd} 100%)`,
    padding: "120px 0 100px",
    position: "relative",
    overflow: "hidden",
    minHeight: "85vh",
    display: "flex",
    alignItems: "center",
  },

  heroPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `radial-gradient(circle at 25% 25%, rgba(255,255,255,0.1) 2px, transparent 2px)`,
    backgroundSize: "40px 40px",
    opacity: 0.4,
  },

  heroContent: {
    maxWidth: "1400px",
    margin: "0 auto",
    padding: "0 40px",
    position: "relative",
    zIndex: 10,
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "80px",
    alignItems: "center",
  },

  heroText: {
    maxWidth: "700px",
  },

  heroTitle: {
    fontSize: "clamp(3rem, 5vw, 4.5rem)",
    fontWeight: 800,
    lineHeight: 1.1,
    color: "white",
    marginBottom: "24px",
    background: "linear-gradient(to right, #ffffff, #E0F2FE)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  heroSubtitle: {
    fontSize: "clamp(1.1rem, 2vw, 1.3rem)",
    color: "rgba(255, 255, 255, 0.9)",
    lineHeight: 1.7,
    marginBottom: "48px",
    maxWidth: "600px",
  },

  // Modern CTA Buttons
  ctaContainer: {
    display: "flex",
    flexWrap: "wrap",
    gap: "20px",
    alignItems: "center",
  },

  ctaButton: {
    padding: "18px 40px",
    borderRadius: "16px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "12px",
    border: "none",
    cursor: "pointer",
    position: "relative",
    overflow: "hidden",
  },

  primaryButton: {
    background: "white",
    color: colors.primary,
    boxShadow: "0 20px 40px rgba(59, 130, 246, 0.3)",
  },

  secondaryButton: {
    background: "transparent",
    color: "white",
    border: "2px solid rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
  },

  // Hero Illustration/Stats
  heroStats: {
    background: "rgba(255, 255, 255, 0.1)",
    backdropFilter: "blur(20px)",
    borderRadius: "24px",
    padding: "40px",
    border: "1px solid rgba(255, 255, 255, 0.2)",
  },

  // --- MODERN STATS SECTION ---
  statsSection: {
    maxWidth: "1400px",
    margin: "-60px auto 100px",
    padding: "0 40px",
    position: "relative",
    zIndex: 20,
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "30px",
  },

  statCard: {
    background: "white",
    borderRadius: "24px",
    padding: "40px 30px",
    boxShadow: "0 20px 60px rgba(0, 0, 0, 0.08)",
    transition: "all 0.4s ease",
    border: "1px solid rgba(0, 0, 0, 0.05)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    textAlign: "center",
  },

  statIcon: {
    width: "64px",
    height: "64px",
    borderRadius: "16px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "24px",
  },

  iconBg1: { background: "linear-gradient(135deg, #3B82F6, #8B5CF6)" },
  iconBg2: { background: "linear-gradient(135deg, #10B981, #3B82F6)" },
  iconBg3: { background: "linear-gradient(135deg, #8B5CF6, #10B981)" },

  statNumber: {
    fontSize: "3.5rem",
    fontWeight: 800,
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
    lineHeight: 1,
    marginBottom: "8px",
  },

  statLabel: {
    fontSize: "1.1rem",
    color: colors.gray,
    fontWeight: 500,
    lineHeight: 1.5,
  },

  // --- FEATURES SECTION ---
  featuresSection: {
    maxWidth: "1400px",
    margin: "0 auto 120px",
    padding: "0 40px",
  },

  sectionHeader: {
    textAlign: "center",
    marginBottom: "80px",
  },

  sectionTitle: {
    fontSize: "clamp(2rem, 4vw, 3rem)",
    fontWeight: 800,
    color: colors.dark,
    marginBottom: "16px",
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  sectionSubtitle: {
    fontSize: "1.2rem",
    color: colors.gray,
    maxWidth: "600px",
    margin: "0 auto",
    lineHeight: 1.6,
  },

  featuresGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(350px, 1fr))",
    gap: "40px",
  },

  featureCard: {
    background: colors.light,
    borderRadius: "24px",
    padding: "48px 40px",
    transition: "all 0.4s ease",
    border: "1px solid rgba(0, 0, 0, 0.05)",
  },

  featureIcon: {
    width: "56px",
    height: "56px",
    borderRadius: "14px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "32px",
    background: "white",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.05)",
  },

  featureTitle: {
    fontSize: "1.5rem",
    fontWeight: 700,
    color: colors.dark,
    marginBottom: "16px",
  },

  featureDescription: {
    fontSize: "1.1rem",
    color: colors.gray,
    lineHeight: 1.7,
    marginBottom: "24px",
  },

  featureLink: {
    color: colors.primary,
    textDecoration: "none",
    fontWeight: 600,
    display: "inline-flex",
    alignItems: "center",
    gap: "8px",
    fontSize: "1rem",
  },

  // --- CTA BANNER ---
  ctaBanner: {
    maxWidth: "1400px",
    margin: "0 auto 100px",
    padding: "0 40px",
  },

  bannerContent: {
    background: `linear-gradient(135deg, ${colors.accent}, ${colors.primary})`,
    borderRadius: "32px",
    padding: "80px 60px",
    textAlign: "center",
    position: "relative",
    overflow: "hidden",
