// src/pages/Home.js (MODERN DESIGN - Sophisticated, Professional, No Red Color)

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  Droplet,
  Heart,
  Users,
  Shield,
  ChevronRight,
  Clock,
  MapPin,
  Award,
} from "lucide-react";

// Modern Color Palette - Sophisticated & Trustworthy
const colors = {
  primary: "#3B82F6", // Professional Blue
  secondary: "#8B5CF6", // Elegant Purple
  accent: "#10B981", // Life-affirming Emerald
  dark: "#1F2937", // Deep Charcoal
  light: "#F8FAFC", // Clean Off-white
  gray: "#6B7280", // Medium Gray
  gradientStart: "#6366F1",
  gradientEnd: "#3B82F6",
};

// Modern Design System
const styles = {
  // Main Container
  container: {
    fontFamily:
      "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
    color: colors.dark,
    overflowX: "hidden",
  },

  // --- MODERN HERO SECTION ---
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.gradientStart} 0%, ${colors.gradientEnd} 100%)`,
    padding: "120px 0 100px",
    position: "relative",
    overflow: "hidden",
    minHeight: "85vh",
    display: "flex",
    alignItems: "center",
  },

  heroPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `radial-gradient(circle at 25% 25%, rgba(255,255,255,0.1) 2px, transparent 2px)`,
    backgroundSize: "40px 40px",
    opacity: 0.4,
  },

  heroContent: {
    maxWidth: "1400px",
    margin: "0 auto",
    padding: "0 40px",
    position: "relative",
    zIndex: 10,
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "80px",
    alignItems: "center",
  },

  heroText: {
    maxWidth: "700px",
  },

  heroTitle: {
    fontSize: "clamp(3rem, 5vw, 4.5rem)",
    fontWeight: 800,
    lineHeight: 1.1,
    color: "white",
    marginBottom: "24px",
    background: "linear-gradient(to right, #ffffff, #E0F2FE)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  heroSubtitle: {
    fontSize: "clamp(1.1rem, 2vw, 1.3rem)",
    color: "rgba(255, 255, 255, 0.9)",
    lineHeight: 1.7,
    marginBottom: "48px",
    maxWidth: "600px",
  },

  // Modern CTA Buttons
  ctaContainer: {
    display: "flex",
    flexWrap: "wrap",
    gap: "20px",
    alignItems: "center",
  },

  ctaButton: {
    padding: "18px 40px",
    borderRadius: "16px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "12px",
    border: "none",
    cursor: "pointer",
    position: "relative",
    overflow: "hidden",
  },

  primaryButton: {
    background: "white",
    color: colors.primary,
    boxShadow: "0 20px 40px rgba(59, 130, 246, 0.3)",
  },

  secondaryButton: {
    background: "transparent",
    color: "white",
    border: "2px solid rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
  },

  // Hero Illustration/Stats
  heroStats: {
    background: "rgba(255, 255, 255, 0.1)",
    backdropFilter: "blur(20px)",
    borderRadius: "24px",
    padding: "40px",
    border: "1px solid rgba(255, 255, 255, 0.2)",
  },

  // --- MODERN STATS SECTION ---
  statsSection: {
    maxWidth: "1400px",
    margin: "-60px auto 100px",
    padding: "0 40px",
    position: "relative",
    zIndex: 20,
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "30px",
  },

  statCard: {
    background: "white",
    borderRadius: "24px",
    padding: "40px 30px",
    boxShadow: "0 20px 60px rgba(0, 0, 0, 0.08)",
    transition: "all 0.4s ease",
    border: "1px solid rgba(0, 0, 0, 0.05)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    textAlign: "center",
  },

  statIcon: {
    width: "64px",
    height: "64px",
    borderRadius: "16px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "24px",
  },

  iconBg1: { background: "linear-gradient(135deg, #3B82F6, #8B5CF6)" },
  iconBg2: { background: "linear-gradient(135deg, #10B981, #3B82F6)" },
  iconBg3: { background: "linear-gradient(135deg, #8B5CF6, #10B981)" },

  statNumber: {
    fontSize: "3.5rem",
    fontWeight: 800,
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
    lineHeight: 1,
    marginBottom: "8px",
  },

  statLabel: {
    fontSize: "1.1rem",
    color: colors.gray,
    fontWeight: 500,
    lineHeight: 1.5,
  },

  // --- FEATURES SECTION ---
  featuresSection: {
    maxWidth: "1400px",
    margin: "0 auto 120px",
    padding: "0 40px",
  },

  sectionHeader: {
    textAlign: "center",
    marginBottom: "80px",
  },

  sectionTitle: {
    fontSize: "clamp(2rem, 4vw, 3rem)",
    fontWeight: 800,
    color: colors.dark,
    marginBottom: "16px",
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  sectionSubtitle: {
    fontSize: "1.2rem",
    color: colors.gray,
    maxWidth: "600px",
    margin: "0 auto",
    lineHeight: 1.6,
  },

  featuresGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(350px, 1fr))",
    gap: "40px",
  },

  featureCard: {
    background: colors.light,
    borderRadius: "24px",
    padding: "48px 40px",
    transition: "all 0.4s ease",
    border: "1px solid rgba(0, 0, 0, 0.05)",
  },

  featureIcon: {
    width: "56px",
    height: "56px",
    borderRadius: "14px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "32px",
    background: "white",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.05)",
  },

  featureTitle: {
    fontSize: "1.5rem",
    fontWeight: 700,
    color: colors.dark,
    marginBottom: "16px",
  },

  featureDescription: {
    fontSize: "1.1rem",
    color: colors.gray,
    lineHeight: 1.7,
    marginBottom: "24px",
  },

  featureLink: {
    color: colors.primary,
    textDecoration: "none",
    fontWeight: 600,
    display: "inline-flex",
    alignItems: "center",
    gap: "8px",
    fontSize: "1rem",
  },

  // --- CTA BANNER ---
  ctaBanner: {
    maxWidth: "1400px",
    margin: "0 auto 100px",
    padding: "0 40px",
  },

  bannerContent: {
    background: `linear-gradient(135deg, ${colors.accent}, ${colors.primary})`,
    borderRadius: "32px",
    padding: "80px 60px",
    textAlign: "center",
    position: "relative",
    overflow: "hidden",
  },

  bannerTitle: {
    fontSize: "clamp(2rem, 4vw, 3rem)",
    fontWeight: 800,
    color: "white",
    marginBottom: "24px",
  },

  bannerSubtitle: {
    fontSize: "1.2rem",
    color: "rgba(255, 255, 255, 0.9)",
    maxWidth: "700px",
    margin: "0 auto 48px",
    lineHeight: 1.7,
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = useState(null);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 100);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleMouseOver = (e, isPrimary = true) => {
    e.currentTarget.style.transform = "translateY(-8px) scale(1.02)";
    e.currentTarget.style.boxShadow = isPrimary
      ? "0 30px 60px rgba(59, 130, 246, 0.4)"
      : "0 30px 60px rgba(255, 255, 255, 0.2)";
  };

  const handleMouseOut = (e, isPrimary = true) => {
    e.currentTarget.style.transform = "translateY(0) scale(1)";
    e.currentTarget.style.boxShadow = isPrimary
      ? "0 20px 40px rgba(59, 130, 246, 0.3)"
      : "0 10px 30px rgba(255, 255, 255, 0.1)";
  };

  return (
    <div style={styles.container}>
      {/* --- MODERN HERO SECTION --- */}
      <div style={styles.heroWrapper}>
        <div style={styles.heroPattern} />
        <div style={styles.heroContent}>
          <div style={styles.heroText}>
            <h1 style={styles.heroTitle}>
              Give Life.
              <br />
              <span style={{ display: "block", fontSize: "0.85em" }}>
                Transform Futures.
              </span>
            </h1>

            <p style={styles.heroSubtitle}>
              Join our community of life-savers. Every donation creates a ripple
              effect of hope, healing, and second chances. Your simple act can
              rewrite someone's story.
            </p>

            <div style={styles.ctaContainer}>
              <Link
                to="/register"
                style={{ ...styles.ctaButton, ...styles.primaryButton }}
                onMouseOver={(e) => handleMouseOver(e, true)}
                onMouseOut={(e) => handleMouseOut(e, true)}
              >
                <Droplet size={20} />
                Become a Donor
                <ChevronRight size={20} />
              </Link>

              <Link
                to="/search"
                style={{ ...styles.ctaButton, ...styles.secondaryButton }}
                onMouseOver={(e) => handleMouseOver(e, false)}
                onMouseOut={(e) => handleMouseOut(e, false)}
              >
                <Users size={20} />
                Find Donors
                <ChevronRight size={20} />
              </Link>
            </div>

            {/* Trust Indicators */}
            <div
              style={{
                display: "flex",
                gap: "32px",
                marginTop: "48px",
                flexWrap: "wrap",
              }}
            >
              <div
                style={{ display: "flex", alignItems: "center", gap: "8px" }}
              >
                <Shield size={20} color="rgba(255,255,255,0.9)" />
                <span
                  style={{
                    color: "rgba(255,255,255,0.9)",
                    fontSize: "0.95rem",
                  }}
                >
                  Verified & Safe
                </span>
              </div>
              <div
                style={{ display: "flex", alignItems: "center", gap: "8px" }}
              >
                <Clock size={20} color="rgba(255,255,255,0.9)" />
                <span
                  style={{
                    color: "rgba(255,255,255,0.9)",
                    fontSize: "0.95rem",
                  }}
                >
                  Quick Process
                </span>
              </div>
              <div
                style={{ display: "flex", alignItems: "center", gap: "8px" }}
              >
                <Award size={20} color="rgba(255,255,255,0.9)" />
                <span
                  style={{
                    color: "rgba(255,255,255,0.9)",
                    fontSize: "0.95rem",
                  }}
                >
                  Certified Network
                </span>
              </div>
            </div>
          </div>

          {/* Hero Stats Panel */}
          <div style={styles.heroStats}>
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "1fr 1fr",
                gap: "30px",
                marginBottom: "40px",
              }}
            >
              <div style={{ textAlign: "center" }}>
                <div
                  style={{
                    fontSize: "2.5rem",
                    fontWeight: 800,
                    color: "white",
                    marginBottom: "8px",
                  }}
                >
                  1.8M+
                </div>
                <div
                  style={{ color: "rgba(255,255,255,0.8)", fontSize: "0.9rem" }}
                >
                  Lives Impacted
                </div>
              </div>
              <div style={{ textAlign: "center" }}>
                <div
                  style={{
                    fontSize: "2.5rem",
                    fontWeight: 800,
                    color: "white",
                    marginBottom: "8px",
                  }}
                >
                  92%
                </div>
                <div
                  style={{ color: "rgba(255,255,255,0.8)", fontSize: "0.9rem" }}
                >
                  Response Rate
                </div>
              </div>
            </div>
            <div
              style={{
                background: "rgba(255, 255, 255, 0.1)",
                padding: "24px",
                borderRadius: "16px",
                border: "1px solid rgba(255, 255, 255, 0.1)",
              }}
            >
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: "12px",
                  marginBottom: "12px",
                }}
              >
                <MapPin size={18} color="rgba(255,255,255,0.9)" />
                <span style={{ color: "white", fontWeight: 600 }}>
                  Active Now
                </span>
              </div>
              <div
                style={{ color: "rgba(255,255,255,0.8)", fontSize: "0.95rem" }}
              >
                2,847 donors available in your region
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* --- MODERN STATS SECTION --- */}
      <section style={styles.statsSection}>
        <div style={styles.statsGrid}>
          {[
            {
              number: "50K+",
              label: "Active Donors",
              icon: <Users size={28} color="white" />,
              iconBg: styles.iconBg1,
            },
            {
              number: "24/7",
              label: "Emergency Support",
              icon: <Heart size={28} color="white" />,
              iconBg: styles.iconBg2,
            },
            {
              number: "98%",
              label: "Satisfaction Rate",
              icon: <Shield size={28} color="white" />,
              iconBg: styles.iconBg3,
            },
            {
              number: "1.8M+",
              label: "Lives Transformed",
              icon: <Droplet size={28} color="white" />,
              iconBg: styles.iconBg1,
            },
          ].map((stat, index) => (
            <div
              key={index}
              style={
                hoveredStat === index
                  ? {
                      ...styles.statCard,
                      transform: "translateY(-10px)",
                      boxShadow: "0 40px 80px rgba(0, 0, 0, 0.12)",
                    }
                  : styles.statCard
              }
              onMouseOver={() => setHoveredStat(index)}
              onMouseOut={() => setHoveredStat(null)}
            >
              <div style={{ ...styles.statIcon, ...stat.iconBg }}>
                {stat.icon}
              </div>
              <div style={styles.statNumber}>{stat.number}</div>
              <div style={styles.statLabel}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- FEATURES SECTION --- */}
      <section style={styles.featuresSection}>
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Why Choose LifeShare?</h2>
          <p style={styles.sectionSubtitle}>
            We've reimagined blood donation for the modern worldâ€”making it
            simpler, smarter, and more impactful than ever before.
          </p>
        </div>

        <div style={styles.featuresGrid}>
          {[
            {
              title: "Smart Matching",
              description:
                "Our intelligent algorithm matches donors with recipients based on location, blood type, and urgency.",
              icon: <Heart size={24} color={colors.primary} />,
              link: "/how-it-works",
            },
            {
              title: "Digital Health Tracker",
              description:
                "Monitor your donation history, health metrics, and impact over time with our digital dashboard.",
              icon: <Shield size={24} color={colors.primary} />,
              link: "/dashboard",
            },
            {
              title: "Community Support",
              description:
                "Join local donor communities, participate in events, and connect with fellow life-savers.",
              icon: <Users size={24} color={colors.primary} />,
              link: "/community",
            },
          ].map((feature, index) => (
            <div
              key={index}
              style={styles.featureCard}
              onMouseOver={(e) => {
                e.currentTarget.style.transform = "translateY(-8px)";
                e.currentTarget.style.boxShadow =
                  "0 30px 60px rgba(0, 0, 0, 0.1)";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.boxShadow = "none";
              }}
            >
              <div style={styles.featureIcon}>{feature.icon}</div>
              <h3 style={styles.featureTitle}>{feature.title}</h3>
              <p style={styles.featureDescription}>{feature.description}</p>
              <Link to={feature.link} style={styles.featureLink}>
                Learn more <ChevronRight size={16} />
              </Link>
            </div>
          ))}
        </div>
      </section>

      {/* --- FINAL CTA BANNER --- */}
      <section style={styles.ctaBanner}>
        <div style={styles.bannerContent}>
          <h2 style={styles.bannerTitle}>Ready to Make a Difference?</h2>
          <p style={styles.bannerSubtitle}>
            Join thousands of donors who are transforming lives every day. Your
            journey as a life-saver starts with a single click.
          </p>
          <Link
            to="/register"
            style={{
              ...styles.ctaButton,
              ...styles.primaryButton,
              background: "white",
              color: colors.primary,
              fontSize: "1.2rem",
              padding: "22px 50px",
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.transform = "translateY(-8px) scale(1.05)";
              e.currentTarget.style.boxShadow =
                "0 40px 80px rgba(255, 255, 255, 0.3)";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.transform = "translateY(0) scale(1)";
              e.currentTarget.style.boxShadow =
                "0 20px 40px rgba(255, 255, 255, 0.2)";
            }}
          >
            <Droplet size={24} />
            Start Your Journey
            <ChevronRight size={24} />
          </Link>
        </div>
      </section>
    </div>
