// src/pages/Home.js (REAL-WORLD HOSPITAL/BLOOD BANK DESIGN)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Professional Blood Bank Color Scheme (Inspired by real blood banks)
const colors = {
  primary: "#C62828", // Medical Alert Red (like Red Cross)
  secondary: "#1A237E", // Trust Navy Blue
  accent: "#2E7D32", // Medical Green
  lightRed: "#FFEBEE", // Light red background
  white: "#FFFFFF",
  darkText: "#212121",
  lightText: "#757575",
  cardBg: "rgba(255, 255, 255, 0.95)",
};

// Professional Medical Icons (Similar to real blood bank websites)
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill={color}>
    <path d="M12,2.69l5.66,5.66a8,8,0,1,1-11.31,0Z" />
  </svg>
);

const HospitalIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M2 22h20M5 22V9a4 4 0 014-4h6a4 4 0 014 4v13M5 10h14M12 6v4M9 8h6" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ClockIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <circle cx="12" cy="12" r="10" />
    <polyline points="12 6 12 12 16 14" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

const PhoneIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z" />
  </svg>
);

// Styles inspired by real blood bank websites
const styles = {
  container: {
    fontFamily: "'Roboto', 'Segoe UI', Arial, sans-serif",
    color: colors.darkText,
  },

  // === HERO SECTION with Medical Background ===
  heroSection: {
    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1519494026892-80bbd2d6fd0d?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    color: colors.white,
    padding: "120px 20px",
    minHeight: "90vh",
    display: "flex",
    alignItems: "center",
    position: "relative",
  },

  heroContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    width: "100%",
  },

  heroTitle: {
    fontSize: "clamp(2.8rem, 6vw, 4rem)",
    fontWeight: "700",
    marginBottom: "24px",
    lineHeight: "1.2",
    textShadow: "2px 2px 4px rgba(0,0,0,0.3)",
  },

  heroHighlight: {
    color: colors.primary,
  },

  heroSubtitle: {
    fontSize: "1.3rem",
    maxWidth: "700px",
    marginBottom: "40px",
    lineHeight: "1.6",
    opacity: "0.9",
  },

  emergencyAlert: {
    backgroundColor: "rgba(198, 40, 40, 0.9)",
    color: colors.white,
    padding: "15px 25px",
    borderRadius: "8px",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    marginBottom: "40px",
    boxShadow: "0 4px 15px rgba(198, 40, 40, 0.4)",
  },

  heroButtons: {
    display: "flex",
    flexWrap: "wrap",
    gap: "20px",
    marginTop: "40px",
  },

  buttonPrimary: {
    backgroundColor: colors.primary,
    color: colors.white,
    padding: "18px 40px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: "none",
    transition: "all 0.3s ease",
    boxShadow: "0 4px 15px rgba(198, 40, 40, 0.4)",
  },

  buttonSecondary: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "18px 40px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: "none",
    transition: "all 0.3s ease",
    boxShadow: "0 4px 15px rgba(26, 35, 126, 0.4)",
  },

  // === URGENT NEEDS SECTION ===
  urgentSection: {
    backgroundColor: colors.lightRed,
    padding: "80px 20px",
  },

  sectionContainer: {
    maxWidth: "1200px",
    margin: "0 auto",
  },

  sectionTitle: {
    textAlign: "center",
    fontSize: "2.5rem",
    fontWeight: "700",
    color: colors.darkText,
    marginBottom: "20px",
  },

  sectionSubtitle: {
    textAlign: "center",
    fontSize: "1.1rem",
    color: colors.lightText,
    maxWidth: "800px",
    margin: "0 auto 50px",
    lineHeight: "1.6",
  },

  bloodTypesGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "25px",
    marginBottom: "60px",
  },

  bloodTypeCard: {
    backgroundColor: colors.white,
    borderRadius: "12px",
    padding: "30px",
    textAlign: "center",
    boxShadow: "0 5px 20px rgba(0, 0, 0, 0.08)",
    transition: "transform 0.3s ease",
    borderTop: "4px solid #C62828",
  },

  bloodType: {
    fontSize: "2.5rem",
    fontWeight: "700",
    color: colors.primary,
    marginBottom: "10px",
  },

  bloodStatus: {
    fontSize: "1rem",
    color: colors.lightText,
    fontWeight: "500",
  },

  criticalStatus: {
    color: colors.primary,
    fontWeight: "600",
  },

  // === HOW IT WORKS SECTION ===
  processSection: {
    backgroundImage: `linear-gradient(rgba(255, 255, 255, 0.95), rgba(255, 255, 255, 0.95)), url('https://images.unsplash.com/photo-1584036561566-baf8f5f1b144?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    padding: "100px 20px",
  },

  processSteps: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "40px",
    marginTop: "60px",
  },

  processStep: {
    backgroundColor: colors.cardBg,
    borderRadius: "15px",
    padding: "40px 30px",
    textAlign: "center",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.1)",
    position: "relative",
    overflow: "hidden",
  },

  stepNumber: {
    position: "absolute",
    top: "20px",
    left: "20px",
    width: "40px",
    height: "40px",
    backgroundColor: colors.primary,
    color: colors.white,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.2rem",
    fontWeight: "700",
  },

  stepIcon: {
    width: "70px",
    height: "70px",
    backgroundColor: `${colors.primary}15`,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 25px",
  },

  stepTitle: {
    fontSize: "1.5rem",
    fontWeight: "600",
    color: colors.darkText,
    marginBottom: "15px",
  },

  stepDescription: {
    fontSize: "1rem",
    color: colors.lightText,
    lineHeight: "1.6",
  },

  // === STATS SECTION ===
  statsSection: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "80px 20px",
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "40px",
    marginTop: "40px",
  },

  statItem: {
    textAlign: "center",
  },

  statNumber: {
    fontSize: "3.5rem",
    fontWeight: "700",
    marginBottom: "10px",
    color: "#FFEB3B",
  },

  statText: {
    fontSize: "1.2rem",
    opacity: "0.9",
  },

  // === FINAL CTA ===
  ctaSection: {
    backgroundImage: `linear-gradient(rgba(26, 35, 126, 0.9), rgba(26, 35, 126, 0.9)), url('https://images.unsplash.com/photo-1516549655669-df6654e435de?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    color: colors.white,
    padding: "120px 20px",
    textAlign: "center",
  },

  ctaContent: {
    maxWidth: "800px",
    margin: "0 auto",
  },

  ctaTitle: {
    fontSize: "2.8rem",
    fontWeight: "700",
    marginBottom: "25px",
    lineHeight: "1.2",
  },

  ctaText: {
    fontSize: "1.3rem",
    marginBottom: "45px",
    opacity: "0.9",
    lineHeight: "1.6",
  },

  ctaButton: {
    backgroundColor: colors.primary,
    color: colors.white,
    padding: "20px 50px",
    borderRadius: "8px",
    fontSize: "1.3rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    transition: "all 0.3s ease",
    boxShadow: "0 6px 25px rgba(198, 40, 40, 0.4)",
  },

  // === EMERGENCY CONTACT ===
  emergencySection: {
    backgroundColor: "#FFEBEE",
    padding: "60px 20px",
    textAlign: "center",
  },

  emergencyContact: {
    display: "inline-flex",
    alignItems: "center",
    gap: "15px",
    backgroundColor: colors.white,
    padding: "25px 40px",
    borderRadius: "12px",
    boxShadow: "0 5px 20px rgba(0, 0, 0, 0.1)",
  },

  emergencyNumber: {
    fontSize: "2rem",
    fontWeight: "700",
    color: colors.primary,
  },

  emergencyText: {
    fontSize: "1.1rem",
    color: colors.darkText,
    fontWeight: "500",
  },
};

const Home = () => {
  const [hoveredButton, setHoveredButton] = useState(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  const handleButtonHover = (type) => {
    setHoveredButton(type);
  };

  return (
    <div style={styles.container}>
      {/* === HERO SECTION - Inspired by real blood bank websites === */}
      <section style={styles.heroSection}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            <span style={styles.heroHighlight}>Donate Blood</span>
            <br />
            Save Lives Today
          </h1>
          <p style={styles.heroSubtitle}>
            Join our nationwide network of blood donors. Your single donation
            can save up to 3 lives. Register now and become someone's hero.
          </p>

          {/* Emergency Alert - Common on blood bank websites */}
          <div style={styles.emergencyAlert}>
            <ShieldIcon size={24} color={colors.white} />
            <div>
              <strong>Emergency Need:</strong> Type O- blood currently at
              critical levels
            </div>
          </div>

          <div style={styles.heroButtons}>
            <Link
              to="/register"
              style={{
                ...styles.buttonPrimary,
                transform:
                  hoveredButton === "donate"
                    ? "translateY(-3px) scale(1.05)"
                    : "translateY(0) scale(1)",
                boxShadow:
                  hoveredButton === "donate"
                    ? "0 8px 25px rgba(198, 40, 40, 0.6)"
                    : "0 4px 15px rgba(198, 40, 40, 0.4)",
              }}
              onMouseEnter={() => handleButtonHover("donate")}
              onMouseLeave={() => handleButtonHover(null)}
            >
              <DropletIcon size={22} color={colors.white} />
              Register to Donate
              <ArrowRightIcon size={22} color={colors.white} />
            </Link>

            <Link
              to="/search"
              style={{
                ...styles.buttonSecondary,
                transform:
                  hoveredButton === "find"
                    ? "translateY(-3px)"
                    : "translateY(0)",
                boxShadow:
                  hoveredButton === "find"
                    ? "0 8px 25px rgba(26, 35, 126, 0.6)"
                    : "0 4px 15px rgba(26, 35, 126, 0.4)",
              }}
              onMouseEnter={() => handleButtonHover("find")}
              onMouseLeave={() => handleButtonHover(null)}
            >
              <UsersIcon size={22} color={colors.white} />
              Find Donors
            </Link>
          </div>
        </div>
      </section>

      {/* === URGENT BLOOD NEEDS SECTION === */}
      <section style={styles.urgentSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Current Blood Stock Status</h2>
          <p style={styles.sectionSubtitle}>
            Real-time blood type availability across our partner hospitals.
            Critical levels require immediate donor response.
          </p>

          <div style={styles.bloodTypesGrid}>
            {[
              { type: "O-", status: "CRITICAL", level: "red" },
              { type: "B-", status: "LOW", level: "orange" },
              { type: "A+", status: "MODERATE", level: "yellow" },
              { type: "AB+", status: "ADEQUATE", level: "green" },
            ].map((blood, index) => (
              <div
                key={index}
                style={{
                  ...styles.bloodTypeCard,
                  transform:
                    hoveredCard === index
                      ? "translateY(-10px)"
                      : "translateY(0)",
                  borderTopColor:
                    blood.level === "red"
                      ? colors.primary
                      : blood.level === "orange"
                      ? "#FF9800"
                      : blood.level === "yellow"
                      ? "#FFC107"
                      : "#4CAF50",
                }}
                onMouseEnter={() => setHoveredCard(index)}
                onMouseLeave={() => setHoveredCard(null)}
              >
                <div style={styles.bloodType}>{blood.type}</div>
                <div
                  style={{
                    ...styles.bloodStatus,
                    ...(blood.level === "red" ? styles.criticalStatus : {}),
                  }}
                >
                  {blood.status}
                </div>
                {blood.level === "red" && (
                  <div
                    style={{
                      color: colors.primary,
                      fontSize: "0.9rem",
                      marginTop: "10px",
                    }}
                  >
                    Urgent Donors Needed
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* === HOW IT WORKS SECTION === */}
      <section style={styles.processSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Simple 3-Step Process</h2>
          <p style={styles.sectionSubtitle}>
            From registration to saving lives - quick, safe, and efficient
          </p>

          <div style={styles.processSteps}>
            {[
              {
                icon: <UsersIcon size={32} color={colors.primary} />,
                title: "Register Online",
                description:
                  "Complete our simple online registration form with basic health information.",
              },
              {
                icon: <ShieldIcon size={32} color={colors.primary} />,
                title: "Get Verified",
                description:
                  "Our medical team verifies your eligibility and adds you to our donor database.",
              },
              {
                icon: <HospitalIcon size={32} color={colors.primary} />,
                title: "Donate & Save Lives",
                description:
                  "Visit any partner blood bank for a safe, supervised donation process.",
              },
            ].map((step, index) => (
              <div key={index} style={styles.processStep}>
                <div style={styles.stepNumber}>{index + 1}</div>
                <div style={styles.stepIcon}>{step.icon}</div>
                <h3 style={styles.stepTitle}>{step.title}</h3>
                <p style={styles.stepDescription}>{step.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* === IMPACT STATISTICS === */}
      <section style={styles.statsSection}>
        <div style={styles.sectionContainer}>
          <h2
            style={{
              ...styles.sectionTitle,
              color: colors.white,
              textAlign: "center",
            }}
          >
            Our Life-Saving Impact
          </h2>

          <div style={styles.statsGrid}>
            {[
              { number: "50,000+", text: "Lives Saved" },
              { number: "25,000+", text: "Active Donors" },
              { number: "98%", text: "Donor Satisfaction" },
              { number: "500+", text: "Partner Hospitals" },
            ].map((stat, index) => (
              <div key={index} style={styles.statItem}>
                <div style={styles.statNumber}>{stat.number}</div>
                <div style={styles.statText}>{stat.text}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* === FINAL CALL TO ACTION === */}
      <section style={styles.ctaSection}>
        <div style={styles.ctaContent}>
          <h2 style={styles.ctaTitle}>Ready to Save a Life?</h2>
          <p style={styles.ctaText}>
            Join thousands of heroes who donate blood regularly. Register today
            and get notified when your blood type is needed.
          </p>
          <Link
            to="/register"
            style={{
              ...styles.ctaButton,
              transform:
                hoveredButton === "cta"
                  ? "translateY(-3px) scale(1.05)"
                  : "translateY(0) scale(1)",
              boxShadow:
                hoveredButton === "cta"
                  ? "0 10px 30px rgba(198, 40, 40, 0.6)"
                  : "0 6px 25px rgba(198, 40, 40, 0.4)",
            }}
            onMouseEnter={() => handleButtonHover("cta")}
            onMouseLeave={() => handleButtonHover(null)}
          >
            <DropletIcon size={28} color={colors.white} />
            Become a Donor Today
          </Link>
        </div>
      </section>

      {/* === EMERGENCY CONTACT SECTION === */}
      <section style={styles.emergencySection}>
        <div style={styles.emergencyContact}>
          <PhoneIcon size={32} color={colors.primary} />
          <div>
            <div style={styles.emergencyText}>
              For Emergency Blood Requests:
            </div>
            <div style={styles.emergencyNumber}> 24/7 HELPLINE: 104</div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
