// src/pages/DonorProfile.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import {
  FiUser,
  FiMail,
  FiDroplet,
  FiMapPin,
  FiPhone,
  FiSave,
  FiRefreshCw,
  FiCheckCircle,
  FiAlertCircle,
  FiHeart,
  FiShield,
  FiClock,
  FiGlobe,
} from "react-icons/fi";

// Import background images (You'll need to add these to your public folder)
// Add these images to public/images/ folder or use Unsplash URLs

// --- Modern Professional Styles with Emotional Theme ---
const styles = {
  pageWrapper: {
    minHeight: "100vh",
    background:
      "linear-gradient(rgba(10, 25, 47, 0.92), rgba(139, 0, 0, 0.88))",
    position: "relative",
    padding: "20px",
    fontFamily:
      "'Poppins', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
  },
  backgroundOverlay: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: "url('/images/blood-donation-hero.jpg')", // Add your image
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    opacity: 0.15,
    zIndex: 1,
  },
  container: {
    maxWidth: "1000px",
    margin: "30px auto",
    position: "relative",
    zIndex: 2,
  },
  headerCard: {
    backgroundColor: "rgba(255, 255, 255, 0.95)",
    backdropFilter: "blur(10px)",
    borderRadius: "24px",
    padding: "40px",
    marginBottom: "30px",
    boxShadow: "0 20px 60px rgba(0, 0, 0, 0.25)",
    border: "1px solid rgba(255, 255, 255, 0.2)",
  },
  heroSection: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: "40px",
  },
  heroContent: {
    flex: 1,
  },
  title: {
    fontSize: "2.8rem",
    fontWeight: "800",
    background: "linear-gradient(135deg, #cf1322 0%, #ff6b6b 100%)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    marginBottom: "15px",
    letterSpacing: "-0.5px",
  },
  subtitle: {
    fontSize: "1.1rem",
    color: "#5a5a5a",
    fontWeight: "400",
    lineHeight: "1.6",
    maxWidth: "600px",
  },
  heroStats: {
    display: "flex",
    gap: "30px",
