const API_URL = "http://localhost:5000/api/donors";

const DonorProfile = () => {
  const { user, token, isAuthenticated, updateProfile } = useAuth();
  const [updates, setUpdates] = useState({});
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [hoveredCard, setHoveredCard] = useState(null);
  const [focusedInput, setFocusedInput] = useState(null);
  const [stats, setStats] = useState({
    donations: 5,
    livesSaved: 15,
    lastDonation: "2 months ago",
  });

  // Dynamic stats calculation (simulated)
  useEffect(() => {
    if (user) {
      // You can replace this with actual API call for user stats
      const calculatedStats = {
        donations: Math.floor(Math.random() * 10) + 3,
        livesSaved: Math.floor(Math.random() * 30) + 10,
        lastDonation: [
          "1 month ago",
          "2 months ago",
          "3 weeks ago",
          "Just now",
        ][Math.floor(Math.random() * 4)],
      };
      setStats(calculatedStats);
    }
  }, [user]);

  const handleChange = (e) => {
    setUpdates({ ...updates, [e.target.name]: e.target.value });
  };

  const handleReset = () => {
    setUpdates({});
    setMessage("");
  };

  const handleUpdate = async () => {
    if (Object.keys(updates).length === 0) {
      setMessage("Please make changes before saving.");
      return;
    }

    setIsSubmitting(true);
    setMessage("");

    try {
      const res = await axios.put(`${API_URL}/profile`, updates, {
        headers: { Authorization: `Bearer ${token}` },
      });

      updateProfile(res.data);
      setUpdates({});
      setMessage(
        "✓ Profile updated successfully! Changes are synchronized globally."
      );

      // Auto-clear success message after 5 seconds
      setTimeout(() => {
        setMessage("");
      }, 5000);
    } catch (err) {
      setMessage(
        "✗ " +
          (err.response?.data?.message ||
            "Error updating profile. Please try again.")
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isAuthenticated || !user) {
    return (
      <div style={styles.pageWrapper}>
        <div style={styles.backgroundOverlay}></div>
        <div
          style={{
            ...styles.container,
            textAlign: "center",
            padding: "100px 20px",
          }}
        >
          <div style={styles.headerCard}>
            <FiAlertCircle
              style={{
                fontSize: "60px",
                color: "#ff4d4f",
                marginBottom: "20px",
              }}
            />
            <h2 style={{ color: "#1a1a1a", marginBottom: "15px" }}>
              Profile Access Required
            </h2>
            <p
              style={{
                color: "#666",
                maxWidth: "500px",
                margin: "0 auto 30px",
              }}
            >
              Please log in to access your donor profile and help save lives.
            </p>
          </div>
        </div>
      </div>
    );
  }

  const hasChanges = Object.keys(updates).length > 0;

  return (
    <div style={styles.pageWrapper}>
      {/* Background Overlay */}
      <div style={styles.backgroundOverlay}></div>

      {/* Floating Elements for Emotional Impact */}
      <div style={styles.floatingElements}>
        <FiHeart
          style={{
            ...styles.floatingElement,
            top: "10%",
            left: "5%",
            animationDelay: "0s",
          }}
        />
        <FiDroplet
          style={{
            ...styles.floatingElement,
            top: "20%",
            right: "10%",
            animationDelay: "5s",
          }}
        />
        <FiHeart
          style={{
            ...styles.floatingElement,
            bottom: "30%",
            left: "15%",
            animationDelay: "10s",
          }}
        />
        <FiDroplet
          style={{
            ...styles.floatingElement,
            bottom: "15%",
            right: "20%",
            animationDelay: "15s",
          }}
        />
      </div>

      <div style={styles.container}>
        {/* Hero Header */}
        <div style={styles.headerCard}>
          <div style={styles.heroSection}>
            <div style={styles.heroContent}>
              <h1 style={styles.title}>Donor Profile</h1>
              <p style={styles.subtitle}>
                Every drop counts. Your profile helps connect donors with
                recipients in need, creating life-saving opportunities across
                the nation.
              </p>

              <div style={styles.heroStats}>
                <div style={styles.statItem}>
                  <div style={styles.statIcon}>
                    <FiDroplet />
                  </div>
                  <div style={styles.statText}>
                    <span style={styles.statValue}>{stats.donations}</span>
                    <span style={styles.statLabel}>Donations</span>
                  </div>
                </div>

                <div style={styles.statItem}>
                  <div style={styles.statIcon}>
                    <FiHeart />
                  </div>
                  <div style={styles.statText}>
                    <span style={styles.statValue}>{stats.livesSaved}</span>
                    <span style={styles.statLabel}>Lives Impacted</span>
                  </div>
                </div>

                <div style={styles.statItem}>
                  <div style={styles.statIcon}>
                    <FiClock />
                  </div>
                  <div style={styles.statText}>
                    <span style={styles.statValue}>{stats.lastDonation}</span>
                    <span style={styles.statLabel}>Last Donation</span>
                  </div>
                </div>
              </div>
            </div>

            <div
              style={{
                backgroundColor: "#fff0f0",
                borderRadius: "20px",
                padding: "30px",
                minWidth: "200px",
                textAlign: "center",
                border: "2px solid #ffcccc",
              }}
            >
              <FiShield
                size={40}
                color="#cf1322"
                style={{ marginBottom: "15px" }}
              />
              <div
                style={{
                  fontSize: "0.9rem",
                  color: "#666",
                  marginBottom: "10px",
                }}
              >
                Donor Status
              </div>
              <div
                style={{
                  backgroundColor: "#cf1322",
                  color: "white",
                  padding: "8px 20px",
                  borderRadius: "50px",
                  fontWeight: "700",
                  fontSize: "1.1rem",
                }}
              >
                ACTIVE
              </div>
            </div>
          </div>
        </div>

        {/* Profile Information Grid */}
        <div style={styles.profileGrid}>
          {/* Personal Info Card */}
          <div
            style={{
              ...styles.profileCard,
              ...(hoveredCard === "personal" && styles.profileCardHover),
            }}
            onMouseEnter={() => setHoveredCard("personal")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.cardHeader}>
              <div style={styles.cardIcon}>
                <FiUser />
              </div>
              <h3 style={styles.cardTitle}>Personal Information</h3>
            </div>

            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>
                <FiUser size={18} />
                Full Name
              </div>
              <div style={styles.infoValue}>{user.name}</div>
            </div>

            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>
                <FiMail size={18} />
                Email Address
              </div>
              <div style={styles.infoValue}>{user.email}</div>
            </div>
          </div>

          {/* Medical Info Card */}
          <div
            style={{
              ...styles.profileCard,
              ...(hoveredCard === "medical" && styles.profileCardHover),
            }}
            onMouseEnter={() => setHoveredCard("medical")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.cardHeader}>
              <div style={styles.cardIcon}>
                <FiDroplet />
              </div>
              <h3 style={styles.cardTitle}>Medical Information</h3>
            </div>

            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>
                <FiDroplet size={18} />
                Blood Group
              </div>
              <div style={styles.infoValue}>
                <span style={styles.bloodGroupBadge}>
                  <FiDroplet size={18} />
                  {user.bloodGroup}
                </span>
              </div>
            </div>

            <div style={{ ...styles.infoRow, borderBottom: "none" }}>
              <div style={styles.infoLabel}>
                <FiGlobe size={18} />
                Availability
              </div>
              <div
                style={{
                  ...styles.infoValue,
                  color: "#389e0d",
                  fontSize: "0.95rem",
                  backgroundColor: "rgba(56, 158, 13, 0.1)",
                  padding: "6px 15px",
                  borderRadius: "20px",
                }}
              >
                Ready to Donate
              </div>
            </div>
          </div>

          {/* Contact Info Card */}
          <div
            style={{
              ...styles.profileCard,
              ...(hoveredCard === "contact" && styles.profileCardHover),
            }}
            onMouseEnter={() => setHoveredCard("contact")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.cardHeader}>
              <div style={styles.cardIcon}>
                <FiMapPin />
              </div>
              <h3 style={styles.cardTitle}>Contact & Location</h3>
            </div>

            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>
                <FiMapPin size={18} />
                City
              </div>
              <div style={styles.infoValue}>{user.city}</div>
            </div>

            <div style={{ ...styles.infoRow, borderBottom: "none" }}>
              <div style={styles.infoLabel}>
                <FiPhone size={18} />
                Phone Number
              </div>
              <div style={styles.infoValue}>{user.phone}</div>
            </div>
          </div>
        </div>

        {/* Update Profile Section */}
        <div style={styles.updateCard}>
          <div style={styles.updateHeader}>
            <div style={styles.updateIcon}>
              <FiSave />
            </div>
            <div>
              <h3 style={styles.updateTitle}>Update Your Information</h3>
              <p style={styles.updateSubtitle}>
                Keep your details current to ensure you can be reached when
                lives depend on it
              </p>
            </div>
          </div>

          <div style={styles.inputGrid}>
            {/* City Input */}
            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>
                <FiMapPin size={18} />
                Update City
              </label>
              <div style={styles.inputWrapper}>
                <FiMapPin style={styles.inputIcon} />
                <input
                  type="text"
                  name="city"
                  placeholder="Enter your city"
                  onChange={handleChange}
                  onFocus={() => setFocusedInput("city")}
                  onBlur={() => setFocusedInput(null)}
                  style={{
                    ...styles.input,
                    ...(focusedInput === "city" && styles.inputFocus),
                  }}
                  value={updates.city || ""}
                />
              </div>
              <div style={styles.currentValue}>Current: {user.city}</div>
            </div>

            {/* Phone Input */}
            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>
                <FiPhone size={18} />
                Update Phone
              </label>
              <div style={styles.inputWrapper}>
                <FiPhone style={styles.inputIcon} />
                <input
                  type="tel"
                  name="phone"
                  placeholder="Enter your phone number"
                  onChange={handleChange}
                  onFocus={() => setFocusedInput("phone")}
                  onBlur={() => setFocusedInput(null)}
                  style={{
                    ...styles.input,
                    ...(focusedInput === "phone" && styles.inputFocus),
                  }}
                  value={updates.phone || ""}
                />
              </div>
              <div style={styles.currentValue}>Current: {user.phone}</div>
            </div>

            {/* Blood Group Input */}
            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>
                <FiDroplet size={18} />
                Update Blood Group
              </label>
              <div style={styles.inputWrapper}>
                <FiDroplet style={styles.inputIcon} />
                <input
                  type="text"
                  name="bloodGroup"
                  placeholder="Enter your blood group"
                  onChange={handleChange}
                  onFocus={() => setFocusedInput("bloodGroup")}
                  onBlur={() => setFocusedInput(null)}
                  style={{
                    ...styles.input,
                    ...(focusedInput === "bloodGroup" && styles.inputFocus),
                  }}
                  value={updates.bloodGroup || ""}
                />
              </div>
              <div style={styles.currentValue}>Current: {user.bloodGroup}</div>
            </div>
          </div>

          <div style={styles.buttonGroup}>
            <button
              onClick={handleUpdate}
              style={{
                ...styles.saveButton,
                ...(hasChanges && !isSubmitting && styles.saveButtonHover),
                ...((isSubmitting || !hasChanges) && styles.saveButtonDisabled),
              }}
              disabled={isSubmitting || !hasChanges}
              onMouseEnter={(e) => {
                if (hasChanges && !isSubmitting) {
                  e.currentTarget.style.transform = "translateY(-3px)";
                  e.currentTarget.style.boxShadow =
                    "0 10px 25px rgba(207, 19, 34, 0.4)";
                }
              }}
              onMouseLeave={(e) => {
                if (hasChanges && !isSubmitting) {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow =
                    "0 6px 20px rgba(207, 19, 34, 0.3)";
                }
              }}
            >
              {isSubmitting ? (
                <>
                  <FiRefreshCw className="spin" />
                  Updating Profile...
                </>
              ) : (
                <>
                  <FiSave />
                  Save Changes
                </>
              )}
            </button>

            {hasChanges && (
              <button
                onClick={handleReset}
                style={styles.resetButton}
                onMouseEnter={(e) => {
                  e.currentTarget.style.borderColor = "#cf1322";
                  e.currentTarget.style.color = "#cf1322";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.borderColor = "#e0e0e0";
                  e.currentTarget.style.color = "#666";
                }}
              >
                Discard Changes
              </button>
            )}
          </div>

          {/* Message Display */}
          {message && (
            <div style={styles.messageContainer}>
              <div
                style={{
                  ...styles.message,
                  ...(message.includes("✓")
                    ? styles.successMessage
                    : message.includes("✗")
                    ? styles.errorMessage
                    : styles.infoMessage),
                }}
              >
                {message.includes("✓") ? (
                  <FiCheckCircle size={24} />
                ) : message.includes("✗") ? (
                  <FiAlertCircle size={24} />
                ) : (
                  <FiAlertCircle size={24} />
                )}
                <span>{message}</span>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* CSS Animations */}
      <style jsx>{`
        @keyframes spin {
          0% {
            transform: rotate(0deg);
          }
          100% {
            transform: rotate(360deg);
          }
        }

        @keyframes float {
          0% {
            transform: translateY(0) rotate(0deg);
          }
          25% {
            transform: translateY(-20px) rotate(90deg);
          }
          50% {
            transform: translateY(0) rotate(180deg);
          }
          75% {
            transform: translateY(20px) rotate(270deg);
          }
          100% {
            transform: translateY(0) rotate(360deg);
          }
        }

        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .spin {
          animation: spin 1s linear infinite;
        }

        /* Add smooth transitions */
        * {
          transition: background-color 0.3s ease, border-color 0.3s ease;
        }
      `}</style>
    </div>
  );
};

export default DonorProfile;
