// src/pages/Home.js (FULL CODE - Implements Modern Design, Gradient, and Thematic Look)

import React from "react";
import { Link } from "react-router-dom";

// --- Thematic Colors & Modern Styles ---
const styles = {
  // Main background container (applied to the parent div in App.js usually)
  // Here, we ensure the Home content itself is centered and spaced.
  container: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    padding: "0 0 50px 0", // Padding only at the bottom
  },

  // --- HERO SECTION (Gradient Background & Thematic Look) ---
  heroWrapper: {
    // Gradient Background: Red to Dark Teal/Hospital Green
    background: "linear-gradient(135deg, #d32f2f 0%, #00796b 100%)",
    padding: "100px 0",
    marginBottom: "60px",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.2)",
    position: "relative", // Necessary for the overlay effect
    overflow: "hidden",
  },
  // Overlay for hospital/blood theme (Simulated with pseudo-element effect)
  heroOverlay: {
    maxWidth: "1200px",
    margin: "0 auto",
    padding: "0 20px",
    textAlign: "left",
    position: "relative",
    zIndex: 10,
    // Add a subtle, dark background behind the text for contrast
    backgroundColor: "rgba(0, 0, 0, 0.2)",
    padding: "40px",
    borderRadius: "10px",
  },

  heroTitle: {
    fontSize: "3.5em",
    color: "white",
    marginBottom: "15px",
    fontWeight: 800,
    letterSpacing: "1px",
    textShadow: "0 2px 4px rgba(0, 0, 0, 0.5)",
  },
  heroSubtitle: {
    fontSize: "1.2em",
    color: "#e0e0e0", // Lighter gray for readability on dark background
    marginBottom: "40px",
    lineHeight: 1.6,
    maxWidth: "600px",
  },

  // --- CTA Buttons (High Contrast) ---
  ctaContainer: {
    display: "flex",
    gap: "20px",
  },
  ctaButton: {
    padding: "15px 30px",
    borderRadius: "50px", // Pill shape for modern look
    fontSize: "1em",
    fontWeight: "bold",
    textDecoration: "none",
    transition: "background-color 0.3s, transform 0.2s",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
    minWidth: "180px",
    textTransform: "uppercase",
  },
  donateButton: {
    backgroundColor: "white",
    color: "#d32f2f", // Red text
    border: "2px solid white",
  },
  findButton: {
    backgroundColor: "#d32f2f",
    color: "white",
    border: "2px solid #d32f2f",
  },

  // --- STATS SECTION (Clean Card Layout) ---
  statsSection: {
    maxWidth: "1200px",
    margin: "0 auto",
    display: "flex",
    justifyContent: "space-around",
    padding: "30px 20px",
    gap: "30px",
  },
  statItem: {
    flex: 1,
    minWidth: "250px",
    backgroundColor: "#ffffff",
    padding: "30px",
    borderRadius: "10px",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.08)",
    transition: "transform 0.3s",
    borderBottom: "4px solid #00796b",
  },
  statItemHover: {
    transform: "translateY(-5px)",
  },
  statNumber: {
    fontSize: "3em",
    color: "#d32f2f", // Primary Red for emphasis
    fontWeight: 700,
    marginBottom: "5px",
  },
  statLabel: {
    color: "#546e7a",
    fontSize: "1em",
    marginTop: "5px",
    textTransform: "uppercase",
    letterSpacing: "0.5px",
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = React.useState(null);

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <div style={styles.heroWrapper}>
        <div style={styles.heroOverlay}>
          <h1 style={styles.heroTitle}>Donate Blood, Save Lives.</h1>
          <p style={styles.heroSubtitle}>
            Your voluntary blood donation can make a monumental difference. Join
            our network to connect with those who need it or find nearby donors
            effortlessly.
          </p>
          <div style={styles.ctaContainer}>
            <Link
              to="/register"
              style={{ ...styles.ctaButton, ...styles.donateButton }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = "#f0f0f0";
                e.target.style.transform = "translateY(-2px)";
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = "white";
                e.target.style.transform = "translateY(0)";
              }}
            >
              Register to Donate
            </Link>
            <Link
              to="/search"
              style={{ ...styles.ctaButton, ...styles.findButton }}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = "#c62828";
                e.target.style.transform = "translateY(-2px)";
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = "#d32f2f";
                e.target.style.transform = "translateY(0)";
              }}
            >
              Find a Donor
            </Link>
          </div>
        </div>
      </div>

      {/* --- STATS SECTION --- */}
      <section style={styles.statsSection}>
        <div
          style={
            hoveredStat === 1
              ? { ...styles.statItem, ...styles.statItemHover }
              : styles.statItem
          }
          onMouseOver={() => setHoveredStat(1)}
          onMouseOut={() => setHoveredStat(null)}
        >
          <div style={styles.statNumber}>1.8M+</div>
          <div style={styles.statLabel}>Lives Impacted Globally</div>
        </div>
        <div
          style={
            hoveredStat === 2
              ? { ...styles.statItem, ...styles.statItemHover }
              : styles.statItem
          }
          onMouseOver={() => setHoveredStat(2)}
          onMouseOut={() => setHoveredStat(null)}
        >
          <div style={styles.statNumber}>92%</div>
          <div style={styles.statLabel}>Emergency Response Globally</div>
        </div>
        <div
          style={
            hoveredStat === 3
              ? { ...styles.statItem, ...styles.statItemHover }
              : styles.statItem
          }
          onMouseOver={() => setHoveredStat(3)}
          onMouseOut={() => setHoveredStat(null)}
        >
          <div style={styles.statNumber}>50k+</div>
          <div style={styles.statLabel}>Active Donors Registered</div>
        </div>
      </section>
    </div>
  );
};

export default Home;
