// src/services/donorService.js (FULL CODE)

import axios from "axios";

const API_URL = "http://localhost:5000/api/donors";

// Register a new donor
export const registerDonor = async (donorData) => {
  try {
    const response = await axios.post(`${API_URL}/register`, donorData);
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : error;
  }
};

// Login donor
export const loginDonor = async (loginData) => {
  try {
    const response = await axios.post(`${API_URL}/login`, loginData);
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : error;
  }
};

// Get donor profile (requires token)
export const getDonorProfile = async (token) => {
  try {
    const response = await axios.get(`${API_URL}/profile`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    // We throw the error here so AuthContext can catch it and log the user out if the token is bad
    throw error;
  }
};
