// src/pages/Home.js (NO RED OR BLUE COLOR SCHEME)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Warm, Professional Color Palette (No Red/Blue)
const colors = {
  primary: "#4A6FA5", // Muted Teal Blue-Gray
  secondary: "#8B7355", // Warm Brown (Earth tone)
  accent: "#7D8E5A", // Sage Green
  lightBg: "#F8F5F0", // Warm off-white
  white: "#FFFFFF",
  darkText: "#2C3E50", // Dark slate
  lightText: "#6D7B8C", // Medium gray
  success: "#7D8E5A", // Sage Green (reused)
  warning: "#D49A6A", // Warm orange-brown
  purple: "#8A7AA4", // Muted purple
};

// Minimal Icons
const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

const CheckIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20 6L9 17l-5-5" />
  </svg>
);

const PhoneIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z" />
  </svg>
);

const HospitalIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M2 22h20M5 22V9a4 4 0 014-4h6a4 4 0 014 4v13M5 10h14M12 6v4M9 8h6" />
  </svg>
);

// Styles
const styles = {
  container: {
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif",
    color: colors.darkText,
    lineHeight: 1.6,
  },

  // Hero Section
  hero: {
    background: `linear-gradient(135deg, ${colors.primary} 0%, #3A5673 100%)`,
    color: colors.white,
    padding: "120px 20px 100px",
    textAlign: "center",
    position: "relative",
    overflow: "hidden",
  },

  heroPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M45 45H20V55H45V80H55V55H80V45H55V20H45V45Z' fill='white' opacity='0.05'/%3E%3C/svg%3E")`,
    backgroundSize: "100px 100px",
  },

  heroContent: {
    maxWidth: "900px",
    margin: "0 auto",
    position: "relative",
    zIndex: 2,
  },

  heroTitle: {
    fontSize: "clamp(2.8rem, 5vw, 3.8rem)",
    fontWeight: "700",
    marginBottom: "24px",
    lineHeight: "1.1",
  },

  heroSubtitle: {
    fontSize: "1.3rem",
    maxWidth: "700px",
    margin: "0 auto 48px",
    opacity: "0.9",
    lineHeight: "1.6",
  },

  heroButton: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "18px 40px",
    borderRadius: "10px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: `2px solid ${colors.secondary}`,
    transition: "all 0.3s ease",
  },

  // Stats Section
  statsSection: {
    padding: "80px 20px",
    backgroundColor: colors.lightBg,
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "30px",
    maxWidth: "1200px",
    margin: "0 auto",
  },

  statCard: {
    backgroundColor: colors.white,
    borderRadius: "15px",
    padding: "40px 30px",
    textAlign: "center",
    boxShadow: "0 8px 25px rgba(0,0,0,0.06)",
    border: `1px solid ${colors.lightBg}`,
  },

  statIcon: {
    width: "70px",
    height: "70px",
    backgroundColor: `${colors.secondary}15`,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 20px",
  },

  statNumber: {
    fontSize: "2.8rem",
    fontWeight: "700",
    color: colors.secondary,
    marginBottom: "10px",
  },

  statText: {
    fontSize: "1.1rem",
    color: colors.lightText,
    fontWeight: "500",
  },

  // Blood Status Section
  bloodStatusSection: {
    padding: "100px 20px",
    backgroundColor: colors.white,
  },

  sectionTitle: {
    fontSize: "2.8rem",
    fontWeight: "700",
    textAlign: "center",
    marginBottom: "20px",
    color: colors.darkText,
  },

  sectionSubtitle: {
    fontSize: "1.2rem",
    textAlign: "center",
    color: colors.lightText,
    maxWidth: "700px",
    margin: "0 auto 60px",
    lineHeight: "1.6",
  },

  bloodGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: "30px",
    maxWidth: "1000px",
    margin: "0 auto 60px",
  },

  bloodCard: {
    backgroundColor: colors.lightBg,
    borderRadius: "15px",
    padding: "40px 30px",
    textAlign: "center",
    borderTop: `5px solid ${colors.secondary}`,
    transition: "transform 0.3s ease",
  },

  bloodType: {
    fontSize: "3.5rem",
    fontWeight: "800",
    marginBottom: "15px",
    color: colors.darkText,
  },

  bloodStatus: {
    fontSize: "1.2rem",
    fontWeight: "600",
    padding: "8px 20px",
    borderRadius: "25px",
    display: "inline-block",
  },

  bloodButton: {
    backgroundColor: colors.accent,
    color: colors.white,
    padding: "16px 36px",
    borderRadius: "10px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: `2px solid ${colors.accent}`,
    transition: "all 0.3s ease",
  },

  // Testimonial Section
  testimonialSection: {
    padding: "100px 20px",
    backgroundColor: `linear-gradient(135deg, ${colors.lightBg} 0%, #F0EDE7 100%)`,
    background: `linear-gradient(135deg, ${colors.lightBg} 0%, #F0EDE7 100%)`,
  },

  testimonialContent: {
    maxWidth: "800px",
    margin: "0 auto",
    textAlign: "center",
  },

  quoteMark: {
    fontSize: "5rem",
    color: colors.secondary,
    opacity: "0.3",
    lineHeight: "1",
    marginBottom: "-20px",
    fontFamily: "'Georgia', serif",
  },

  testimonialText: {
    fontSize: "1.5rem",
    fontStyle: "italic",
    color: colors.darkText,
    lineHeight: "1.8",
    marginBottom: "40px",
  },

  testimonialAuthor: {
    fontSize: "1.1rem",
    color: colors.secondary,
    fontWeight: "600",
  },

  // Process Section
  processSection: {
    padding: "100px 20px",
    backgroundColor: colors.white,
  },

  processGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(320px, 1fr))",
    gap: "40px",
    maxWidth: "1200px",
    margin: "0 auto",
  },

  processCard: {
    textAlign: "center",
    padding: "30px",
  },

  processNumber: {
    width: "70px",
    height: "70px",
    backgroundColor: colors.primary,
    color: colors.white,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.8rem",
    fontWeight: "700",
    margin: "0 auto 30px",
  },

  processTitle: {
    fontSize: "1.6rem",
    fontWeight: "600",
    marginBottom: "20px",
    color: colors.darkText,
  },

  processDescription: {
    fontSize: "1.1rem",
    color: colors.lightText,
    lineHeight: "1.7",
  },

  // Features Section
  featuresSection: {
    padding: "100px 20px",
    backgroundColor: colors.lightBg,
  },

  featuresGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "40px",
    maxWidth: "1200px",
    margin: "0 auto",
  },

  featureCard: {
    backgroundColor: colors.white,
    borderRadius: "15px",
    padding: "50px 40px",
    textAlign: "center",
    boxShadow: "0 10px 30px rgba(0,0,0,0.05)",
    border: `1px solid ${colors.lightBg}`,
  },

  featureIcon: {
    width: "80px",
    height: "80px",
    backgroundColor: `${colors.primary}15`,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 30px",
  },

  featureTitle: {
    fontSize: "1.5rem",
    fontWeight: "600",
    marginBottom: "20px",
    color: colors.darkText,
  },

  featureDescription: {
    fontSize: "1.1rem",
    color: colors.lightText,
    lineHeight: "1.7",
  },

  // CTA Section
  ctaSection: {
    padding: "120px 20px",
    backgroundColor: colors.primary,
    color: colors.white,
    textAlign: "center",
    position: "relative",
    overflow: "hidden",
  },

  ctaPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `url("data:image/svg+xml,%3Csvg width='120' height='120' viewBox='0 0 120 120' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M60 60m-50 0a50 50 0 1 0 100 0a50 50 0 1 0-100 0' fill='none' stroke='white' stroke-width='0.5' opacity='0.1'/%3E%3C/svg%3E")`,
    backgroundSize: "100px 100px",
  },

  ctaContent: {
    maxWidth: "800px",
    margin: "0 auto",
    position: "relative",
    zIndex: 2,
  },

  ctaTitle: {
    fontSize: "2.8rem",
    fontWeight: "700",
    marginBottom: "30px",
    lineHeight: "1.1",
  },

  ctaSubtitle: {
    fontSize: "1.3rem",
    marginBottom: "50px",
    opacity: "0.9",
    lineHeight: "1.6",
  },

  ctaButton: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "20px 50px",
    borderRadius: "10px",
    fontSize: "1.2rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "15px",
    border: `2px solid ${colors.secondary}`,
    transition: "all 0.3s ease",
  },

  // Emergency Section
  emergencySection: {
    padding: "80px 20px",
    backgroundColor: colors.darkText,
    color: colors.white,
    textAlign: "center",
  },

  emergencyContent: {
    maxWidth: "600px",
    margin: "0 auto",
  },

  emergencyTitle: {
    fontSize: "1.6rem",
    marginBottom: "20px",
    opacity: "0.9",
  },

  emergencyNumber: {
    fontSize: "2.8rem",
    fontWeight: "700",
    color: colors.secondary,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "20px",
  },

  // Footer
  footer: {
    padding: "80px 20px 40px",
    backgroundColor: "#2C3E50",
    color: colors.white,
  },

  footerContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "50px",
    marginBottom: "50px",
  },

  footerSection: {
    marginBottom: "30px",
  },

  footerTitle: {
    fontSize: "1.3rem",
    fontWeight: "600",
    marginBottom: "25px",
    color: colors.secondary,
  },

  footerText: {
    fontSize: "1rem",
    opacity: "0.8",
    lineHeight: "1.6",
    marginBottom: "25px",
  },

  footerLinks: {
    listStyle: "none",
    padding: "0",
    margin: "0",
  },

  footerLink: {
    color: "rgba(255,255,255,0.8)",
    textDecoration: "none",
    fontSize: "1rem",
    marginBottom: "15px",
    display: "block",
    transition: "color 0.3s ease",
  },

  contactInfo: {
    fontSize: "1rem",
    opacity: "0.8",
    lineHeight: "1.6",
  },

  contactItem: {
    marginBottom: "15px",
  },

  copyright: {
    textAlign: "center",
    paddingTop: "50px",
    borderTop: "1px solid rgba(255,255,255,0.1)",
    color: "rgba(255,255,255,0.6)",
    fontSize: "0.95rem",
  },
};

const Home = () => {
  const [hoveredButton, setHoveredButton] = useState(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  const bloodStatusData = [
    {
      type: "O-",
      status: "CRITICAL",
      color: colors.warning,
      bgColor: `${colors.warning}15`,
    },
    {
      type: "B-",
      status: "LOW",
      color: colors.purple,
      bgColor: `${colors.purple}15`,
    },
    {
      type: "A+",
      status: "MODERATE",
      color: colors.primary,
      bgColor: `${colors.primary}15`,
    },
    {
      type: "AB+",
      status: "ADEQUATE",
      color: colors.accent,
      bgColor: `${colors.accent}15`,
    },
  ];

  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <div style={styles.heroPattern} />
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            LifeShare Network
            <br />
            <span style={{ color: colors.secondary }}>
              Connecting Compassion
            </span>
          </h1>
          <p style={styles.heroSubtitle}>
            Join our community of donors who are making a real difference. Your
            donation creates a ripple effect of hope and healing.
          </p>
          <Link
            to="/register"
            style={{
              ...styles.heroButton,
              backgroundColor:
                hoveredButton === "hero" ? colors.accent : colors.secondary,
              borderColor:
                hoveredButton === "hero" ? colors.accent : colors.secondary,
              transform: hoveredButton === "hero" ? "translateY(-3px)" : "none",
            }}
            onMouseEnter={() => setHoveredButton("hero")}
            onMouseLeave={() => setHoveredButton(null)}
          >
            <DropletIcon size={22} color={colors.white} />
            Join as Donor
            <ArrowRightIcon size={22} color={colors.white} />
          </Link>
        </div>
      </section>

      {/* Stats Section */}
      <section style={styles.statsSection}>
        <div style={styles.statsGrid}>
          <div style={styles.statCard}>
            <div style={styles.statIcon}>
              <HeartIcon size={32} color={colors.secondary} />
            </div>
            <div style={styles.statNumber}>3</div>
            <div style={styles.statText}>Lives Saved Per Donation</div>
          </div>

          <div style={styles.statCard}>
            <div style={styles.statIcon}>
              <UsersIcon size={32} color={colors.secondary} />
            </div>
            <div style={styles.statNumber}>50K+</div>
            <div style={styles.statText}>Registered Donors</div>
          </div>

          <div style={styles.statCard}>
            <div style={styles.statIcon}>
              <ShieldIcon size={32} color={colors.secondary} />
            </div>
            <div style={styles.statNumber}>98%</div>
            <div style={styles.statText}>Safety Satisfaction</div>
          </div>
        </div>
      </section>

      {/* Blood Status Section */}
      <section style={styles.bloodStatusSection}>
        <h2 style={styles.sectionTitle}>Current Blood Availability</h2>
        <p style={styles.sectionSubtitle}>
          Real-time stock levels across partner medical centers. Your timely
          donation can make all the difference.
        </p>

        <div style={styles.bloodGrid}>
          {bloodStatusData.map((blood, index) => (
            <div
              key={index}
              style={{
                ...styles.bloodCard,
                borderTopColor: blood.color,
                transform:
                  hoveredCard === `blood${index}`
                    ? "translateY(-10px)"
                    : "none",
              }}
              onMouseEnter={() => setHoveredCard(`blood${index}`)}
              onMouseLeave={() => setHoveredCard(null)}
            >
              <div style={styles.bloodType}>{blood.type}</div>
              <div
                style={{
                  ...styles.bloodStatus,
                  backgroundColor: blood.bgColor,
                  color: blood.color,
                }}
              >
                {blood.status}
              </div>
            </div>
          ))}
        </div>

        <div style={{ textAlign: "center" }}>
          <Link
            to="/search"
            style={{
              ...styles.bloodButton,
              backgroundColor:
                hoveredButton === "check" ? colors.secondary : colors.accent,
              borderColor:
                hoveredButton === "check" ? colors.secondary : colors.accent,
              transform:
                hoveredButton === "check" ? "translateY(-3px)" : "none",
            }}
            onMouseEnter={() => setHoveredButton("check")}
            onMouseLeave={() => setHoveredButton(null)}
          >
            <CheckIcon size={22} color={colors.white} />
            Check Availability
          </Link>
        </div>
      </section>

      {/* Testimonial Section */}
      <section style={styles.testimonialSection}>
        <div style={styles.testimonialContent}>
          <div style={styles.quoteMark}>"</div>
          <p style={styles.testimonialText}>
            I was just 24 when a car accident changed everything. For 48 hours,
            my family waited, praying for a miracle. That miracle came in the
            form of a stranger's blood donation. Today, I'm alive because
            someone chose to give. Your donation isn't just bloodit's someone's
            tomorrow.
          </p>
          <div style={styles.testimonialAuthor}>
             Sarah M., Accident Survivor
          </div>
        </div>
      </section>

      {/* Process Section */}
      <section style={styles.processSection}>
        <h2 style={styles.sectionTitle}>Simple Donation Journey</h2>
        <p style={styles.sectionSubtitle}>
          From registration to saving lives - a clear, compassionate process
        </p>

        <div style={styles.processGrid}>
          <div style={styles.processCard}>
            <div style={styles.processNumber}>01</div>
            <h3 style={styles.processTitle}>Register & Screen</h3>
            <p style={styles.processDescription}>
              Create your donor profile and complete a simple health assessment.
              Our team ensures you're ready to donate safely.
            </p>
          </div>

          <div style={styles.processCard}>
            <div style={styles.processNumber}>02</div>
            <h3 style={styles.processTitle}>Intelligent Matching</h3>
            <p style={styles.processDescription}>
              Our system connects you with patients who need your specific blood
              type. Location-based matching ensures timely support.
            </p>
          </div>

          <div style={styles.processCard}>
            <div style={styles.processNumber}>03</div>
            <h3 style={styles.processTitle}>Life-Saving Donation</h3>
            <p style={styles.processDescription}>
              Visit our certified centers for a comfortable, supervised
              donation. Track the impact of your contribution in real-time.
            </p>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section style={styles.featuresSection}>
