// src/pages/Home.js (SIMPLIFIED & POLISHED - Focus on Core Utility)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Modern Color Palette (Slightly modified from last iteration for simplicity)
const colors = {
  primary: "#1976D2", // Deep Blue (Trust/Professional)
  secondary: "#00796B", // Teal (Medical/Safety)
  accent: "#D32F2F", // Blood Red (Urgency/Accent)
  light: "#E3F2FD", // Very Light Blue for background
  darkText: "#263238",
  grayText: "#546E7A",
};

// --- SIMPLIFIED SVG ICONS ---
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);
const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);
const ChevronRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

// --- SIMPLIFIED STYLES ---
const styles = {
  container: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: colors.light, // Light background for contrast
    paddingBottom: "80px",
  },

  // Hero Section (Full width, gradient)
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
    padding: "100px 20px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
  },
  heroContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    textAlign: "center",
  },
  heroTitle: {
    fontSize: "clamp(2.5rem, 5vw, 4rem)",
    fontWeight: 800,
    color: "white",
    marginBottom: "20px",
    lineHeight: 1.1,
  },
  heroSubtitle: {
    fontSize: "clamp(1.1rem, 2vw, 1.4rem)",
    color: "rgba(255, 255, 255, 0.9)",
    marginBottom: "40px",
    lineHeight: 1.6,
    maxWidth: "800px",
    margin: "0 auto 40px",
  },

  // CTA Buttons
  ctaContainer: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "20px",
  },
  ctaButton: {
    padding: "16px 30px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.3s",
    display: "inline-flex",
    alignItems: "center",
    gap: "10px",
    border: "2px solid transparent",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
    minWidth: "220px",
    justifyContent: "center",
  },
  primaryButton: {
    backgroundColor: "white",
    color: colors.primary,
  },
  secondaryButton: {
    backgroundColor: colors.accent, // Use Red for high-urgency action
    color: "white",
    border: `2px solid ${colors.accent}`,
  },

  // Stats Section (Pulling up into Hero area)
  statsSection: {
    maxWidth: "1200px",
    margin: "-60px auto 40px", // Pulls section up over the hero boundary
    padding: "0 20px",
    position: "relative",
    zIndex: 10,
  },
  statsGrid: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "20px",
  },
  statCard: {
    flex: "1 1 280px", // Responsive flex basis
    backgroundColor: "white",
    borderRadius: "16px",
    padding: "30px",
    boxShadow: "0 20px 40px rgba(0, 0, 0, 0.1)",
    transition: "all 0.3s ease",
    textAlign: "center",
  },
  statCardHover: {
    transform: "translateY(-10px)",
    boxShadow: "0 30px 60px rgba(0, 0, 0, 0.25)",
  },
  statNumber: {
    fontSize: "3rem",
    fontWeight: 800,
    color: colors.primary,
    lineHeight: 1,
    marginBottom: "10px",
  },
  statLabel: {
    fontSize: "1.05rem",
    color: colors.grayText,
    fontWeight: 500,
  },

  // Final CTA Banner
  finalCta: {
    maxWidth: "1000px",
    margin: "80px auto 40px",
    padding: "40px 30px",
    background: colors.light,
    borderRadius: "16px",
    textAlign: "center",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.05)",
  },
  finalCtaTitle: {
    fontSize: "2rem",
    color: colors.darkText,
    marginBottom: "15px",
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = useState(null);

  // General button hover handler
  const handleHover = (e, isHovered, isPrimary) => {
    e.currentTarget.style.transform = isHovered
      ? "translateY(-3px)"
      : "translateY(0)";
    e.currentTarget.style.boxShadow = isHovered
      ? "0 8px 20px rgba(0, 0, 0, 0.3)"
      : "0 4px 15px rgba(0, 0, 0, 0.2)";

    if (isPrimary) {
      e.currentTarget.style.backgroundColor = isHovered
        ? colors.light
        : "white";
    } else {
      e.currentTarget.style.backgroundColor = isHovered
        ? colors.primary
        : colors.accent;
    }
  };

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <div style={styles.heroWrapper}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>BloodConnect: Secure Donor Network</h1>
          <p style={styles.heroSubtitle}>
            Connecting those in need with verified, available donors across your
            city. Fast, reliable, and crucial support when it matters most.
          </p>
          <div style={styles.ctaContainer}>
            <Link
              to="/register"
              style={{ ...styles.ctaButton, ...styles.primaryButton }}
              onMouseOver={(e) => handleHover(e, true, true)}
              onMouseOut={(e) => handleHover(e, false, true)}
            >
              <DropletIcon size={20} color={colors.primary} />
              Register to Donate
            </Link>

            <Link
              to="/search"
              style={{ ...styles.ctaButton, ...styles.secondaryButton }}
              onMouseOver={(e) => handleHover(e, true, false)}
              onMouseOut={(e) => handleHover(e, false, false)}
            >
              <UsersIcon size={20} color="white" />
              Find Donors Instantly
              <ChevronRightIcon size={20} color="white" />
            </Link>
          </div>
        </div>
      </div>

      {/* --- STATS SECTION (Pulled up for visual interest) --- */}
      <section style={styles.statsSection}>
        <div style={styles.statsGrid}>
          {[
            { number: "24/7", label: "Emergency Support" },
            { number: "10K+", label: "Verified Donors" },
            { number: "95%", label: "Urgency Match Rate" },
          ].map((stat, index) => (
            <div
              key={index}
              style={
                hoveredStat === index
                  ? { ...styles.statCard, ...styles.statCardHover }
                  : styles.statCard
              }
              onMouseOver={() => setHoveredStat(index)}
              onMouseOut={() => setHoveredStat(null)}
            >
              <div style={styles.statNumber}>{stat.number}</div>
              <div style={styles.statLabel}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- FINAL CTA BANNER --- */}
      <section style={styles.finalCta}>
        <h2 style={styles.finalCtaTitle}>Ready to be a Lifeline?</h2>
        <p style={{ color: colors.grayText, marginBottom: "30px" }}>
          Your profile helps save lives. Login or Register to keep your
          availability up to date.
        </p>
        <Link
          to="/login"
          style={{
            ...styles.ctaButton,
            backgroundColor: colors.secondary,
            color: "white",
            boxShadow: "0 4px 15px rgba(0, 121, 107, 0.4)",
          }}
          onMouseOver={(e) =>
            (e.currentTarget.style.backgroundColor = colors.primary)
          }
          onMouseOut={(e) =>
            (e.currentTarget.style.backgroundColor = colors.secondary)
          }
        >
          Access My Profile
        </Link>
      </section>
    </div>
  );
};

export default Home;
