// src/pages/Home.js (PROFESSIONAL & INDUSTRY STANDARD DESIGN)

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// Professional Healthcare Color Palette (Industry Standard)
const colors = {
  primary: "#2563EB", // Professional Blue (Trust, Calm)
  secondary: "#059669", // Emerald Green (Health, Growth)
  accent: "#7C3AED", // Royal Purple (Compassion, Dignity)
  dark: "#1E293B", // Dark Navy (Professionalism)
  light: "#F8FAFC", // Clean White Background
  muted: "#64748B", // Muted Gray for text
  success: "#10B981", // Success Green
  warning: "#F59E0B", // Warning Amber
};

// --- PROFESSIONAL SVG ICONS ---
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ChevronRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ClockIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <circle cx="12" cy="12" r="10" />
    <polyline points="12 6 12 12 16 14" />
  </svg>
);

const MapPinIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0118 0z" />
    <circle cx="12" cy="10" r="3" />
  </svg>
);

// --- RESPONSIVE HELPER ---
const getResponsiveStyles = (viewportWidth) => {
  const isMobile = viewportWidth < 768;
  const isTablet = viewportWidth >= 768 && viewportWidth < 1024;

  return {
    heroWrapper: {
      padding: isMobile ? "80px 20px" : isTablet ? "100px 40px" : "120px 40px",
    },
    heroTitle: {
      fontSize: isMobile ? "2.25rem" : isTablet ? "3rem" : "3.5rem",
    },
    statsGrid: {
      gridTemplateColumns: isMobile
        ? "1fr"
        : isTablet
        ? "repeat(2, 1fr)"
        : "repeat(3, 1fr)",
    },
  };
};

// --- STYLES ---
const styles = {
  container: {
    fontFamily: "'Inter', 'Segoe UI', Roboto, -apple-system, sans-serif",
    backgroundColor: colors.light,
    minHeight: "100vh",
  },

  // Hero Section (Professional Gradient)
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.accent} 100%)`,
    position: "relative",
    overflow: "hidden",
  },

  heroPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M45 45H20V55H45V80H55V55H80V45H55V20H45V45Z' fill='white' opacity='0.08'/%3E%3C/svg%3E")`,
    backgroundSize: "120px 120px",
    opacity: 0.4,
  },

  heroContent: {
    maxWidth: "1280px",
    margin: "0 auto",
    position: "relative",
    zIndex: 10,
    textAlign: "center",
  },

  heroTitle: {
    fontWeight: 700,
    color: "white",
    marginBottom: "24px",
    lineHeight: 1.1,
  },

  heroSubtitle: {
    fontSize: "1.25rem",
    color: "rgba(255, 255, 255, 0.95)",
    marginBottom: "48px",
    lineHeight: 1.6,
    maxWidth: "800px",
    margin: "0 auto 48px",
  },

  // Trust Indicators
  trustIndicators: {
    display: "flex",
    justifyContent: "center",
    gap: "32px",
    flexWrap: "wrap",
    marginBottom: "48px",
  },

  trustItem: {
    display: "flex",
    alignItems: "center",
    gap: "8px",
    color: "rgba(255, 255, 255, 0.9)",
    fontSize: "0.95rem",
  },

  // CTA Buttons
  ctaContainer: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "20px",
    marginBottom: "40px",
  },

  ctaButton: {
    padding: "16px 32px",
    borderRadius: "12px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "12px",
    border: "2px solid transparent",
    minWidth: "220px",
  },

  primaryButton: {
    backgroundColor: "white",
    color: colors.primary,
    boxShadow: "0 8px 24px rgba(37, 99, 235, 0.3)",
  },

  secondaryButton: {
    backgroundColor: "transparent",
    color: "white",
    border: "2px solid rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
  },

  // Stats Section
  statsSection: {
    maxWidth: "1280px",
    margin: "-60px auto 80px",
    padding: "0 20px",
    position: "relative",
    zIndex: 20,
  },

  statsGrid: {
    display: "grid",
    gap: "24px",
  },

  statCard: {
    backgroundColor: "white",
    borderRadius: "16px",
    padding: "32px 24px",
    boxShadow: "0 12px 32px rgba(0, 0, 0, 0.08)",
    transition: "all 0.3s ease",
    textAlign: "center",
    border: "1px solid rgba(0, 0, 0, 0.05)",
  },

  statIconWrapper: {
    width: "64px",
    height: "64px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 20px",
    backgroundColor: `${colors.primary}15`, // 15% opacity
  },

  statNumber: {
    fontSize: "2.5rem",
    fontWeight: 700,
    color: colors.dark,
    lineHeight: 1,
    marginBottom: "8px",
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.accent})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  statLabel: {
    fontSize: "1rem",
    color: colors.muted,
    fontWeight: 500,
    lineHeight: 1.4,
  },

  // Features Section
  featuresSection: {
    maxWidth: "1280px",
    margin: "0 auto 80px",
    padding: "0 20px",
  },

  sectionHeader: {
    textAlign: "center",
    marginBottom: "48px",
  },

  sectionTitle: {
    fontSize: "2.25rem",
    fontWeight: 700,
    color: colors.dark,
    marginBottom: "16px",
  },

  sectionSubtitle: {
    fontSize: "1.125rem",
    color: colors.muted,
    maxWidth: "600px",
    margin: "0 auto",
    lineHeight: 1.6,
  },

  featuresGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "24px",
  },

  featureCard: {
    backgroundColor: "white",
    borderRadius: "16px",
    padding: "32px",
    border: "1px solid rgba(0, 0, 0, 0.05)",
    transition: "all 0.3s ease",
  },

  featureIcon: {
    width: "48px",
    height: "48px",
    borderRadius: "10px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "24px",
    backgroundColor: `${colors.primary}15`,
  },

  featureTitle: {
    fontSize: "1.25rem",
    fontWeight: 600,
    color: colors.dark,
    marginBottom: "12px",
  },

  featureDescription: {
    fontSize: "1rem",
    color: colors.muted,
    lineHeight: 1.6,
    marginBottom: "20px",
  },

  featureLink: {
    color: colors.primary,
    textDecoration: "none",
    fontWeight: 500,
    display: "inline-flex",
    alignItems: "center",
    gap: "6px",
    fontSize: "0.95rem",
  },

  // Final CTA
  finalCta: {
    maxWidth: "1280px",
    margin: "80px auto",
    padding: "64px 40px",
    background: `linear-gradient(135deg, ${colors.secondary}, ${colors.primary})`,
    borderRadius: "20px",
    textAlign: "center",
    color: "white",
  },

  finalCtaTitle: {
    fontSize: "2rem",
    fontWeight: 700,
    marginBottom: "16px",
  },

  finalCtaSubtitle: {
    fontSize: "1.125rem",
    opacity: 0.9,
    maxWidth: "600px",
    margin: "0 auto 32px",
    lineHeight: 1.6,
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = useState(null);
  const [hoveredFeature, setHoveredFeature] = useState(null);
  const [viewportWidth, setViewportWidth] = useState(
    typeof window !== "undefined" ? window.innerWidth : 1200
  );

  useEffect(() => {
    const handleResize = () => setViewportWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const responsiveStyles = getResponsiveStyles(viewportWidth);

  const handleButtonHover = (e, isHovered, isPrimary) => {
    const target = e.currentTarget;
    if (isPrimary) {
      target.style.transform = isHovered ? "translateY(-4px)" : "translateY(0)";
      target.style.boxShadow = isHovered
        ? "0 16px 32px rgba(37, 99, 235, 0.4)"
        : "0 8px 24px rgba(37, 99, 235, 0.3)";
    } else {
      target.style.transform = isHovered ? "translateY(-4px)" : "translateY(0)";
      target.style.backgroundColor = isHovered
        ? "rgba(255, 255, 255, 0.15)"
        : "transparent";
    }
  };

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <div style={{ ...styles.heroWrapper, ...responsiveStyles.heroWrapper }}>
        <div style={styles.heroPattern} />
        <div style={styles.heroContent}>
          <h1 style={{ ...styles.heroTitle, ...responsiveStyles.heroTitle }}>
            LifeShare: The Professional Blood Donation Network
          </h1>
          <p style={styles.heroSubtitle}>
            Connecting hospitals with verified donors through secure,
            industry-standard protocols. Join a trusted network dedicated to
            saving lives with precision and care.
          </p>

          {/* Trust Indicators */}
          <div style={styles.trustIndicators}>
            <div style={styles.trustItem}>
              <ShieldIcon size={18} />
              HIPAA Compliant
            </div>
            <div style={styles.trustItem}>
              <ClockIcon size={18} />
              24/7 Emergency Response
            </div>
            <div style={styles.trustItem}>
              <MapPinIcon size={18} />
              Nationwide Coverage
            </div>
          </div>

          {/* CTA Buttons */}
          <div style={styles.ctaContainer}>
            <Link
              to="/register"
              style={{ ...styles.ctaButton, ...styles.primaryButton }}
              onMouseOver={(e) => handleButtonHover(e, true, true)}
              onMouseOut={(e) => handleButtonHover(e, false, true)}
            >
              <DropletIcon size={20} />
              Register as Donor
            </Link>
            <Link
              to="/search"
              style={{ ...styles.ctaButton, ...styles.secondaryButton }}
              onMouseOver={(e) => handleButtonHover(e, true, false)}
              onMouseOut={(e) => handleButtonHover(e, false, false)}
            >
              <UsersIcon size={20} />
              Find Donors
              <ChevronRightIcon size={20} />
            </Link>
          </div>
        </div>
      </div>

      {/* --- STATS SECTION --- */}
      <section style={styles.statsSection}>
        <div style={{ ...styles.statsGrid, ...responsiveStyles.statsGrid }}>
          {[
            {
              number: "25K+",
              label: "Verified Medical Donors",
              icon: <UsersIcon size={28} color={colors.primary} />,
            },
            {
              number: "98.5%",
              label: "Hospital Satisfaction",
              icon: <ShieldIcon size={28} color={colors.primary} />,
            },
            {
              number: "45min",
              label: "Avg. Response Time",
              icon: <ClockIcon size={28} color={colors.primary} />,
            },
          ].map((stat, index) => (
            <div
              key={index}
              style={{
                ...styles.statCard,
                ...(hoveredStat === index && {
                  transform: "translateY(-8px)",
                  boxShadow: "0 20px 40px rgba(0, 0, 0, 0.12)",
                }),
              }}
              onMouseOver={() => setHoveredStat(index)}
              onMouseOut={() => setHoveredStat(null)}
            >
              <div style={styles.statIconWrapper}>{stat.icon}</div>
              <div style={styles.statNumber}>{stat.number}</div>
              <div style={styles.statLabel}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- FEATURES SECTION --- */}
      <section style={styles.featuresSection}>
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Enterprise-Grade Features</h2>
          <p style={styles.sectionSubtitle}>
            Built with healthcare industry standards and security protocols
          </p>
        </div>

        <div style={styles.featuresGrid}>
          {[
            {
              title: "Secure Matching System",
              description:
                "End-to-end encrypted matching between hospitals and verified donors with complete privacy protection.",
              icon: <ShieldIcon size={24} color={colors.primary} />,
              link: "/security",
            },
            {
              title: "Real-Time Inventory",
              description:
                "Live blood type availability tracking with predictive analytics for emergency preparedness.",
              icon: <ClockIcon size={24} color={colors.primary} />,
              link: "/inventory",
            },
            {
              title: "Hospital Integration",
              description:
                "Seamless API integration with hospital systems for automated donor requests and notifications.",
              icon: <MapPinIcon size={24} color={colors.primary} />,
              link: "/integration",
            },
          ].map((feature, index) => (
            <div
              key={index}
              style={{
                ...styles.featureCard,
                ...(hoveredFeature === index && {
                  transform: "translateY(-8px)",
                  boxShadow: "0 20px 40px rgba(0, 0, 0, 0.1)",
                }),
              }}
              onMouseOver={() => setHoveredFeature(index)}
              onMouseOut={() => setHoveredFeature(null)}
            >
              <div style={styles.featureIcon}>{feature.icon}</div>
              <h3 style={styles.featureTitle}>{feature.title}</h3>
              <p style={styles.featureDescription}>{feature.description}</p>
              <Link to={feature.link} style={styles.featureLink}>
                Learn more
                <ChevronRightIcon size={16} />
              </Link>
            </div>
          ))}
        </div>
      </section>

      {/* --- FINAL CTA --- */}
      <section style={styles.finalCta}>
        <h2 style={styles.finalCtaTitle}>Partner with Healthcare Excellence</h2>
        <p style={styles.finalCtaSubtitle}>
          Join hospitals, clinics, and medical institutions nationwide in our
          mission to ensure no patient goes without life-saving blood.
        </p>
        <Link
          to="/hospital-registration"
          style={{
            ...styles.ctaButton,
            backgroundColor: "white",
            color: colors.primary,
            boxShadow: "0 8px 24px rgba(0, 0, 0, 0.2)",
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.transform = "translateY(-4px)";
            e.currentTarget.style.boxShadow = "0 16px 32px rgba(0, 0, 0, 0.3)";
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.transform = "translateY(0)";
            e.currentTarget.style.boxShadow = "0 8px 24px rgba(0, 0, 0, 0.2)";
          }}
        >
          <ShieldIcon size={20} />
          Register Your Hospital
        </Link>
      </section>
    </div>
  );
};

export default Home;
