import React, { useState, useEffect } from "react";
import { useAuth } from "../context/AuthContext";
import { useToast } from "../context/ToastContext";
import {
  getAllDonorsAdmin,
  deleteDonorAdmin,
  toggleVerifyDonorAdmin,
} from "../services/donorService";

const AdminDashboard = () => {
  const { user, token } = useAuth();
  const { showToast } = useToast();

  const [donors, setDonors] = useState([]);
  const [loading, setLoading] = useState(true);

  // 1. Load all donors when page opens
  useEffect(() => {
    if (token && user?.role === "admin") {
      fetchDonors();
    }
  }, [token, user]);

  const fetchDonors = async () => {
    try {
      const data = await getAllDonorsAdmin(token);
      setDonors(data);
    } catch (error) {
      showToast("Failed to fetch donors.", "error");
    } finally {
      setLoading(false);
    }
  };

  // 2. Handle User Deletion
  const handleDelete = async (id) => {
    if (
      window.confirm(
        "Are you sure you want to delete this donor? This cannot be undone."
      )
    ) {
      try {
        await deleteDonorAdmin(id, token);
        setDonors(donors.filter((d) => d._id !== id));
        showToast("Donor deleted successfully", "success");
      } catch (error) {
        showToast("Failed to delete donor", "error");
      }
    }
  };

  // 3. Handle Verification Toggle
  const handleToggleVerify = async (id, currentStatus) => {
    try {
      await toggleVerifyDonorAdmin(id, !currentStatus, token);
      setDonors(
        donors.map((d) =>
          d._id === id ? { ...d, isVerified: !currentStatus } : d
        )
      );
      showToast("Verification status updated", "success");
    } catch (error) {
      showToast("Update failed", "error");
    }
  };

  // Security Gate
  if (user?.role !== "admin") {
    return (
      <div style={{ textAlign: "center", padding: "100px" }}>
        <h2>ðŸš« Access Denied</h2>
        <p>You do not have permission to view this page.</p>
      </div>
    );
  }

  if (loading)
    return (
      <div style={{ textAlign: "center", padding: "50px" }}>
        Loading Users...
      </div>
    );

  return (
    <div style={{ maxWidth: "1200px", margin: "40px auto", padding: "20px" }}>
      <header
        style={{
          marginBottom: "30px",
          borderBottom: "2px solid #eee",
          paddingBottom: "10px",
        }}
      >
        <h1>Admin Control Panel</h1>
        <p>Manage all registered donors and verify accounts.</p>
      </header>

      <div
        style={{
          overflowX: "auto",
          background: "white",
          borderRadius: "10px",
          boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
        }}
      >
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            textAlign: "left",
          }}
        >
          <thead style={{ background: "#f8f9fa" }}>
            <tr>
              <th style={tableHeaderStyle}>Name</th>
              <th style={tableHeaderStyle}>Blood</th>
              <th style={tableHeaderStyle}>Location</th>
              <th style={tableHeaderStyle}>Status</th>
              <th style={tableHeaderStyle}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {donors.map((donor) => (
              <tr key={donor._id} style={{ borderBottom: "1px solid #eee" }}>
                <td style={tableCellStyle}>
                  <div style={{ fontWeight: "bold" }}>{donor.name}</div>
                  <div style={{ fontSize: "12px", color: "#666" }}>
                    {donor.email}
                  </div>
                </td>
                <td style={tableCellStyle}>
                  <span style={{ color: "#e63946", fontWeight: "bold" }}>
                    {donor.bloodGroup}
                  </span>
                </td>
                <td style={tableCellStyle}>{donor.city}</td>
                <td style={tableCellStyle}>
                  <span
                    style={{
                      padding: "4px 8px",
                      borderRadius: "4px",
                      fontSize: "12px",
                      background: donor.isVerified ? "#d1fae5" : "#fef3c7",
                      color: donor.isVerified ? "#065f46" : "#92400e",
                    }}
                  >
                    {donor.isVerified ? "Verified âœ…" : "Unverified"}
                  </span>
                </td>
                <td style={tableCellStyle}>
                  <button
                    onClick={() =>
                      handleToggleVerify(donor._id, donor.isVerified)
                    }
                    style={{
                      marginRight: "10px",
                      background: "#3b82f6",
                      color: "white",
                      border: "none",
                      padding: "5px 10px",
                      borderRadius: "4px",
                      cursor: "pointer",
                    }}
                  >
                    {donor.isVerified ? "Revoke" : "Verify"}
                  </button>
                  {donor.role !== "admin" && (
                    <button
                      onClick={() => handleDelete(donor._id)}
                      style={{
                        background: "#ef4444",
                        color: "white",
                        border: "none",
                        padding: "5px 10px",
                        borderRadius: "4px",
                        cursor: "pointer",
                      }}
                    >
                      Delete
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Simple Styles
const tableHeaderStyle = { padding: "15px", borderBottom: "1px solid #ddd" };
const tableCellStyle = { padding: "15px" };

export default AdminDashboard;
