// src/components/Header.js (Updated with Green/Gray Palette)

import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

// New Color Palette (Consistent with Home.js Step 28)
const colors = {
  primary: "#10B981", // Emerald Green (Accent/Action)
  secondary: "#047857", // Forest Green (Secondary Action)
  dark: "#1F2937", // Deep Charcoal Gray (Header Background)
  white: "#FFFFFF",
  darkText: "#111827",
};

// --- Header Component Styles ---
const styles = {
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "15px 30px",
    backgroundColor: colors.dark, // Changed from RED to Deep Charcoal Gray
    color: colors.white,
    boxShadow: "0 2px 10px rgba(0,0,0,0.3)", // Stronger shadow for depth
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
  },
  logo: {
    margin: 0,
    fontSize: "1.8em",
    fontWeight: 600,
    letterSpacing: "1px",
    color: colors.white,
    textDecoration: "none",
  },
  logoAccent: {
    color: colors.primary, // Emerald Green accent
  },
  navLinks: {
    display: "flex",
    gap: "20px",
    alignItems: "center",
  },
  navLink: {
    color: colors.white,
    textDecoration: "none",
    fontSize: "1em",
    padding: "5px 10px",
    borderRadius: "4px",
    transition: "background-color 0.2s",
  },
  navLinkHover: {
    backgroundColor: colors.secondary, // Forest Green on hover
  },
  logoutButton: {
    backgroundColor: colors.primary, // Emerald Green for reliable actions
    color: colors.darkText, // Dark text on light button
    border: "none",
    padding: "8px 15px",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "0.9em",
    fontWeight: "bold",
    transition: "background-color 0.2s",
  },
  logoutButtonHover: {
    backgroundColor: colors.secondary,
    color: colors.white, // Ensure white text when hovering on a darker green
  },
};

const Header = () => {
  const { isAuthenticated, logout } = useAuth();

  const handleLogout = () => {
    logout();
  };

  const linkProps = (to) => ({
    to: to,
    style: styles.navLink,
    onMouseOver: (e) =>
      (e.target.style.backgroundColor = styles.navLinkHover.backgroundColor),
    onMouseOut: (e) => (e.target.style.backgroundColor = "transparent"),
  });

  // Custom props for the Logout button
  const logoutButtonProps = {
    onClick: handleLogout,
    style: styles.logoutButton,
    onMouseOver: (e) => {
      e.target.style.backgroundColor = styles.logoutButtonHover.backgroundColor;
      e.target.style.color = styles.logoutButtonHover.color;
    },
    onMouseOut: (e) => {
      e.target.style.backgroundColor = styles.logoutButton.backgroundColor;
      e.target.style.color = styles.logoutButton.color;
    },
  };

  return (
    <header style={styles.header}>
      <Link {...linkProps("/")} style={styles.logo}>
        BloodConnect <span style={styles.logoAccent}>ðŸ©¸</span>
      </Link>
      <nav style={styles.navLinks}>
        {/* Register Link */}
        <Link {...linkProps("/register")}>Register</Link>

        {/* Profile Link */}
        <Link {...linkProps("/profile")}>Profile</Link>

        {/* Search/Find Donor Link */}
        <Link {...linkProps("/search")}>Find Donor</Link>

        {isAuthenticated ? (
          // Show Logout button if authenticated
          <button {...logoutButtonProps}>Logout</button>
        ) : (
          // Show Login link if not authenticated
          <Link {...linkProps("/login")}>Login</Link>
        )}
      </nav>
    </header>
  );
};

export default Header;
