// src/pages/Home.js (UPDATED WITH YOUR SPECIFICATIONS)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Updated Color Palette - Clearer, Less Blurry Look
const colors = {
  primary: "#D32F2F", // Medical Red (Urgency, Life)
  secondary: "#1976D2", // Trust Blue (Clean, Clear)
  accent: "#388E3C", // Health Green (Cleaner Green)
  white: "#FFFFFF",
  lightBg: "#F8F9FA", // Cleaner light background
  darkText: "#212121",
  lightText: "#616161",
  cardBg: "#FFFFFF", // Pure white for cards
  sectionBg: "#F5F5F5", // Light grey for sections
};

// Medical SVG Icons (Added Child icon for the specific request)
const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const HospitalIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M2 22h20M5 22V9a4 4 0 014-4h6a4 4 0 014 4v13M5 10h14M12 6v4M9 8h6" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

// Child with medical symbol icon (for innocent child looking at blood)
const ChildHeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <circle cx="9" cy="9" r="2" />
    <path d="M7.2 7.9L3 11v9c0 .55.45 1 1 1h16c.55 0 1-.45 1-1v-9c0-2-3-6-7-8l-3.6 2.6" />
    <path d="M16 13H3M20 17H3M21 10H3" />
    <path d="M15 18l2 2 4-4" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

// Styles with cleaner, less blurry backgrounds
const styles = {
  container: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    minHeight: "100vh",
    color: colors.darkText,
  },

  // Hero Section - Clean hospital background
  heroSection: {
    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1582750433449-648ed127bb54?ixlib=rb-4.0.3&auto=format&fit=crop&w=1927&q=80')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    color: colors.white,
    padding: "100px 20px",
    textAlign: "center",
  },

  heroContent: {
    maxWidth: "800px",
    margin: "0 auto",
  },

  heroTitle: {
    fontSize: "clamp(2.5rem, 5vw, 3.5rem)",
    fontWeight: "700",
    marginBottom: "20px",
    lineHeight: "1.2",
  },

  heroSubtitle: {
    fontSize: "1.25rem",
    margin: "0 auto 40px",
    lineHeight: "1.6",
    opacity: "0.9",
    maxWidth: "700px",
  },

  ctaButtons: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "20px",
    marginTop: "40px",
  },

  primaryButton: {
    backgroundColor: colors.primary,
    color: colors.white,
    padding: "18px 36px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: "none",
    cursor: "pointer",
    transition: "all 0.3s ease",
    boxShadow: "0 4px 15px rgba(211, 47, 47, 0.4)",
  },

  secondaryButton: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "18px 36px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: `2px solid ${colors.secondary}`,
    cursor: "pointer",
    transition: "all 0.3s ease",
  },

  // Why Donate Section - Clean white background
  whyDonateSection: {
    backgroundColor: colors.white,
    padding: "80px 20px",
  },

  sectionContainer: {
    maxWidth: "1200px",
    margin: "0 auto",
  },

  sectionTitle: {
    textAlign: "center",
    fontSize: "2.5rem",
    fontWeight: "700",
    marginBottom: "20px",
    color: colors.primary,
  },

  sectionSubtitle: {
    textAlign: "center",
    fontSize: "1.1rem",
    color: colors.lightText,
    maxWidth: "700px",
    margin: "0 auto 50px",
    lineHeight: "1.6",
  },

  benefitsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "30px",
    marginTop: "40px",
  },

  benefitCard: {
    backgroundColor: colors.cardBg,
    borderRadius: "12px",
    padding: "40px 30px",
    textAlign: "center",
    boxShadow: "0 5px 20px rgba(0, 0, 0, 0.08)",
    transition: "transform 0.3s ease",
    border: "1px solid rgba(0,0,0,0.05)",
  },

  benefitIcon: {
    width: "80px",
    height: "80px",
    borderRadius: "50%",
    backgroundColor: `${colors.primary}10`, // Lighter opacity
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 25px",
  },

  benefitTitle: {
    fontSize: "1.4rem",
    fontWeight: "600",
    marginBottom: "15px",
    color: colors.darkText,
  },

  benefitDescription: {
    fontSize: "1rem",
    color: colors.lightText,
    lineHeight: "1.6",
  },

  // Stats Section - Light grey background
  statsSection: {
    backgroundColor: colors.sectionBg,
    padding: "80px 20px",
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "30px",
    marginTop: "50px",
  },

  statCard: {
    textAlign: "center",
    padding: "30px 20px",
  },

  statNumber: {
    fontSize: "3rem",
    fontWeight: "700",
    color: colors.primary,
    marginBottom: "10px",
  },

  statText: {
    fontSize: "1.1rem",
    color: colors.darkText,
    fontWeight: "500",
  },

  // Process Section - White background with blue accent
  processSection: {
    backgroundColor: colors.white,
    padding: "80px 20px",
  },

  processSteps: {
    display: "flex",
    flexDirection: "column",
    gap: "30px",
    maxWidth: "800px",
    margin: "50px auto 0",
  },

  processStep: {
    display: "flex",
    alignItems: "center",
    gap: "30px",
    padding: "35px",
    backgroundColor: colors.cardBg,
    borderRadius: "12px",
    boxShadow: "0 5px 15px rgba(0, 0, 0, 0.05)",
    border: `2px solid ${colors.secondary}20`, // Subtle blue border
  },

  stepNumber: {
    width: "70px",
    height: "70px",
    backgroundColor: colors.secondary, // Using the blue color
    color: colors.white,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "2rem",
    fontWeight: "700",
    flexShrink: 0,
  },

  stepContent: {
    flex: 1,
  },

  stepTitle: {
    fontSize: "1.5rem",
    fontWeight: "600",
    marginBottom: "10px",
    color: colors.darkText,
  },

  stepDescription: {
    fontSize: "1.1rem",
    color: colors.lightText,
    lineHeight: "1.6",
  },

  // Final CTA - Solid blue background (no blur)
  finalCta: {
    backgroundColor: colors.secondary,
    color: colors.white,
    padding: "100px 20px",
    textAlign: "center",
  },

  finalCtaContent: {
    maxWidth: "800px",
    margin: "0 auto",
  },

  finalCtaTitle: {
    fontSize: "2.5rem",
    fontWeight: "700",
    marginBottom: "20px",
  },

  finalCtaText: {
    fontSize: "1.2rem",
    marginBottom: "40px",
    lineHeight: "1.6",
  },

  ctaButton: {
    backgroundColor: colors.white,
    color: colors.secondary,
    padding: "18px 45px",
    borderRadius: "8px",
    fontSize: "1.2rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "10px",
    transition: "all 0.3s ease",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)",
  },
};

const Home = () => {
  const [hoveredButton, setHoveredButton] = useState(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  const handleButtonHover = (type) => {
    setHoveredButton(type);
  };

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <section style={styles.heroSection}>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            Every Drop Counts, Every Life Matters
          </h1>
          <p style={styles.heroSubtitle}>
            Join our network of life-savers and help hospitals maintain critical
            blood supplies. Your donation can be the difference between life and
            death for someone in need.
          </p>

          <div style={styles.ctaButtons}>
            <Link
              to="/register"
              style={{
                ...styles.primaryButton,
                transform:
                  hoveredButton === "primary"
                    ? "translateY(-3px) scale(1.05)"
                    : "translateY(0) scale(1)",
                boxShadow:
                  hoveredButton === "primary"
                    ? "0 8px 25px rgba(211, 47, 47, 0.6)"
                    : "0 4px 15px rgba(211, 47, 47, 0.4)",
              }}
              onMouseEnter={() => handleButtonHover("primary")}
              onMouseLeave={() => handleButtonHover(null)}
            >
              <DropletIcon size={22} color={colors.white} />
              Register as Donor
            </Link>

            <Link
              to="/search"
              style={{
                ...styles.secondaryButton,
                backgroundColor:
                  hoveredButton === "secondary"
                    ? colors.primary
                    : colors.secondary,
                transform:
                  hoveredButton === "secondary"
                    ? "translateY(-3px)"
                    : "translateY(0)",
              }}
              onMouseEnter={() => handleButtonHover("secondary")}
              onMouseLeave={() => handleButtonHover(null)}
            >
              <HospitalIcon size={22} color={colors.white} />
              Find Donors Near You
              <ArrowRightIcon size={22} color={colors.white} />
            </Link>
          </div>
        </div>
      </section>

      {/* --- WHY DONATE SECTION --- */}
      <section style={styles.whyDonateSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Why Your Donation Matters</h2>
          <p style={styles.sectionSubtitle}>
            Blood cannot be manufactured - it can only come from generous donors
            like you. Your contribution directly helps hospitals save lives
            every day.
          </p>

          <div style={styles.benefitsGrid}>
            {[
              {
                title: "Save Up to 3 Lives",
                description:
                  "A single blood donation can help multiple patients including accident victims, surgery patients, and cancer fighters.",
                icon: <HeartIcon size={36} color={colors.primary} />,
              },
              {
                title: "Verified & Safe",
                description:
                  "All donations are processed through certified medical facilities following strict safety protocols and hygiene standards.",
                icon: <HospitalIcon size={36} color={colors.primary} />,
              },
              {
                title: "Quick & Easy Process",
                description:
                  "The entire donation process takes less than an hour, and you can donate every 56 days.",
                icon: <DropletIcon size={36} color={colors.primary} />,
              },
              {
                title: "Join a Community",
                description:
                  "Become part of a network of compassionate individuals making a real difference in healthcare.",
                icon: <ChildHeartIcon size={36} color={colors.primary} />,
              },
            ].map((benefit, index) => (
              <div
                key={index}
                style={{
                  ...styles.benefitCard,
                  transform:
                    hoveredCard === index
                      ? "translateY(-10px)"
                      : "translateY(0)",
                }}
                onMouseEnter={() => setHoveredCard(index)}
                onMouseLeave={() => setHoveredCard(null)}
              >
                <div style={styles.benefitIcon}>{benefit.icon}</div>
                <h3 style={styles.benefitTitle}>{benefit.title}</h3>
                <p style={styles.benefitDescription}>{benefit.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* --- STATS SECTION --- */}
      <section style={styles.statsSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Our Life-Saving Impact</h2>
          <p style={styles.sectionSubtitle}>
            Together with hospitals and medical centers, we're making a real
            difference
          </p>

          <div style={styles.statsGrid}>
            {[
              { number: "50,000+", text: "Lives Saved" },
              { number: "500+", text: "Partner Hospitals" },
              { number: "1.2M", text: "Units Collected" },
              { number: "24/7", text: "Emergency Support" },
            ].map((stat, index) => (
              <div key={index} style={styles.statCard}>
                <div style={styles.statNumber}>{stat.number}</div>
                <div style={styles.statText}>{stat.text}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* --- SIMPLE 3-STEP PROCESS SECTION --- */}
      <section style={styles.processSection}>
        <div style={styles.sectionContainer}>
          <h2 style={styles.sectionTitle}>Simple 3-Step Process</h2>
          <p style={styles.sectionSubtitle}>
            From registration to saving lives - it's easier than you think
          </p>

          <div style={styles.processSteps}>
            {/* STEP 1 */}
            <div style={styles.processStep}>
              <div style={styles.stepNumber}>1</div>
              <div style={styles.stepContent}>
                <h3 style={styles.stepTitle}>Register & Get Verified</h3>
                <p style={styles.stepDescription}>
                  Create your profile, provide basic health information, and get
                  verified by our medical team.
                </p>
              </div>
            </div>

            {/* STEP 2 */}
            <div style={styles.processStep}>
              <div style={styles.stepNumber}>2</div>
              <div style={styles.stepContent}>
                <h3 style={styles.stepTitle}>Get Matched with Patients</h3>
                <p style={styles.stepDescription}>
                  Our system matches you with patients in need based on blood
                  type, location, and urgency.
                </p>
              </div>
            </div>

            {/* STEP 3 */}
            <div style={styles.processStep}>
              <div style={styles.stepNumber}>3</div>
              <div style={styles.stepContent}>
                <h3 style={styles.stepTitle}>Donate at Certified Centers</h3>
                <p style={styles.stepDescription}>
                  Visit our partner hospitals or blood banks for a safe,
                  supervised donation process.
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* --- FINAL CTA --- */}
      <section style={styles.finalCta}>
        <div style={styles.finalCtaContent}>
          <h2 style={styles.finalCtaTitle}>Be Someone's Hero Today</h2>
          <p style={styles.finalCtaText}>
            Join thousands of donors who are saving lives right now. Register in
            minutes and start making a difference immediately.
          </p>
          <Link
            to="/register"
            style={{
              ...styles.ctaButton,
              transform:
                hoveredButton === "final"
                  ? "translateY(-3px) scale(1.05)"
                  : "translateY(0) scale(1)",
              boxShadow:
                hoveredButton === "final"
                  ? "0 8px 30px rgba(0, 0, 0, 0.2)"
                  : "0 4px 20px rgba(0, 0, 0, 0.1)",
            }}
            onMouseEnter={() => handleButtonHover("final")}
            onMouseLeave={() => handleButtonHover(null)}
          >
            <DropletIcon size={24} color={colors.secondary} />
            Start Saving Lives Now
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
