// src/context/AuthContext.js (FULL CODE - Enhanced for Reload/Persistence)

import React, { createContext, useState, useEffect, useContext } from "react";
import { getDonorProfile } from "../services/donorService"; // <-- NEW IMPORT

// 1. Create the Context
export const AuthContext = createContext();

// 2. Create the Provider Component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [loading, setLoading] = useState(true); // Set to true initially to check token

  // Function to handle login success
  const login = (newToken, donorData) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    setUser(donorData);
    setLoading(false);
  };

  // Function to handle logout
  const logout = () => {
    localStorage.removeItem("token");
    setToken(null);
    setUser(null);
  };

  // Function to update donor profile data after a successful PUT request
  const updateProfile = (newDonorData) => {
    setUser(newDonorData);
  };

  // 3. useEffect to handle initial load and token persistence check
  useEffect(() => {
    const checkAuthStatus = async () => {
      if (token) {
        try {
          // If token exists, fetch the user data to populate the state
          const donorData = await getDonorProfile(token);
          setUser(donorData);
        } catch (error) {
          // If token is invalid/expired, clear it
          console.error("Token invalid or expired. Logging out.", error);
          logout();
        }
      }
      setLoading(false); // Authentication check is complete
    };

    checkAuthStatus();
  }, []); // Run only once on mount

  // The context value we expose
  const contextValue = {
    user,
    token,
    isAuthenticated: !!token && !!user, // User must have a token AND data loaded
    loading,
    login,
    logout,
    updateProfile,
  };

  // If loading is true (checking token), you can show a splash screen or null
  if (loading) {
    // In a real application, you would return a loading spinner here
    return (
      <div style={{ textAlign: "center", padding: "100px" }}>
        Authenticating User...
      </div>
    );
  }

  return (
    <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>
  );
};

// Custom hook for easy access to auth context
export const useAuth = () => {
  return useContext(AuthContext);
};
