import React, { useState } from "react";
import { registerDonor } from "../services/donorService";

// --- Theme and Styles ---
const styles = {
  container: {
    maxWidth: "500px",
    margin: "40px auto",
    padding: "30px",
    backgroundColor: "#f7f9fc", // Lightest background
    borderRadius: "12px",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
  },
  heading: {
    textAlign: "center",
    color: "#00796b", // Teal/Medical green
    marginBottom: "20px",
    borderBottom: "2px solid #e0e0e0",
    paddingBottom: "10px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px", // Spacing between inputs
  },
  input: {
    padding: "12px 15px",
    border: "1px solid #b0bec5", // Subtle border
    borderRadius: "6px",
    fontSize: "16px",
    transition: "border-color 0.3s, box-shadow 0.3s",
  },
  inputFocus: {
    borderColor: "#00796b",
    boxShadow: "0 0 0 3px rgba(0, 121, 107, 0.2)",
    outline: "none",
  },
  button: {
    padding: "12px 20px",
    backgroundColor: "#d32f2f", // Deep Red (Blood/Urgency)
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background-color 0.3s, transform 0.1s",
    textTransform: "uppercase",
    letterSpacing: "1px",
  },
  buttonHover: {
    backgroundColor: "#c62828", // Slightly darker on hover
  },
  messageSuccess: {
    marginTop: "20px",
    padding: "15px",
    backgroundColor: "#e8f5e9", // Light Green for success
    border: "1px solid #4caf50",
    color: "#388e3c",
    borderRadius: "6px",
    textAlign: "center",
  },
  messageError: {
    marginTop: "20px",
    padding: "15px",
    backgroundColor: "#ffebee", // Light Red for error
    border: "1px solid #f44336",
    color: "#d32f2f",
    borderRadius: "6px",
    textAlign: "center",
  },
};

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    bloodGroup: "",
    city: "",
    phone: "",
  });

  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);
  const [isButtonHovered, setIsButtonHovered] = useState(false);
  const [focusedInput, setFocusedInput] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Registering...");
    setIsError(false);
    try {
      const data = await registerDonor(formData);
      setMessage("Registration successful! Thank you for your contribution.");
      setIsError(false);
      // Optional: Clear form data on success
      setFormData({
        name: "",
        email: "",
        password: "",
        bloodGroup: "",
        city: "",
        phone: "",
      });
    } catch (err) {
      const errorMsg =
        err.response?.data?.message ||
        "Error registering donor. Please check your information.";
      setMessage(errorMsg);
      setIsError(true);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>
        ðŸ©¸ Donor Registration{" "}
        <small
          style={{
            fontWeight: 400,
            fontSize: "0.6em",
            display: "block",
            color: "#546e7a",
          }}
        >
          Join the network, save a life.
        </small>
      </h2>
      <form style={styles.form} onSubmit={handleSubmit}>
        {/* Name Input */}
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          onFocus={() => setFocusedInput("name")}
          onBlur={() => setFocusedInput(null)}
          style={
            focusedInput === "name"
              ? { ...styles.input, ...styles.inputFocus }
              : styles.input
          }
          required
        />
        {/* Email Input */}
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          onFocus={() => setFocusedInput("email")}
          onBlur={() => setFocusedInput(null)}
          style={
            focusedInput === "email"
              ? { ...styles.input, ...styles.inputFocus }
              : styles.input
          }
          required
        />
        {/* Password Input */}
        <input
          type="password"
          name="password"
          placeholder="Secure Password"
          value={formData.password}
          onChange={handleChange}
          onFocus={() => setFocusedInput("password")}
          onBlur={() => setFocusedInput(null)}
          style={
            focusedInput === "password"
              ? { ...styles.input, ...styles.inputFocus }
              : styles.input
          }
          required
        />
        {/* Blood Group Input */}
        <input
          type="text"
          name="bloodGroup"
          placeholder="Blood Group (e.g., A+, O-)"
          value={formData.bloodGroup}
          onChange={handleChange}
          onFocus={() => setFocusedInput("bloodGroup")}
          onBlur={() => setFocusedInput(null)}
          style={
            focusedInput === "bloodGroup"
              ? { ...styles.input, ...styles.inputFocus }
              : styles.input
          }
          required
        />
        {/* City Input */}
        <input
          type="text"
          name="city"
          placeholder="City/Locality"
          value={formData.city}
          onChange={handleChange}
          onFocus={() => setFocusedInput("city")}
          onBlur={() => setFocusedInput(null)}
          style={
            focusedInput === "city"
              ? { ...styles.input, ...styles.inputFocus }
              : styles.input
          }
          required
        />
        {/* Phone Input */}
        <input
          type="tel" // Use type="tel" for better mobile experience
          name="phone"
          placeholder="Phone Number (e.g., 9876543210)"
          value={formData.phone}
          onChange={handleChange}
          onFocus={() => setFocusedInput("phone")}
          onBlur={() => setFocusedInput(null)}
          style={
            focusedInput === "phone"
              ? { ...styles.input, ...styles.inputFocus }
              : styles.input
          }
          required
        />
        <button
          type="submit"
          onMouseEnter={() => setIsButtonHovered(true)}
          onMouseLeave={() => setIsButtonHovered(false)}
          style={
            isButtonHovered
              ? { ...styles.button, ...styles.buttonHover }
              : styles.button
          }
        >
          Register & Donate
        </button>
      </form>
      {message && (
        <p style={isError ? styles.messageError : styles.messageSuccess}>
          {message}
        </p>
      )}
    </div>
  );
};

export default Register;
