const styles = `
  /* Admin Dashboard Styles */
  .admin-dashboard {
    min-height: 100vh;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
  }

  .admin-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 24px;
  }

  .admin-container--centered {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
  }

  /* Header */
  .admin-header {
    background: white;
    border-radius: 16px;
    padding: 32px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  }

  .admin-header__main {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 32px;
    flex-direction: column;
    gap: 24px;
  }

  @media (min-width: 768px) {
    .admin-header__main {
      flex-direction: row;
      align-items: center;
    }
  }

  .admin-header__title {
    font-size: 2rem;
    font-weight: 700;
    color: #1a1a1a;
    margin: 0 0 8px 0;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .admin-header__subtitle {
    color: #666;
    font-size: 1rem;
    margin: 0;
  }

  .admin-header__actions {
    display: flex;
    gap: 12px;
    flex-wrap: wrap;
  }

  /* Stats Grid */
  .stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
    gap: 20px;
  }

  .stat-card {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 24px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  }

  .stat-card__header {
    display: flex;
    align-items: center;
    gap: 12px;
    margin-bottom: 12px;
  }

  .stat-card__icon {
    width: 48px;
    height: 48px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
  }

  .stat-card__title {
    font-size: 0.875rem;
    opacity: 0.9;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .stat-card__value {
    font-size: 2.5rem;
    font-weight: 700;
    line-height: 1;
  }

  /* Controls Section */
  .controls-section {
    background: white;
    border-radius: 16px;
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  }

  .controls-section__search {
    margin-bottom: 24px;
  }

  .search-input {
    position: relative;
    width: 100%;
  }

  .search-input__icon {
    position: absolute;
    left: 16px;
    top: 50%;
    transform: translateY(-50%);
    color: #999;
  }

  .search-input__field {
    width: 100%;
    padding: 16px 48px 16px 48px;
    border: 2px solid #e0e0e0;
    border-radius: 12px;
    font-size: 1rem;
    transition: all 0.3s ease;
  }

  .search-input__field:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  .search-input__clear {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: #999;
    font-size: 24px;
    cursor: pointer;
    padding: 0;
    width: 24px;
    height: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .controls-section__filters {
    display: flex;
    flex-direction: column;
    gap: 20px;
  }

  @media (min-width: 768px) {
    .controls-section__filters {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }
  }

  .filters-group {
    display: flex;
    align-items: center;
    gap: 16px;
    flex-wrap: wrap;
  }

  .filters-label {
    font-weight: 600;
    color: #333;
    font-size: 0.875rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .filters-chips {
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
  }

  .filter-chip {
    padding: 8px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 20px;
    background: white;
    color: #666;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 6px;
  }

  .filter-chip:hover {
    border-color: #667eea;
    color: #667eea;
  }

  .filter-chip--active {
    background: #667eea;
    border-color: #667eea;
    color: white;
  }

  .filter-chip__count {
    background: rgba(255, 255, 255, 0.2);
    padding: 2px 8px;
    border-radius: 10px;
    font-size: 0.75rem;
  }

  .controls-section__sort {
    display: flex;
    gap: 8px;
    align-items: center;
  }

  .sort-select {
    padding: 10px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    background: white;
    color: #333;
    font-size: 0.875rem;
    cursor: pointer;
    min-width: 160px;
  }

  .sort-order {
    padding: 10px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    background: white;
    color: #333;
    cursor: pointer;
    font-weight: bold;
  }

  /* Main Content */
  .admin-main {
    background: white;
    border-radius: 16px;
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  }

  .error-alert {
    background: linear-gradient(135deg, #feb2b2 0%, #fed7d7 100%);
    border-left: 4px solid #e53e3e;
    padding: 16px;
    border-radius: 8px;
    margin-bottom: 24px;
    display: flex;
    align-items: flex-start;
    gap: 12px;
  }

  .error-alert__icon {
    font-size: 1.5rem;
  }

  .error-alert__content {
    flex: 1;
  }

  .error-alert__retry {
    margin-left: 12px;
    color: #e53e3e;
    text-decoration: underline;
    background: none;
    border: none;
    cursor: pointer;
    font-weight: 600;
  }

  .results-header {
    margin-bottom: 32px;
  }

  .results-title {
    font-size: 1.5rem;
    font-weight: 600;
    color: #1a1a1a;
    margin: 0 0 8px 0;
    display: flex;
    align-items: center;
    gap: 12px;
  }

  .results-count {
    background: #667eea;
    color: white;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 0.875rem;
    font-weight: 500;
  }

  .results-description {
    color: #666;
    margin: 0;
  }

  /* Donor Cards Grid - RESPONSIVE */
  .donors-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
  }

  @media (min-width: 640px) {
    .donors-grid {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .donors-grid {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  @media (min-width: 1400px) {
    .donors-grid {
      grid-template-columns: repeat(4, 1fr);
    }
  }

  /* Donor Card */
  .donor-card {
    background: white;
    border-radius: 12px;
    border: 1px solid #e0e0e0;
    overflow: hidden;
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
  }

  .donor-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
    border-color: #667eea;
  }

  .donor-card__header {
    padding: 20px;
    border-bottom: 1px solid #f0f0f0;
    display: flex;
    align-items: center;
    gap: 16px;
  }

  .donor-card__avatar {
    width: 48px;
    height: 48px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 1.25rem;
  }

  .donor-card__info {
    flex: 1;
  }

  .donor-card__name {
    margin: 0 0 4px 0;
    font-size: 1.125rem;
    font-weight: 600;
    color: #1a1a1a;
  }

  .donor-card__email {
    margin: 0 0 4px 0;
    color: #666;
    font-size: 0.875rem;
  }

  .donor-card__phone {
    margin: 0;
    color: #666;
    font-size: 0.875rem;
  }

  .donor-card__menu {
    background: none;
    border: none;
    color: #999;
    font-size: 1.5rem;
    cursor: pointer;
    padding: 0;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .donor-card__content {
    padding: 20px;
    flex: 1;
  }

  .donor-card__details {
    display: grid;
    gap: 16px;
  }

  .donor-card__detail {
    display: flex;
    flex-direction: column;
    gap: 4px;
  }

  .donor-card__detail-label {
    font-size: 0.75rem;
    color: #999;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .donor-card__blood-group {
    font-size: 1.25rem;
    font-weight: 700;
    color: #e53e3e;
  }

  .donor-card__location {
    font-weight: 500;
    color: #333;
  }

  .donor-card__footer {
    padding: 20px;
    border-top: 1px solid #f0f0f0;
  }

  .donor-card__actions {
    display: flex;
    gap: 12px;
  }

  /* Status Badge */
  .status-badge {
    display: inline-flex;
    align-items: center;
    gap: 6px;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.75rem;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .status-badge--success {
    background: #d1fae5;
    color: #065f46;
  }

  .status-badge--warning {
    background: #fef3c7;
    color: #92400e;
  }

  .status-badge--danger {
    background: #fee2e2;
    color: #991b1b;
  }

  .status-badge--info {
    background: #dbeafe;
    color: #1e40af;
  }

  /* Action Button */
  .action-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    padding: 12px 24px;
    border: none;
    border-radius: 8px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.875rem;
  }

  .action-button--primary {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
  }

  .action-button--secondary {
    background: white;
    color: #667eea;
    border: 2px solid #667eea;
  }

  .action-button--success {
    background: linear-gradient(135deg, #10b981 0%, #059669 100%);
    color: white;
  }

  .action-button--danger {
    background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
    color: white;
  }

  .action-button--disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  .action-button--full {
    width: 100%;
  }

  .action-button--small {
    padding: 8px 16px;
    font-size: 0.75rem;
  }

  /* Empty State */
  .empty-state {
    text-align: center;
    padding: 60px 20px;
  }

  .empty-state__icon {
    font-size: 4rem;
    margin-bottom: 24px;
    opacity: 0.5;
  }

  .empty-state__title {
    font-size: 1.5rem;
    color: #333;
    margin: 0 0 12px 0;
  }

  .empty-state__message {
    color: #666;
    max-width: 400px;
    margin: 0 auto 24px;
    line-height: 1.6;
  }

  /* Loading Skeleton */
  .loading-skeleton {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
  }

  @media (min-width: 640px) {
    .loading-skeleton {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .loading-skeleton {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  .skeleton {
    background: white;
    border-radius: 12px;
    border: 1px solid #e0e0e0;
    padding: 20px;
  }

  .skeleton__header {
    display: flex;
    align-items: center;
    gap: 16px;
    margin-bottom: 20px;
  }

  .skeleton__avatar {
    width: 48px;
    height: 48px;
    background: #f0f0f0;
    border-radius: 50%;
    animation: pulse 1.5s infinite;
  }

  .skeleton__info {
    flex: 1;
  }

  .skeleton__line {
    background: #f0f0f0;
    border-radius: 4px;
    animation: pulse 1.5s infinite;
  }

  .skeleton__line--title {
    height: 24px;
    width: 60%;
    margin-bottom: 8px;
  }

  .skeleton__line--subtitle {
    height: 16px;
    width: 40%;
  }

  .skeleton__content {
    margin-bottom: 20px;
  }

  .skeleton__detail {
    margin-bottom: 16px;
  }

  .skeleton__line--small {
    height: 12px;
    width: 40%;
    margin-bottom: 4px;
  }

  .skeleton__line--medium {
    height: 20px;
    width: 60%;
  }

  .skeleton__footer {
    display: flex;
    gap: 12px;
  }

  .skeleton__button {
    flex: 1;
    height: 40px;
    background: #f0f0f0;
    border-radius: 8px;
    animation: pulse 1.5s infinite;
  }

  /* Access Denied */
  .access-denied {
    background: white;
    border-radius: 16px;
    padding: 48px;
    text-align: center;
    max-width: 400px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  }

  .access-denied__icon {
    font-size: 4rem;
    margin-bottom: 24px;
  }

  .access-denied__title {
    font-size: 2rem;
    color: #dc2626;
    margin: 0 0 16px 0;
  }

  .access-denied__message {
    color: #666;
    line-height: 1.6;
    margin-bottom: 32px;
  }

  /* Admin Footer */
  .admin-footer {
    text-align: center;
    padding: 24px;
    color: #666;
    font-size: 0.875rem;
  }

  .admin-footer__text {
    margin: 0 0 8px 0;
  }

  .admin-footer__status {
    margin: 0;
    opacity: 0.7;
  }

  /* Animations */
  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  /* Responsive Adjustments */
  @media (max-width: 640px) {
    .admin-container {
      padding: 16px;
    }

    .admin-header,
    .controls-section,
    .admin-main {
      padding: 20px;
    }

    .stat-card__value {
      font-size: 2rem;
    }

    .admin-header__actions {
      width: 100%;
    }

    .action-button {
      flex: 1;
    }

    .controls-section__sort {
      width: 100%;
    }

    .sort-select {
      flex: 1;
      min-width: 0;
    }
  }
`;

// Inject styles
const styleSheet = document.createElement("style");
styleSheet.textContent = styles;
document.head.appendChild(styleSheet);
export default AdminDashboard;
