import React, { useState, useEffect, useCallback, useMemo } from "react";
import { useAuth } from "../context/AuthContext";
import {
  getAllDonorsAdmin,
  deleteDonorAdmin,
  toggleVerifyDonorAdmin,
} from "../services/donorService";

// Reusable Professional Components
const StatCard = ({ title, value, icon, color, trend }) => (
  <div className="stat-card">
    <div className="stat-card__header">
      <div className={`stat-card__icon ${color}`}>{icon}</div>
      <span className="stat-card__title">{title}</span>
    </div>
    <div className="stat-card__value">{value}</div>
    {trend && <div className="stat-card__trend">{trend}</div>}
  </div>
);

const StatusBadge = ({ status, type = "donor" }) => {
  const statusConfig = {
    verified: { label: "Verified", color: "success", icon: "✓" },
    pending: { label: "Pending", color: "warning", icon: "⏳" },
    blocked: { label: "Blocked", color: "danger", icon: "⛔" },
    active: { label: "Active", color: "info", icon: "●" },
  };

  const config = statusConfig[status] || statusConfig.pending;

  return (
    <span className={`status-badge status-badge--${config.color}`}>
      <span className="status-badge__icon">{config.icon}</span>
      <span className="status-badge__label">{config.label}</span>
    </span>
  );
};

const ActionButton = ({
  children,
  variant = "primary",
  size = "medium",
  icon,
  onClick,
  disabled = false,
  fullWidth = false,
}) => (
  <button
    className={`
      action-button 
      action-button--${variant} 
      action-button--${size}
      ${fullWidth ? "action-button--full" : ""}
      ${disabled ? "action-button--disabled" : ""}
    `}
    onClick={onClick}
    disabled={disabled}
  >
    {icon && <span className="action-button__icon">{icon}</span>}
    <span className="action-button__label">{children}</span>
  </button>
);

const SearchInput = ({ value, onChange, placeholder }) => (
  <div className="search-input">
    <span className="search-input__icon">🔍</span>
    <input
      type="text"
      value={value}
      onChange={(e) => onChange(e.target.value)}
      placeholder={placeholder}
      className="search-input__field"
    />
    {value && (
      <button
        className="search-input__clear"
        onClick={() => onChange("")}
        aria-label="Clear search"
      >
        ×
      </button>
    )}
  </div>
);

const FilterChip = ({ label, active, onClick, count }) => (
  <button
    className={`filter-chip ${active ? "filter-chip--active" : ""}`}
    onClick={onClick}
  >
    <span className="filter-chip__label">{label}</span>
    {count !== undefined && <span className="filter-chip__count">{count}</span>}
  </button>
);

const DonorCard = ({ donor, onVerify, onDelete }) => {
  const [showActions, setShowActions] = useState(false);

  return (
    <div className="donor-card">
      <div className="donor-card__header">
        <div className="donor-card__avatar">
          {donor.name.charAt(0).toUpperCase()}
        </div>
        <div className="donor-card__info">
          <h3 className="donor-card__name">{donor.name}</h3>
          <p className="donor-card__email">{donor.email}</p>
          {donor.phone && <p className="donor-card__phone">📞 {donor.phone}</p>}
        </div>
        <button
          className="donor-card__menu"
          onClick={() => setShowActions(!showActions)}
          aria-label="More options"
        >
          ⋮
        </button>
      </div>

      <div className="donor-card__content">
        <div className="donor-card__details">
          <div className="donor-card__detail">
            <span className="donor-card__detail-label">Blood Group</span>
            <span className="donor-card__blood-group">{donor.bloodGroup}</span>
          </div>
          <div className="donor-card__detail">
            <span className="donor-card__detail-label">Location</span>
            <span className="donor-card__location">
              {donor.city}
              {donor.state ? `, ${donor.state}` : ""}
            </span>
          </div>
          <div className="donor-card__detail">
            <span className="donor-card__detail-label">Status</span>
            <StatusBadge status={donor.isVerified ? "verified" : "pending"} />
          </div>
        </div>
      </div>

      <div className="donor-card__footer">
        <div className="donor-card__actions">
          <ActionButton
            variant={donor.isVerified ? "secondary" : "success"}
            size="small"
            icon={donor.isVerified ? "↶" : "✓"}
            onClick={() => onVerify(donor._id, donor.isVerified, donor.name)}
          >
            {donor.isVerified ? "Revoke" : "Verify"}
          </ActionButton>
          {donor.role !== "admin" && (
            <ActionButton
              variant="danger"
              size="small"
              icon="🗑️"
              onClick={() => onDelete(donor._id, donor.name)}
            >
              Delete
            </ActionButton>
          )}
        </div>
      </div>
    </div>
  );
};

const LoadingSkeleton = () => (
  <div className="loading-skeleton">
    {[...Array(3)].map((_, index) => (
      <div key={index} className="donor-card skeleton">
        <div className="skeleton__header">
          <div className="skeleton__avatar"></div>
          <div className="skeleton__info">
            <div className="skeleton__line skeleton__line--title"></div>
            <div className="skeleton__line skeleton__line--subtitle"></div>
          </div>
        </div>
        <div className="skeleton__content">
          <div className="skeleton__detail">
            <div className="skeleton__line skeleton__line--small"></div>
            <div className="skeleton__line skeleton__line--medium"></div>
          </div>
          <div className="skeleton__detail">
            <div className="skeleton__line skeleton__line--small"></div>
            <div className="skeleton__line skeleton__line--medium"></div>
          </div>
        </div>
        <div className="skeleton__footer">
          <div className="skeleton__button"></div>
          <div className="skeleton__button"></div>
        </div>
      </div>
    ))}
  </div>
);

const AdminDashboard = () => {
  const { user, token } = useAuth();
  const [donors, setDonors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("all");
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");
  const [mobileView, setMobileView] = useState(false);

  // Check screen size for responsive behavior
  useEffect(() => {
    const checkMobile = () => {
      setMobileView(window.innerWidth < 768);
    };

    checkMobile();
    window.addEventListener("resize", checkMobile);
    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  // Load donors on component mount
  useEffect(() => {
    if (token && user?.role === "admin") {
      fetchData();
    }
  }, [token, user]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getAllDonorsAdmin(token);
      setDonors(data);
    } catch (err) {
      console.error("Admin Fetch Error:", err);
      setError(err.message || "Failed to fetch donors list. Please try again.");
    } finally {
      setLoading(false);
    }
  }, [token]);

  const handleDelete = async (id, name) => {
    if (
      window.confirm(
        `Are you sure you want to delete ${name}'s account? This action cannot be undone.`
      )
    ) {
      try {
        await deleteDonorAdmin(id, token);
        setDonors((prev) => prev.filter((d) => d._id !== id));
      } catch (err) {
        alert(`Deletion failed: ${err.message || "Please try again."}`);
      }
    }
  };

  const handleToggleVerify = async (id, currentStatus, name) => {
    const action = currentStatus ? "revoke verification from" : "verify";
    if (window.confirm(`Are you sure you want to ${action} ${name}?`)) {
      try {
        await toggleVerifyDonorAdmin(id, !currentStatus, token);
        setDonors((prev) =>
          prev.map((d) =>
            d._id === id ? { ...d, isVerified: !currentStatus } : d
          )
        );
      } catch (err) {
        alert(`Update failed: ${err.message || "Please try again."}`);
      }
    }
  };

  // Filter and sort donors
  const filteredDonors = useMemo(() => {
    let result = donors.filter((donor) => {
      if (filter === "verified") return donor.isVerified;
      if (filter === "pending") return !donor.isVerified;
      return true;
    });

    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      result = result.filter(
        (donor) =>
          donor.name.toLowerCase().includes(term) ||
          donor.email.toLowerCase().includes(term) ||
          donor.city.toLowerCase().includes(term)
      );
    }

    return [...result].sort((a, b) => {
      let aValue = a[sortBy];
      let bValue = b[sortBy];

      if (sortBy === "name" || sortBy === "city") {
        aValue = (aValue || "").toLowerCase();
        bValue = (bValue || "").toLowerCase();
      }

      if (sortOrder === "asc") {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });
  }, [donors, filter, searchTerm, sortBy, sortOrder]);

  // Statistics
  const stats = useMemo(
    () => ({
      total: donors.length,
      verified: donors.filter((d) => d.isVerified).length,
      pending: donors.filter((d) => !d.isVerified).length,
      uniqueCities: new Set(donors.map((d) => d.city)).size,
    }),
    [donors]
  );

  // Security check
  if (user?.role !== "admin") {
    return (
      <div className="admin-container admin-container--centered">
        <div className="access-denied">
          <div className="access-denied__icon">🚫</div>
          <h1 className="access-denied__title">Access Denied</h1>
          <p className="access-denied__message">
            Administrator privileges required to access this dashboard. Please
            contact your system administrator.
          </p>
          <ActionButton
            variant="primary"
            onClick={() => (window.location.href = "/")}
            fullWidth
          >
            Return to Home
          </ActionButton>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <div className="admin-container">
        {/* Header */}
        <header className="admin-header">
          <div className="admin-header__main">
            <div>
              <h1 className="admin-header__title">Admin Dashboard</h1>
              <p className="admin-header__subtitle">Donor Management System</p>
            </div>
            <div className="admin-header__actions">
              <ActionButton
                variant="secondary"
                icon="↻"
                onClick={fetchData}
                disabled={loading}
              >
                Refresh
              </ActionButton>
              <ActionButton
                variant="primary"
                icon="📊"
                onClick={() => console.log("Export data")}
              >
                Export Data
              </ActionButton>
            </div>
          </div>

          {/* Stats Overview */}
          <div className="stats-grid">
            <StatCard
              title="Total Donors"
              value={stats.total}
              icon="👥"
              color="primary"
            />
            <StatCard
              title="Verified"
              value={stats.verified}
              icon="✅"
              color="success"
            />
            <StatCard
              title="Pending Review"
              value={stats.pending}
              icon="⏳"
              color="warning"
            />
            <StatCard
              title="Cities"
              value={stats.uniqueCities}
              icon="📍"
              color="info"
            />
          </div>
        </header>

        {/* Controls Section */}
        <section className="controls-section">
          <div className="controls-section__search">
            <SearchInput
              value={searchTerm}
              onChange={setSearchTerm}
              placeholder="Search donors by name, email, or location..."
            />
          </div>

          <div className="controls-section__filters">
            <div className="filters-group">
              <span className="filters-label">Filter by:</span>
              <div className="filters-chips">
                <FilterChip
                  label="All Donors"
                  active={filter === "all"}
                  onClick={() => setFilter("all")}
                  count={stats.total}
                />
                <FilterChip
                  label="Verified"
                  active={filter === "verified"}
                  onClick={() => setFilter("verified")}
                  count={stats.verified}
                />
                <FilterChip
                  label="Pending"
                  active={filter === "pending"}
                  onClick={() => setFilter("pending")}
                  count={stats.pending}
                />
              </div>
            </div>

            <div className="controls-section__sort">
              <select
                className="sort-select"
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                <option value="name">Sort by Name</option>
                <option value="bloodGroup">Sort by Blood Group</option>
                <option value="city">Sort by City</option>
                <option value="createdAt">Sort by Date</option>
              </select>
              <button
                className="sort-order"
                onClick={() =>
                  setSortOrder(sortOrder === "asc" ? "desc" : "asc")
                }
                aria-label={`Sort ${
                  sortOrder === "asc" ? "descending" : "ascending"
                }`}
              >
                {sortOrder === "asc" ? "↑" : "↓"}
              </button>
            </div>
          </div>
        </section>

        {/* Main Content */}
        <main className="admin-main">
          {error && (
            <div className="error-alert">
              <div className="error-alert__icon">⚠️</div>
              <div className="error-alert__content">
                <strong>Error Loading Data:</strong> {error}
                <button className="error-alert__retry" onClick={fetchData}>
                  Retry
                </button>
              </div>
            </div>
          )}

          {loading ? (
            <LoadingSkeleton />
          ) : (
            <>
              <div className="results-header">
                <h2 className="results-title">
                  Donor Management
                  <span className="results-count">
                    ({filteredDonors.length})
                  </span>
                </h2>
                <p className="results-description">
                  {searchTerm
                    ? `Search results for "${searchTerm}"`
                    : "All registered donors"}
                </p>
              </div>

              {filteredDonors.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-state__icon">📭</div>
                  <h3 className="empty-state__title">No donors found</h3>
                  <p className="empty-state__message">
                    {searchTerm
                      ? "No donors match your search criteria. Try a different search term."
                      : filter !== "all"
                      ? "No donors match the selected filter."
                      : "No donor accounts have been registered yet."}
                  </p>
                  {(searchTerm || filter !== "all") && (
                    <ActionButton
                      variant="secondary"
                      onClick={() => {
                        setSearchTerm("");
                        setFilter("all");
                      }}
                    >
                      Clear Filters
                    </ActionButton>
                  )}
                </div>
              ) : (
                <div className="donors-grid">
                  {filteredDonors.map((donor) => (
                    <DonorCard
                      key={donor._id}
                      donor={donor}
                      onVerify={handleToggleVerify}
                      onDelete={handleDelete}
                    />
                  ))}
                </div>
              )}
            </>
          )}
        </main>

        {/* Footer */}
        <footer className="admin-footer">
          <p className="admin-footer__text">
            © {new Date().getFullYear()} Blood Donation System. Admin Dashboard
            v1.0
          </p>
          <p className="admin-footer__status">
            Last updated:{" "}
            {new Date().toLocaleTimeString([], {
              hour: "2-digit",
              minute: "2-digit",
            })}
          </p>
        </footer>
      </div>
    </div>
  );
};

// CSS Styles - Professional Admin Dashboard
const styles = `
  /* Admin Dashboard Styles */
  .admin-dashboard {
    min-height: 100vh;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
  }

  .admin-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 24px;
  }

  .admin-container--centered {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
  }

  /* Header */
  .admin-header {
    background: white;
    border-radius: 16px;
    padding: 32px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  }

  .admin-header__main {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin-bottom: 32px;
    flex-direction: column;
    gap: 24px;
  }

  @media (min-width: 768px) {
    .admin-header__main {
      flex-direction: row;
      align-items: center;
    }
  }

  .admin-header__title {
    font-size: 2rem;
    font-weight: 700;
    color: #1a1a1a;
    margin: 0 0 8px 0;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .admin-header__subtitle {
    color: #666;
    font-size: 1rem;
    margin: 0;
  }

  .admin-header__actions {
    display: flex;
    gap: 12px;
    flex-wrap: wrap;
  }

  /* Stats Grid */
  .stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
    gap: 20px;
  }

  .stat-card {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 24px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
  }

  .stat-card__header {
    display: flex;
    align-items: center;
    gap: 12px;
    margin-bottom: 12px;
  }

  .stat-card__icon {
    width: 48px;
    height: 48px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
  }

  .stat-card__title {
    font-size: 0.875rem;
    opacity: 0.9;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .stat-card__value {
    font-size: 2.5rem;
    font-weight: 700;
    line-height: 1;
  }

  /* Controls Section */
  .controls-section {
    background: white;
    border-radius: 16px;
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  }

  .controls-section__search {
    margin-bottom: 24px;
  }

  .search-input {
    position: relative;
    width: 100%;
  }

  .search-input__icon {
    position: absolute;
    left: 16px;
    top: 50%;
    transform: translateY(-50%);
    color: #999;
  }

  .search-input__field {
    width: 100%;
    padding: 16px 48px 16px 48px;
    border: 2px solid #e0e0e0;
    border-radius: 12px;
    font-size: 1rem;
    transition: all 0.3s ease;
  }

  .search-input__field:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  .search-input__clear {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: #999;
    font-size: 24px;
    cursor: pointer;
    padding: 0;
    width: 24px;
    height: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .controls-section__filters {
    display: flex;
    flex-direction: column;
    gap: 20px;
  }

  @media (min-width: 768px) {
    .controls-section__filters {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }
  }

  .filters-group {
    display: flex;
    align-items: center;
    gap: 16px;
    flex-wrap: wrap;
  }

  .filters-label {
    font-weight: 600;
    color: #333;
    font-size: 0.875rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .filters-chips {
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
  }

  .filter-chip {
    padding: 8px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 20px;
    background: white;
    color: #666;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 6px;
  }

  .filter-chip:hover {
    border-color: #667eea;
    color: #667eea;
  }

  .filter-chip--active {
    background: #667eea;
    border-color: #667eea;
    color: white;
  }

  .filter-chip__count {
    background: rgba(255, 255, 255, 0.2);
    padding: 2px 8px;
    border-radius: 10px;
    font-size: 0.75rem;
  }

  .controls-section__sort {
    display: flex;
    gap: 8px;
    align-items: center;
  }

  .sort-select {
    padding: 10px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    background: white;
    color: #333;
    font-size: 0.875rem;
    cursor: pointer;
    min-width: 160px;
  }

  .sort-order {
    padding: 10px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    background: white;
    color: #333;
    cursor: pointer;
    font-weight: bold;
  }

  /* Main Content */
  .admin-main {
    background: white;
    border-radius: 16px;
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
  }

  .error-alert {
    background: linear-gradient(135deg, #feb2b2 0%, #fed7d7 100%);
    border-left: 4px solid #e53e3e;
    padding: 16px;
    border-radius: 8px;
    margin-bottom: 24px;
    display: flex;
    align-items: flex-start;
    gap: 12px;
  }

  .error-alert__icon {
    font-size: 1.5rem;
  }

  .error-alert__content {
    flex: 1;
  }

  .error-alert__retry {
    margin-left: 12px;
    color: #e53e3e;
    text-decoration: underline;
    background: none;
    border: none;
    cursor: pointer;
    font-weight: 600;
  }

  .results-header {
    margin-bottom: 32px;
  }

  .results-title {
    font-size: 1.5rem;
    font-weight: 600;
    color: #1a1a1a;
    margin: 0 0 8px 0;
    display: flex;
    align-items: center;
    gap: 12px;
  }

  .results-count {
    background: #667eea;
    color: white;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 0.875rem;
    font-weight: 500;
  }

  .results-description {
    color: #666;
    margin: 0;
  }

  /* Donor Cards Grid - RESPONSIVE */
  .donors-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
  }

  @media (min-width: 640px) {
    .donors-grid {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .donors-grid {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  @media (min-width: 1400px) {
    .donors-grid {
      grid-template-columns: repeat(4, 1fr);
    }
  }

  /* Donor Card */
  .donor-card {
    background: white;
    border-radius: 12px;
    border: 1px solid #e0e0e0;
    overflow: hidden;
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
  }

  .donor-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
    border-color: #667eea;
  }

  .donor-card__header {
    padding: 20px;
    border-bottom: 1px solid #f0f0f0;
    display: flex;
    align-items: center;
    gap: 16px;
  }

  .donor-card__avatar {
    width: 48px;
    height: 48px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 1.25rem;
  }

  .donor-card__info {
    flex: 1;
  }

  .donor-card__name {
    margin: 0 0 4px 0;
    font-size: 1.125rem;
    font-weight: 600;
    color: #1a1a1a;
  }

  .donor-card__email {
    margin: 0 0 4px 0;
    color: #666;
    font-size: 0.875rem;
  }

  .donor-card__phone {
    margin: 0;
    color: #666;
    font-size: 0.875rem;
  }

  .donor-card__menu {
    background: none;
    border: none;
    color: #999;
    font-size: 1.5rem;
    cursor: pointer;
    padding: 0;
    width: 32px;
    height: 32px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .donor-card__content {
    padding: 20px;
    flex: 1;
  }

  .donor-card__details {
    display: grid;
    gap: 16px;
  }

  .donor-card__detail {
    display: flex;
    flex-direction: column;
    gap: 4px;
  }

  .donor-card__detail-label {
    font-size: 0.75rem;
    color: #999;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .donor-card__blood-group {
    font-size: 1.25rem;
    font-weight: 700;
    color: #e53e3e;
  }

  .donor-card__location {
    font-weight: 500;
    color: #333;
  }

  .donor-card__footer {
    padding: 20px;
    border-top: 1px solid #f0f0f0;
  }

  .donor-card__actions {
    display: flex;
    gap: 12px;
  }

  /* Status Badge */
  .status-badge {
    display: inline-flex;
    align-items: center;
    gap: 6px;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.75rem;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .status-badge--success {
    background: #d1fae5;
    color: #065f46;
  }

  .status-badge--warning {
    background: #fef3c7;
    color: #92400e;
  }

  .status-badge--danger {
    background: #fee2e2;
    color: #991b1b;
  }

  .status-badge--info {
    background: #dbeafe;
    color: #1e40af;
  }

  /* Action Button */
  .action-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    padding: 12px 24px;
    border: none;
    border-radius: 8px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.875rem;
  }

  .action-button--primary {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
  }

  .action-button--secondary {
    background: white;
    color: #667eea;
    border: 2px solid #667eea;
  }

  .action-button--success {
    background: linear-gradient(135deg, #10b981 0%, #059669 100%);
    color: white;
  }

  .action-button--danger {
    background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
    color: white;
  }

  .action-button--disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  .action-button--full {
    width: 100%;
  }

  .action-button--small {
    padding: 8px 16px;
    font-size: 0.75rem;
  }

  /* Empty State */
  .empty-state {
    text-align: center;
    padding: 60px 20px;
  }

  .empty-state__icon {
    font-size: 4rem;
    margin-bottom: 24px;
    opacity: 0.5;
  }

  .empty-state__title {
    font-size: 1.5rem;
    color: #333;
    margin: 0 0 12px 0;
  }

  .empty-state__message {
    color: #666;
    max-width: 400px;
    margin: 0 auto 24px;
    line-height: 1.6;
  }

  /* Loading Skeleton */
  .loading-skeleton {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
  }

  @media (min-width: 640px) {
    .loading-skeleton {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .loading-skeleton {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  .skeleton {
    background: white;
    border-radius: 12px;
    border: 1px solid #e0e0e0;
    padding: 20px;
  }

  .skeleton__header {
    display: flex;
    align-items: center;
    gap: 16px;
    margin-bottom: 20px;
  }

  .skeleton__avatar {
    width: 48px;
    height: 48px;
    background: #f0f0f0;
    border-radius: 50%;
    animation: pulse 1.5s infinite;
  }

  .skeleton__info {
    flex: 1;
  }

  .skeleton__line {
    background: #f0f0f0;
    border-radius: 4px;
    animation: pulse 1.5s infinite;
  }

  .skeleton__line--title {
    height: 24px;
    width: 60%;
    margin-bottom: 8px;
  }

  .skeleton__line--subtitle {
    height: 16px;
    width: 40%;
  }

  .skeleton__content {
    margin-bottom: 20px;
  }

  .skeleton__detail {
    margin-bottom: 16px;
  }

  .skeleton__line--small {
    height: 12px;
    width: 40%;
    margin-bottom: 4px;
  }

  .skeleton__line--medium {
    height: 20px;
    width: 60%;
  }

  .skeleton__footer {
    display: flex;
    gap: 12px;
  }

  .skeleton__button {
    flex: 1;
    height: 40px;
    background: #f0f0f0;
    border-radius: 8px;
    animation: pulse 1.5s infinite;
  }

  /* Access Denied */
  .access-denied {
    background: white;
    border-radius: 16px;
    padding: 48px;
    text-align: center;
    max-width: 400px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  }

  .access-denied__icon {
    font-size: 4rem;
    margin-bottom: 24px;
  }

  .access-denied__title {
    font-size: 2rem;
    color: #dc2626;
    margin: 0 0 16px 0;
  }

  .access-denied__message {
    color: #666;
    line-height: 1.6;
    margin-bottom: 32px;
  }

  /* Admin Footer */
  .admin-footer {
    text-align: center;
    padding: 24px;
    color: #666;
    font-size: 0.875rem;
  }

  .admin-footer__text {
    margin: 0 0 8px 0;
  }

  .admin-footer__status {
    margin: 0;
    opacity: 0.7;
  }

  /* Animations */
  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }

  /* Responsive Adjustments */
  @media (max-width: 640px) {
    .admin-container {
      padding: 16px;
    }

    .admin-header,
    .controls-section,
    .admin-main {
      padding: 20px;
    }

    .stat-card__value {
      font-size: 2rem;
    }

    .admin-header__actions {
      width: 100%;
    }

    .action-button {
      flex: 1;
    }

    .controls-section__sort {
      width: 100%;
    }

    .sort-select {
      flex: 1;
      min-width: 0;
    }
  }
`;

// Inject styles
const styleSheet = document.createElement("style");
styleSheet.textContent = styles;
document.head.appendChild(styleSheet);

export default AdminDashboard;
