        /* Enhanced CSS with BloodConnect Theme */
        .admin-dashboard {
          min-height: 100vh;
          background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%);
          font-family: "Inter", -apple-system, BlinkMacSystemFont, "Segoe UI",
            Roboto, sans-serif;
        }

        .admin-container {
          max-width: 1400px;
          margin: 0 auto;
          padding: 24px;
          width: 100%;
          box-sizing: border-box;
        }

        /* Header Styles */
        .admin-header {
          background: white;
          border-radius: 20px;
          padding: 32px;
          margin-bottom: 24px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.05);
          border: 1px solid #e2e8f0;
        }

        .admin-header__top {
          display: flex;
          flex-direction: column;
          gap: 24px;
          margin-bottom: 32px;
        }

        @media (min-width: 768px) {
          .admin-header__top {
            flex-direction: row;
            justify-content: space-between;
            align-items: center;
          }
        }

        .header-left {
          flex: 1;
        }

        .admin-title {
          font-size: clamp(1.75rem, 4vw, 2.5rem);
          font-weight: 700;
          color: #1e293b;
          margin: 0 0 8px 0;
          background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .admin-subtitle {
          color: #64748b;
          font-size: 1.125rem;
          margin: 0;
        }

        .header-actions {
          display: flex;
          gap: 12px;
          flex-wrap: wrap;
        }

        /* Stats Overview */
        .stats-overview {
          display: grid;
          grid-template-columns: repeat(
            auto-fit,
            minmax(min(240px, 100%), 1fr)
          );
          gap: 20px;
        }

        .stat-card {
          border-radius: 16px;
          padding: 24px;
          transition: transform 0.2s ease;
        }

        .stat-card:hover {
          transform: translateY(-2px);
        }

        .stat-card__content {
          display: flex;
          align-items: center;
          gap: 20px;
        }

        .stat-card__icon-wrapper {
          font-size: 2rem;
        }

        .stat-card__value {
          font-size: 2.25rem;
          font-weight: 700;
          margin-bottom: 4px;
        }

        .stat-card__title {
          font-size: 0.875rem;
          font-weight: 500;
          opacity: 0.9;
          text-transform: uppercase;
          letter-spacing: 0.05em;
        }

        /* Controls Section */
        .controls-section {
          background: white;
          border-radius: 20px;
          padding: 32px;
          margin-bottom: 24px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.05);
          border: 1px solid #e2e8f0;
        }

        .controls-section__main {
          display: flex;
          flex-direction: column;
          gap: 32px;
        }

        .search-section {
          width: 100%;
        }

        .filters-section,
        .sort-section {
          display: flex;
          flex-direction: column;
          gap: 16px;
        }

        @media (min-width: 768px) {
          .controls-section__main {
            flex-direction: row;
            align-items: center;
            gap: 24px;
          }

          .search-section {
            flex: 1;
          }

          .filters-section,
          .sort-section {
            flex-direction: row;
            align-items: center;
          }
        }

        .filters-header,
        .sort-header {
          display: flex;
          align-items: center;
          gap: 8px;
          color: #475569;
          font-weight: 600;
          font-size: 0.875rem;
          text-transform: uppercase;
          letter-spacing: 0.05em;
        }

        .filters-chips {
          display: flex;
          gap: 8px;
          flex-wrap: wrap;
        }

        .filter-chip {
          padding: 8px 16px;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          background: #f8fafc;
          color: #475569;
          font-size: 0.875rem;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s ease;
          display: inline-flex;
          align-items: center;
          gap: 6px;
        }

        .filter-chip:hover {
          border-color: #cbd5e1;
          background: #f1f5f9;
        }

        .filter-chip--active {
          background: #ef4444;
          border-color: #ef4444;
          color: white;
        }

        .filter-chip__count {
          background: rgba(255, 255, 255, 0.2);
          padding: 2px 8px;
          border-radius: 8px;
          font-size: 0.75rem;
        }

        .sort-controls {
          display: flex;
          gap: 8px;
          align-items: center;
        }

        .sort-select {
          padding: 10px 16px;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          background: #f8fafc;
          color: #475569;
          font-size: 0.875rem;
          font-weight: 500;
          cursor: pointer;
          min-width: 140px;
        }

        .sort-order-btn {
          padding: 10px 16px;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          background: #f8fafc;
          color: #475569;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        /* Search Input */
        .search-input {
          width: 100%;
        }

        .search-input__wrapper {
          position: relative;
          width: 100%;
        }

        .search-input__icon {
          position: absolute;
          left: 20px;
          top: 50%;
          transform: translateY(-50%);
          color: #94a3b8;
          font-size: 1.125rem;
        }

        .search-input__field {
          width: 100%;
          padding: 16px 56px 16px 52px;
          border: 2px solid #e2e8f0;
          border-radius: 16px;
          font-size: 1rem;
          background: #f8fafc;
          color: #1e293b;
          transition: all 0.2s ease;
        }

        .search-input__field:focus {
          outline: none;
          border-color: #ef4444;
          background: white;
          box-shadow: 0 0 0 4px rgba(239, 68, 68, 0.1);
        }

        .search-input__clear {
          position: absolute;
          right: 20px;
          top: 50%;
          transform: translateY(-50%);
          background: #cbd5e1;
          color: white;
          border: none;
          border-radius: 50%;
          width: 24px;
          height: 24px;
          cursor: pointer;
          font-size: 1.125rem;
          display: flex;
          align-items: center;
          justify-content: center;
          line-height: 1;
        }

        .search-input__clear:hover {
          background: #94a3b8;
        }

        /* Main Content */
        .admin-main {
          background: white;
          border-radius: 20px;
          padding: 32px;
          margin-bottom: 24px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.05);
          border: 1px solid #e2e8f0;
        }

        .content-header {
          margin-bottom: 32px;
        }

        .content-title {
          font-size: 1.75rem;
          font-weight: 600;
          color: #1e293b;
          margin: 0 0 8px 0;
          display: flex;
          align-items: center;
          gap: 12px;
          flex-wrap: wrap;
        }

        .donor-count {
          background: #f1f5f9;
          color: #475569;
          padding: 4px 12px;
          border-radius: 12px;
          font-size: 0.875rem;
          font-weight: 500;
        }

        .content-description {
          color: #64748b;
          margin: 0;
        }

        /* Donor Cards Grid */
        .donors-grid {
          display: grid;
          grid-template-columns: 1fr;
          gap: 24px;
        }

        @media (min-width: 640px) {
          .donors-grid {
            grid-template-columns: repeat(2, 1fr);
          }
        }

        @media (min-width: 1024px) {
          .donors-grid {
            grid-template-columns: repeat(3, 1fr);
          }
        }

        @media (min-width: 1400px) {
          .donors-grid {
            grid-template-columns: repeat(4, 1fr);
          }
        }

        /* Donor Card */
        .donor-card {
          background: white;
          border-radius: 16px;
          border: 1px solid #e2e8f0;
          overflow: hidden;
          transition: all 0.3s ease;
          display: flex;
          flex-direction: column;
        }

        .donor-card:hover {
          transform: translateY(-4px);
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
          border-color: #ef4444;
        }

        .donor-card__header {
          padding: 24px;
          border-bottom: 1px solid #f1f5f9;
        }

        .donor-card__avatar {
          display: flex;
          align-items: center;
          gap: 16px;
          margin-bottom: 16px;
        }

        .avatar-initial {
          width: 56px;
          height: 56px;
          background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%);
          color: white;
          border-radius: 12px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1.5rem;
          font-weight: 600;
        }

        .donor-card__info {
          flex: 1;
        }

        .donor-card__name {
          font-size: 1.25rem;
          font-weight: 600;
          color: #1e293b;
          margin: 0 0 12px 0;
        }

        .donor-card__contact {
          display: flex;
          flex-direction: column;
          gap: 8px;
        }

        .contact-item {
          display: flex;
          align-items: center;
          gap: 8px;
          color: #64748b;
          font-size: 0.875rem;
        }

        .contact-icon {
          color: #94a3b8;
          font-size: 0.875rem;
        }

        .donor-card__details {
          padding: 24px;
          flex: 1;
        }

        .detail-row {
          display: grid;
          grid-template-columns: 1fr;
          gap: 20px;
        }

        @media (min-width: 640px) {
          .detail-row {
            grid-template-columns: 1fr 1fr;
          }
        }

        .detail-item {
