// src/pages/Home.js (CLEAN, PROFESSIONAL LAYOUT)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Clean, Professional Color Palette
const colors = {
  primary: "#D32F2F", // Medical Red (accent color)
  secondary: "#1976D2", // Professional Blue
  dark: "#263238", // Dark text
  light: "#F5F5F5", // Light background
  white: "#FFFFFF",
  gray: "#757575",
  success: "#388E3C",
  warning: "#F57C00",
};

// Professional Icons
const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill={color}>
    <path d="M12,2.69l5.66,5.66a8,8,0,1,1-11.31,0Z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

const CheckIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20 6L9 17l-5-5" />
  </svg>
);

const PhoneIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z" />
  </svg>
);

// Clean, Structured Styles
const styles = {
  container: {
    fontFamily: "'Inter', 'Segoe UI', Roboto, sans-serif",
    color: colors.dark,
    lineHeight: 1.6,
  },

  // Hero Section
  hero: {
    background: `linear-gradient(135deg, ${colors.secondary} 0%, #1565C0 100%)`,
    color: colors.white,
    padding: "120px 20px 80px",
    textAlign: "center",
  },

  heroTitle: {
    fontSize: "clamp(2.5rem, 5vw, 3.5rem)",
    fontWeight: "700",
    marginBottom: "24px",
    lineHeight: 1.2,
  },

  heroSubtitle: {
    fontSize: "1.25rem",
    maxWidth: "700px",
    margin: "0 auto 48px",
    opacity: 0.9,
  },

  heroButton: {
    backgroundColor: colors.primary,
    color: colors.white,
    padding: "16px 36px",
    borderRadius: "8px",
    fontSize: "1.1rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    transition: "all 0.3s ease",
  },

  // Stats Section
  statsSection: {
    padding: "80px 20px",
    backgroundColor: colors.light,
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "30px",
    maxWidth: "1200px",
    margin: "0 auto",
  },

  statCard: {
    backgroundColor: colors.white,
    borderRadius: "12px",
    padding: "40px 30px",
    textAlign: "center",
    boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
  },

  statIcon: {
    width: "60px",
    height: "60px",
    backgroundColor: `${colors.primary}15`,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 20px",
  },

  statNumber: {
    fontSize: "2.5rem",
    fontWeight: "700",
    color: colors.primary,
    marginBottom: "8px",
  },

  statText: {
    fontSize: "1.1rem",
    color: colors.gray,
  },

  // Blood Status Section
  bloodStatusSection: {
    padding: "80px 20px",
    backgroundColor: colors.white,
  },

  sectionTitle: {
    fontSize: "2.5rem",
    fontWeight: "700",
    textAlign: "center",
    marginBottom: "48px",
    color: colors.dark,
  },

  bloodGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "24px",
    maxWidth: "1000px",
    margin: "0 auto 48px",
  },

  bloodCard: {
    backgroundColor: colors.light,
    borderRadius: "12px",
    padding: "30px 20px",
    textAlign: "center",
    borderTop: "4px solid transparent",
  },

  bloodType: {
    fontSize: "2.5rem",
    fontWeight: "700",
    marginBottom: "12px",
  },

  bloodStatus: {
    fontSize: "1rem",
    fontWeight: "600",
    padding: "6px 16px",
    borderRadius: "20px",
    display: "inline-block",
  },

  // Testimonial Section
  testimonialSection: {
    padding: "80px 20px",
    backgroundColor: colors.light,
  },

  testimonial: {
    maxWidth: "800px",
    margin: "0 auto",
    textAlign: "center",
    fontSize: "1.25rem",
    fontStyle: "italic",
    color: colors.gray,
    lineHeight: 1.8,
  },

  testimonialAuthor: {
    marginTop: "24px",
    color: colors.primary,
    fontWeight: "600",
  },

  // Process Section
  processSection: {
    padding: "80px 20px",
    backgroundColor: colors.white,
  },

  processGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "40px",
    maxWidth: "1200px",
    margin: "0 auto",
  },

  processCard: {
    textAlign: "center",
  },

  processNumber: {
    width: "60px",
    height: "60px",
    backgroundColor: colors.secondary,
    color: colors.white,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "1.5rem",
    fontWeight: "700",
    margin: "0 auto 24px",
  },

  processTitle: {
    fontSize: "1.5rem",
    fontWeight: "600",
    marginBottom: "16px",
    color: colors.dark,
  },

  processDescription: {
    color: colors.gray,
    lineHeight: 1.6,
  },

  // CTA Section
  ctaSection: {
    padding: "80px 20px",
    backgroundColor: colors.primary,
    color: colors.white,
    textAlign: "center",
  },

  ctaTitle: {
    fontSize: "2.5rem",
    fontWeight: "700",
    marginBottom: "24px",
  },

  ctaSubtitle: {
    fontSize: "1.25rem",
    maxWidth: "700px",
    margin: "0 auto 48px",
    opacity: 0.9,
  },

  ctaButton: {
    backgroundColor: colors.white,
    color: colors.primary,
    padding: "18px 40px",
    borderRadius: "8px",
    fontSize: "1.2rem",
    fontWeight: "600",
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    transition: "all 0.3s ease",
  },

  // Emergency Section
  emergencySection: {
    padding: "60px 20px",
    backgroundColor: colors.dark,
    color: colors.white,
    textAlign: "center",
  },

  emergencyContent: {
    maxWidth: "600px",
    margin: "0 auto",
  },

  emergencyTitle: {
    fontSize: "1.5rem",
    marginBottom: "16px",
    opacity: 0.9,
  },

  emergencyNumber: {
    fontSize: "2.5rem",
    fontWeight: "700",
    color: colors.primary,
  },

  // Footer
  footer: {
    padding: "60px 20px",
    backgroundColor: "#1A237E",
    color: colors.white,
  },

  footerContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "40px",
  },

  footerSection: {
    marginBottom: "30px",
  },

  footerTitle: {
    fontSize: "1.2rem",
    fontWeight: "600",
    marginBottom: "20px",
  },

  footerLinks: {
    listStyle: "none",
    padding: 0,
  },

  footerLink: {
    color: "rgba(255,255,255,0.8)",
    textDecoration: "none",
    marginBottom: "12px",
    display: "block",
    transition: "color 0.3s ease",
  },

  contactInfo: {
    color: "rgba(255,255,255,0.8)",
    marginBottom: "12px",
  },

  copyright: {
    textAlign: "center",
    marginTop: "40px",
    paddingTop: "40px",
    borderTop: "1px solid rgba(255,255,255,0.1)",
    color: "rgba(255,255,255,0.6)",
    fontSize: "0.9rem",
  },
};

const Home = () => {
  const [hoveredButton, setHoveredButton] = useState(null);

  const bloodStatusData = [
    {
      type: "O-",
      status: "CRITICAL",
      color: colors.primary,
      bgColor: `${colors.primary}15`,
    },
    {
      type: "B-",
      status: "LOW",
      color: colors.warning,
      bgColor: `${colors.warning}15`,
    },
    {
      type: "A+",
      status: "MODERATE",
      color: colors.secondary,
      bgColor: `${colors.secondary}15`,
    },
    {
      type: "AB+",
      status: "ADEQUATE",
      color: colors.success,
      bgColor: `${colors.success}15`,
    },
  ];

  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <h1 style={styles.heroTitle}>Your Gift, Someone's Second Chance</h1>
        <p style={styles.heroSubtitle}>
          Join our network of life-savers. Your blood donation can be the
          miracle someone is praying for in their darkest hour.
        </p>
        <Link
          to="/register"
          style={{
            ...styles.heroButton,
            transform: hoveredButton === "hero" ? "translateY(-2px)" : "none",
            boxShadow:
              hoveredButton === "hero"
                ? "0 8px 20px rgba(211, 47, 47, 0.4)"
                : "none",
          }}
          onMouseEnter={() => setHoveredButton("hero")}
          onMouseLeave={() => setHoveredButton(null)}
        >
          <DropletIcon size={20} color={colors.white} />
          Register to Donate
        </Link>
      </section>

      {/* Stats Section */}
      <section style={styles.statsSection}>
        <div style={styles.statsGrid}>
          <div style={styles.statCard}>
            <div style={styles.statIcon}>
