// src/pages/Home.js (CLEAN & PROFESSIONAL DESIGN)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Clean Color Palette
const colors = {
  primary: "#D32F2F", // Medical Red
  secondary: "#1A237E", // Deep Blue
  accent: "#388E3C", // Medical Green
  lightBg: "#F8F9FA", // Light background
  white: "#FFFFFF",
  darkText: "#212121",
  lightText: "#666666",
  success: "#4CAF50",
  warning: "#FF9800",
};

// Icons
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill={color}>
    <path d="M12,2.69l5.66,5.66a8,8,0,1,1-11.31,0Z" />
  </svg>
);

const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
