// src/pages/Home.js (BEAUTIFUL & PROFESSIONAL BLOOD BANK DESIGN)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Professional Medical Color Palette
const colors = {
  primary: "#D32F2F", // Medical Red - Urgency
  secondary: "#1A237E", // Deep Blue - Trust
  accent: "#388E3C", // Medical Green - Health
  lightRed: "#FFEBEE", // Soft Red Background
  lightBlue: "#E8EAF6", // Soft Blue Background
  white: "#FFFFFF",
  darkText: "#212121",
  grayText: "#757575",
  gold: "#FFC107", // For highlights
  criticalRed: "#B71C1C", // Darker red for critical
};

// Professional SVG Icons
const HeartBeatIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M22 12h-4l-3 9L9 3l-3 9H2" />
  </svg>
);

const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill={color}>
    <path d="M12,2.69l5.66,5.66a8,8,0,1,1-11.31,0Z" />
  </svg>
);

const HospitalIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <rect x="3" y="8" width="18" height="14" rx="2" />
    <path d="M7 8V4a2 2 0 012-2h6a2 2 0 012 2v4M12 18v-6M9 15h6" />
  </svg>
);

const ShieldCheckIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
    <path d="M9 12l2 2 4-4" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

const PhoneIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
