// src/pages/DonorProfile.js (Updated with Context)

import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext"; // <-- NEW IMPORT

// --- Profile Page Styles ---
const styles = {
  // ... (Styles remain the same as defined in previous steps)
  container: {
    maxWidth: "800px",
    margin: "40px auto",
    padding: "30px",
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 6px 20px rgba(0, 0, 0, 0.1)",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    borderLeft: "5px solid #d32f2f",
  },
  heading: {
    color: "#00796b",
    borderBottom: "2px solid #e0e0e0",
    paddingBottom: "10px",
    marginBottom: "25px",
  },
  sectionTitle: {
    color: "#d32f2f",
    marginTop: "30px",
    marginBottom: "15px",
    borderBottom: "1px dashed #d32f2f50",
    paddingBottom: "5px",
  },
  infoRow: {
    display: "flex",
    padding: "10px 0",
    borderBottom: "1px dashed #e0e0e0",
    alignItems: "center",
  },
  label: {
    fontWeight: "bold",
    color: "#546e7a",
    width: "150px",
  },
  value: {
    color: "#333",
    flexGrow: 1,
  },
  bloodGroupBadge: {
    backgroundColor: "#d32f2f",
    color: "white",
    padding: "5px 10px",
    borderRadius: "4px",
    fontWeight: "bold",
    fontSize: "1.1em",
  },
  inputGroup: {
    display: "flex",
    gap: "15px",
    marginBottom: "20px",
    flexWrap: "wrap",
  },
  input: {
    padding: "10px 15px",
    border: "1px solid #b0bec5",
    borderRadius: "6px",
    fontSize: "16px",
    flex: 1,
    minWidth: "150px",
  },
  button: {
    padding: "12px 20px",
    backgroundColor: "#00796b",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background-color 0.3s",
    textTransform: "uppercase",
  },
  message: {
    textAlign: "center",
    padding: "15px",
    borderRadius: "8px",
    marginTop: "20px",
  },
};

const API_URL = "http://localhost:5000/api/donors";

const DonorProfile = () => {
  const { user, token, isAuthenticated, updateProfile } = useAuth(); // <-- Use Context!
  const [updates, setUpdates] = useState({});
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  // NOTE: If user object is null, the PrivateRoute should have already redirected.
  // We rely on the global 'user' object for display.

  const handleChange = (e) => {
    setUpdates({ ...updates, [e.target.name]: e.target.value });
  };

  const handleUpdate = async () => {
    if (Object.keys(updates).length === 0) {
      setMessage("No changes detected.");
      return;
    }

    setIsSubmitting(true);

    try {
      const res = await axios.put(`${API_URL}/profile`, updates, {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Use the context function to update the global user state
      updateProfile(res.data);
      setUpdates({});
      setMessage("Profile updated successfully! Global state synchronized.");
    } catch (err) {
      setMessage(err.response?.data?.message || "Error updating profile.");
      console.error("Profile update error:", err);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Display loading/error message if the global user object hasn't been set yet
  if (!isAuthenticated || !user) {
    // This state should be rare due to PrivateRoute, but handles context loading edge cases.
    return (
      <div
        style={{
          ...styles.container,
          backgroundColor: "#ffebee",
          borderLeft: "5px solid #f44336",
        }}
      >
        <p
          style={{ textAlign: "center", color: "#f44336", fontWeight: "bold" }}
        >
          Profile Data Not Found. Please Log In Again.
        </p>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>
        Donor Profile üßë‚Äç‚öïÔ∏è{" "}
        <small
          style={{
            fontWeight: 400,
            fontSize: "0.6em",
            display: "block",
            color: "#546e7a",
          }}
        >
          Data is synchronized from global state.
        </small>
      </h2>

      {/* --- Current Information Section --- */}
      <div style={styles.infoRow}>
        <span style={styles.label}>Name:</span>
        <span style={styles.value}>{user.name}</span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>Email:</span>
        <span style={styles.value}>{user.email}</span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>Blood Group:</span>
        <span style={styles.value}>
          <span style={styles.bloodGroupBadge}>{user.bloodGroup}</span>
        </span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>City:</span>
        <span style={styles.value}>{user.city}</span>
      </div>
      <div style={styles.infoRow}>
        <span style={styles.label}>Phone:</span>
        <span style={styles.value}>{user.phone}</span>
      </div>

      {/* --- Update Form Section --- */}
      <h3 style={styles.sectionTitle}>Update Contact & Location</h3>
      <div style={styles.inputGroup}>
        <input
          placeholder={`Update City (Current: ${user.city})`}
          name="city"
          onChange={handleChange}
          style={styles.input}
          value={updates.city || ""}
        />
        <input
          placeholder={`Update Phone (Current: ${user.phone})`}
          name="phone"
          onChange={handleChange}
          style={styles.input}
          value={updates.phone || ""}
        />
        <input
          placeholder={`Update Blood Group (Current: ${user.bloodGroup})`}
          name="bloodGroup"
          onChange={handleChange}
          style={styles.input}
          value={updates.bloodGroup || ""}
        />
      </div>

      <button
        onClick={handleUpdate}
        style={styles.button}
        disabled={isSubmitting}
      >
        {isSubmitting ? "Saving..." : "Save Changes"}
      </button>

      {/* --- Feedback Message --- */}
      {message && (
        <p
          style={{
            ...styles.message,
            backgroundColor: message.includes("successfully")
              ? "#e8f5e9"
              : "#fff3e0",
            color: message.includes("successfully") ? "#388e3c" : "#ff9800",
          }}
        >
          {message}
        </p>
      )}
    </div>
  );
};

export default DonorProfile;
