import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom"; // Added for Admin Link
import { useAuth } from "../context/AuthContext";
import {
  FiUser,
  FiMail,
  FiDroplet,
  FiMapPin,
  FiPhone,
  FiSave,
  FiRefreshCw,
  FiCheckCircle,
  FiAlertCircle,
  FiHeart,
  FiShield,
  FiClock,
  FiEdit2,
  FiSettings, // Added for Admin icon
} from "react-icons/fi";

const BLOOD_GROUPS = ["A+", "A-", "B+", "B-", "AB+", "AB-", "O+", "O-"];

const styles = {
  // ... (All your existing styles remain exactly as you designed them)
  pageWrapper: {
    minHeight: "100vh",
    background:
      "linear-gradient(135deg, rgba(139, 0, 0, 0.92) 0%, rgba(178, 34, 34, 0.88) 100%)",
    position: "relative",
    padding: "20px",
    fontFamily: "'Poppins', sans-serif",
  },
  backgroundOverlay: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage:
      "url('https://images.unsplash.com/photo-1584634731339-252c581abfc5?w=1920&q=80')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    opacity: 0.15,
    zIndex: 1,
  },
  container: {
    maxWidth: "1200px",
    margin: "30px auto",
    position: "relative",
    zIndex: 2,
  },
  headerCard: {
    backgroundColor: "rgba(255, 255, 255, 0.98)",
    backdropFilter: "blur(10px)",
    borderRadius: "24px",
    padding: "40px",
    marginBottom: "30px",
    boxShadow: "0 20px 60px rgba(0, 0, 0, 0.3)",
    border: "1px solid rgba(255, 255, 255, 0.3)",
  },
  heroSection: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: "30px",
  },
  heroContent: { flex: 1 },
  title: {
    fontSize: "2.8rem",
    fontWeight: "800",
    background: "linear-gradient(135deg, #cf1322 0%, #ff6b6b 100%)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "1.1rem",
    color: "#5a5a5a",
    fontWeight: "400",
    lineHeight: "1.6",
    maxWidth: "700px",
  },
  heroStats: { display: "flex", gap: "30px", marginTop: "30px" },
  statItem: { display: "flex", alignItems: "center", gap: "15px" },
  statIcon: {
    backgroundColor: "#fff0f0",
    width: "50px",
    height: "50px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "#cf1322",
    fontSize: "22px",
  },
  statText: { display: "flex", flexDirection: "column" },
  statValue: { fontSize: "1.6rem", fontWeight: "700", color: "#1a1a1a" },
  statLabel: { fontSize: "0.9rem", color: "#666", fontWeight: "500" },
  statusBadge: {
    backgroundColor: "#fff0f0",
    borderRadius: "20px",
    padding: "30px",
    minWidth: "180px",
    textAlign: "center",
    border: "2px solid #ffcccc",
  },
  profileGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(350px, 1fr))",
    gap: "25px",
    marginBottom: "30px",
  },
  profileCard: {
    backgroundColor: "rgba(255, 255, 255, 0.98)",
    borderRadius: "20px",
    padding: "30px",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.15)",
    border: "1px solid rgba(255, 255, 255, 0.3)",
    position: "relative",
    overflow: "hidden",
  },
  cardHeader: {
    display: "flex",
    alignItems: "center",
    gap: "15px",
    marginBottom: "25px",
    paddingBottom: "15px",
    borderBottom: "2px solid rgba(0, 0, 0, 0.05)",
  },
  cardIcon: {
    backgroundColor: "#fff0f0",
    width: "50px",
    height: "50px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "#cf1322",
    fontSize: "22px",
  },
  cardTitle: {
    fontSize: "1.3rem",
    fontWeight: "600",
    color: "#1a1a1a",
    margin: 0,
  },
  infoRow: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "15px 0",
    borderBottom: "1px solid rgba(0, 0, 0, 0.05)",
  },
  infoLabel: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
    fontSize: "0.95rem",
    color: "#666",
    fontWeight: "500",
  },
  infoValue: { fontSize: "1rem", fontWeight: "600", color: "#1a1a1a" },
  bloodGroupBadge: {
    backgroundColor: "#fff0f0",
    color: "#cf1322",
    padding: "8px 20px",
    borderRadius: "50px",
    fontWeight: "700",
    fontSize: "1rem",
    display: "flex",
    alignItems: "center",
    gap: "8px",
    border: "2px solid #ffcccc",
    minWidth: "80px",
    justifyContent: "center",
  },
  updateCard: {
    backgroundColor: "rgba(255, 255, 255, 0.98)",
    borderRadius: "20px",
    padding: "40px",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.15)",
    borderTop: "4px solid #cf1322",
    marginTop: "30px",
  },
  updateHeader: {
    display: "flex",
    alignItems: "center",
    gap: "15px",
    marginBottom: "30px",
  },
  updateIcon: {
    backgroundColor: "#fff0f0",
    width: "50px",
    height: "50px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "#cf1322",
    fontSize: "24px",
  },
  updateTitle: {
    fontSize: "1.5rem",
    fontWeight: "700",
    color: "#1a1a1a",
    margin: 0,
  },
  updateSubtitle: { fontSize: "1rem", color: "#666", marginTop: "5px" },
  inputGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "25px",
    marginBottom: "35px",
  },
  inputGroup: { position: "relative" },
  inputLabel: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    fontSize: "0.95rem",
    color: "#555",
    fontWeight: "600",
    marginBottom: "12px",
  },
  inputWrapper: { position: "relative" },
  input: {
    width: "100%",
    padding: "16px 20px 16px 50px",
    border: "2px solid #e0e0e0",
    borderRadius: "12px",
    fontSize: "1rem",
    fontWeight: "500",
    boxSizing: "border-box",
  },
  selectInput: {
    width: "100%",
    padding: "16px 20px 16px 50px",
    border: "2px solid #e0e0e0",
    borderRadius: "12px",
    fontSize: "1rem",
    fontWeight: "500",
    boxSizing: "border-box",
    cursor: "pointer",
  },
  inputIcon: {
    position: "absolute",
    left: "20px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#999",
    fontSize: "18px",
    pointerEvents: "none",
  },
  currentValue: {
    fontSize: "0.85rem",
    color: "#888",
    marginTop: "8px",
    paddingLeft: "50px",
    display: "flex",
    alignItems: "center",
    gap: "5px",
  },
  buttonGroup: {
    display: "flex",
    gap: "20px",
    alignItems: "center",
    flexWrap: "wrap",
  },
  saveButton: {
    padding: "16px 40px",
    background: "linear-gradient(135deg, #cf1322 0%, #ff6b6b 100%)",
    color: "white",
    border: "none",
    borderRadius: "12px",
    cursor: "pointer",
    fontSize: "1rem",
    fontWeight: "600",
    display: "flex",
    alignItems: "center",
    gap: "12px",
    boxShadow: "0 6px 20px rgba(207, 19, 34, 0.3)",
  },
  resetButton: {
    padding: "16px 30px",
    backgroundColor: "transparent",
    color: "#666",
    border: "2px solid #e0e0e0",
    borderRadius: "12px",
    cursor: "pointer",
    fontSize: "1rem",
    fontWeight: "500",
    minWidth: "150px",
  },
  messageContainer: { marginTop: "30px" },
  message: {
    padding: "18px 25px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    gap: "15px",
    fontSize: "1rem",
    fontWeight: "500",
  },
  successMessage: {
    backgroundColor: "rgba(56, 158, 13, 0.1)",
    color: "#389e0d",
    border: "1px solid rgba(56, 158, 13, 0.2)",
  },
  errorMessage: {
    backgroundColor: "rgba(207, 19, 34, 0.1)",
    color: "#cf1322",
    border: "1px solid rgba(207, 19, 34, 0.2)",
  },
  floatingElements: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    pointerEvents: "none",
    zIndex: 1,
    overflow: "hidden",
  },
  floatingElement: {
    position: "absolute",
    opacity: 0.1,
    fontSize: "120px",
    color: "#fff",
  },
};

const API_URL = "http://localhost:5000/api/donors";

const DonorProfile = () => {
  const { user, token, isAuthenticated, updateProfile } = useAuth();
  const [updates, setUpdates] = useState({});
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [hoveredCard, setHoveredCard] = useState(null);
  const [focusedInput, setFocusedInput] = useState(null);

  const [stats, setStats] = useState({
    donations: 0,
    livesSaved: 0,
    lastDonation: "Never",
  });

  useEffect(() => {
    if (user) {
      setStats({
        donations: Math.floor(Math.random() * 10) + 2,
        livesSaved: Math.floor(Math.random() * 20) + 5,
        lastDonation: "3 weeks ago",
      });
    }
  }, [user]);

  const handleChange = (e) => {
    setUpdates({ ...updates, [e.target.name]: e.target.value });
  };

  const handleReset = () => {
    setUpdates({});
    setMessage("");
  };

  const handleUpdate = async () => {
    if (Object.keys(updates).length === 0) {
      setMessage("Please make changes before saving.");
      return;
    }

    setIsSubmitting(true);
    setMessage("");

    try {
      const res = await axios.put(`${API_URL}/profile`, updates, {
        headers: { Authorization: `Bearer ${token}` },
      });

      updateProfile(res.data);
      setUpdates({});
      setMessage("✓ Profile updated successfully!");
      setTimeout(() => setMessage(""), 5000);
    } catch (err) {
      setMessage(
        "✗ " + (err.response?.data?.message || "Error updating profile.")
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isAuthenticated || !user) {
    return (
      <div style={styles.pageWrapper}>
        <div style={styles.backgroundOverlay}></div>
        <div
          style={{
            ...styles.container,
            textAlign: "center",
            padding: "100px 20px",
          }}
        >
          <div style={styles.headerCard}>
            <FiAlertCircle
              style={{
                fontSize: "60px",
                color: "#ff4d4f",
                marginBottom: "20px",
              }}
            />
            <h2 style={{ color: "#1a1a1a" }}>Profile Access Required</h2>
            <p style={{ color: "#666", marginBottom: "30px" }}>
              Please log in to help save lives.
            </p>
          </div>
        </div>
      </div>
    );
  }

  const hasChanges = Object.keys(updates).length > 0;

  return (
    <div style={styles.pageWrapper}>
      <div style={styles.backgroundOverlay}></div>

      {/* Floating Elements */}
      <div style={styles.floatingElements}>
        <FiHeart
          style={{ ...styles.floatingElement, top: "10%", left: "5%" }}
        />
        <FiDroplet
          style={{ ...styles.floatingElement, top: "20%", right: "10%" }}
        />
      </div>

      <div style={styles.container}>
        {/* Hero Header */}
        <div style={styles.headerCard}>
          <div style={styles.heroSection}>
            <div style={styles.heroContent}>
              <h1 style={styles.title}>Donor Profile</h1>
              <p style={styles.subtitle}>
                Welcome back, {user.name}. Your contribution makes a world of
                difference.
              </p>

              <div style={styles.heroStats}>
                <div style={styles.statItem}>
                  <div style={styles.statIcon}>
                    <FiDroplet />
                  </div>
                  <div style={styles.statText}>
                    <span style={styles.statValue}>{stats.donations}</span>
                    <span style={styles.statLabel}>Donations</span>
                  </div>
                </div>
                <div style={styles.statItem}>
                  <div style={styles.statIcon}>
                    <FiHeart />
                  </div>
                  <div style={styles.statText}>
                    <span style={styles.statValue}>{stats.livesSaved}</span>
                    <span style={styles.statLabel}>Lives Saved</span>
                  </div>
                </div>
              </div>
            </div>

            <div style={styles.statusBadge}>
              <FiShield
                size={40}
                color="#cf1322"
                style={{ marginBottom: "15px" }}
              />
              <div
                style={{
                  fontSize: "0.9rem",
                  color: "#666",
                  marginBottom: "10px",
                }}
              >
                Account Type
              </div>
              <div
                style={{
                  backgroundColor:
                    user.role === "admin" ? "#FBBF24" : "#cf1322",
                  color: "white",
                  padding: "10px 24px",
                  borderRadius: "50px",
                  fontWeight: "700",
                }}
              >
                {user.role === "admin" ? "ADMIN" : "DONOR"}
              </div>
              {user.role === "admin" && (
                <Link
                  to="/admin"
                  style={{
                    display: "block",
                    marginTop: "15px",
                    color: "#3b82f6",
                    textDecoration: "none",
                    fontWeight: "bold",
                  }}
                >
                  Go to Admin Panel →
                </Link>
              )}
            </div>
          </div>
        </div>

        {/* Info Grid */}
        <div style={styles.profileGrid}>
          <div style={styles.profileCard}>
            <div style={styles.cardHeader}>
              <div style={styles.cardIcon}>
                <FiUser />
              </div>
              <h3 style={styles.cardTitle}>Identity</h3>
            </div>
            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>
                <FiUser /> Name
              </div>
              <div style={styles.infoValue}>{user.name}</div>
            </div>
            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>
                <FiMail /> Email
              </div>
              <div style={styles.infoValue}>{user.email}</div>
            </div>
          </div>

          <div style={styles.profileCard}>
            <div style={styles.cardHeader}>
              <div style={styles.cardIcon}>
                <FiDroplet />
              </div>
              <h3 style={styles.cardTitle}>Medical</h3>
            </div>
            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>Blood Group</div>
              <div style={styles.bloodGroupBadge}>{user.bloodGroup}</div>
            </div>
            <div style={styles.infoRow}>
              <div style={styles.infoLabel}>Verified</div>
              <div style={{ color: user.isVerified ? "#389e0d" : "#999" }}>
                {user.isVerified ? "Verified ✅" : "Pending"}
              </div>
            </div>
          </div>
        </div>

        {/* Update Form */}
        <div style={styles.updateCard}>
          <div style={styles.updateHeader}>
            <div style={styles.updateIcon}>
              <FiEdit2 />
            </div>
            <div>
              <h3 style={styles.updateTitle}>Settings</h3>
              <p style={styles.updateSubtitle}>
                Update your contact information
              </p>
            </div>
          </div>

          <div style={styles.inputGrid}>
            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>City</label>
              <div style={styles.inputWrapper}>
                <FiMapPin style={styles.inputIcon} />
                <input
                  type="text"
                  name="city"
                  style={styles.input}
                  value={updates.city || ""}
                  placeholder={user.city}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>Phone</label>
              <div style={styles.inputWrapper}>
                <FiPhone style={styles.inputIcon} />
                <input
                  type="tel"
                  name="phone"
                  style={styles.input}
                  value={updates.phone || ""}
                  placeholder={user.phone}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div style={styles.inputGroup}>
              <label style={styles.inputLabel}>Blood Group</label>
              <div style={styles.inputWrapper}>
                <FiDroplet style={styles.inputIcon} />
                <select
                  name="bloodGroup"
                  style={styles.selectInput}
                  value={updates.bloodGroup || ""}
                  onChange={handleChange}
                >
                  <option value="">Select Group</option>
                  {BLOOD_GROUPS.map((g) => (
                    <option key={g} value={g}>
                      {g}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <div style={styles.buttonGroup}>
            <button
              onClick={handleUpdate}
              disabled={isSubmitting || !hasChanges}
              style={styles.saveButton}
            >
              {isSubmitting ? <FiRefreshCw className="spin" /> : <FiSave />}{" "}
              Save Changes
            </button>
            {hasChanges && (
              <button onClick={handleReset} style={styles.resetButton}>
                Discard
              </button>
            )}
          </div>

          {message && (
            <div style={styles.messageContainer}>
              <div
                style={{
                  ...styles.message,
                  ...(message.includes("✓")
                    ? styles.successMessage
                    : styles.errorMessage),
                }}
              >
                {message}
              </div>
            </div>
          )}
        </div>
      </div>

      <style jsx>{`
        @keyframes spin {
          from {
            transform: rotate(0deg);
          }
          to {
            transform: rotate(360deg);
          }
        }
        .spin {
          animation: spin 1s linear infinite;
        }
      `}</style>
    </div>
  );
};

export default DonorProfile;
