// src/services/donorService.js (FULL CODE - Added searchDonors)

import axios from "axios";

const API_URL = "http://localhost:5000/api/donors";

// Register a new donor
export const registerDonor = async (donorData) => {
  try {
    const response = await axios.post(`${API_URL}/register`, donorData);
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : error;
  }
};

// Login donor
export const loginDonor = async (loginData) => {
  try {
    const response = await axios.post(`${API_URL}/login`, loginData);
    return response.data;
  } catch (error) {
    throw error.response ? error.response.data : error;
  }
};

// Get donor profile (requires token)
export const getDonorProfile = async (token) => {
  try {
    const response = await axios.get(`${API_URL}/profile`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    throw error;
  }
};

// =======================================================
// New Feature: Search for available donors
// =======================================================
/**
 * Searches the API for available donors based on query parameters.
 * @param {Object} params - Query parameters { bloodGroup, city }
 * @returns {Promise<Array>} List of matching donors.
 */
export const searchDonors = async (params) => {
  try {
    // Axios automatically handles serialization of the params object into a URL query string
    const response = await axios.get(`${API_URL}/search`, { params });
    return response.data;
  } catch (error) {
    // Throw error for component handling, including 404 (No donors found)
    throw error.response ? error.response.data : error;
  }
};
