// src/pages/Home.js (ULTIMATE DESIGN - Maximum Responsiveness/Flexibility)

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// Placeholder image for a medical theme. In a real project, this path would point to your local assets.
const HOSPITAL_IMAGE_URL = "https://picsum.photos/id/400/1800/800?blur=5"; // Blurred medical environment

// Modern Color Palette (Calm & Trustworthy - Green/Gray)
const colors = {
  primary: "#10B981", // Emerald Green (Accent/Action)
  secondary: "#047857", // Forest Green (Deep Green)
  dark: "#1F2937", // Deep Charcoal Gray (Button/Text)
  lightBg: "#F0FDF4", // Very light Mint/Green for main background
  white: "#FFFFFF",
  darkText: "#111827",
  grayText: "#6B7280",
};

// --- SIMPLIFIED SVG ICONS ---
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);
const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);
const ChevronRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

// --- HELPER FOR RESPONSIVENESS (Simulating Media Queries) ---
const getResponsiveStyles = (viewportWidth) => {
  const isMobile = viewportWidth < 768;

  return {
    statsGrid: {
      flexDirection: isMobile ? "column" : "row",
      alignItems: isMobile ? "center" : "stretch",
    },
    statCard: {
      width: isMobile ? "100%" : "auto",
      maxWidth: isMobile ? "400px" : "none",
    },
    heroWrapper: {
      padding: isMobile ? "60px 15px" : "100px 20px",
      // Optimization: Disable 'fixed' attachment on mobile for better performance
      backgroundAttachment: isMobile ? "scroll" : "fixed",
    },
    ctaButton: {
      // Ensure buttons take full width on small screens to prevent crowding
      width: viewportWidth < 480 ? "100%" : "auto",
      maxWidth: viewportWidth < 480 ? "300px" : "none",
    },
  };
};

// --- STYLES ---
const styles = {
  container: {
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: colors.lightBg,
    paddingBottom: "80px",
  },

  // Hero Section Wrapper
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
    backgroundImage: `url(${HOSPITAL_IMAGE_URL})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    // NOTE: backgroundAttachment: "fixed" is now handled dynamically in getResponsiveStyles
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
    position: "relative",
    overflow: "hidden",
  },

  // Overlay to darken the image and make the text readable
  heroOverlay: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background:
      "linear-gradient(135deg, rgba(31, 41, 55, 0.8) 0%, rgba(16, 185, 129, 0.6) 100%)",
    zIndex: 5,
  },

  heroContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    textAlign: "center",
    position: "relative",
    zIndex: 10,
  },

  heroTitle: {
    fontSize: "clamp(2.5rem, 5vw, 4rem)",
    fontWeight: 800,
    color: colors.white,
    marginBottom: "20px",
    lineHeight: 1.1,
    textShadow: "0 2px 4px rgba(0,0,0,0.5)",
  },
  heroSubtitle: {
    fontSize: "clamp(1.1rem, 2vw, 1.4rem)",
    color: "rgba(255, 255, 255, 0.95)",
    marginBottom: "40px",
    lineHeight: 1.6,
    maxWidth: "800px",
    margin: "0 auto 40px",
    textShadow: "0 1px 3px rgba(0,0,0,0.5)",
  },

  // CTA Buttons
  ctaContainer: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "20px",
  },
  ctaButton: {
    padding: "16px 30px",
    borderRadius: "50px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.3s",
    display: "inline-flex",
    alignItems: "center",
    gap: "10px",
    border: "2px solid transparent",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
    minWidth: "220px",
    justifyContent: "center",
  },
  primaryButton: {
    backgroundColor: colors.dark,
    color: colors.white,
    border: `2px solid ${colors.dark}`,
  },
  secondaryButton: {
    backgroundColor: colors.white,
    color: colors.primary,
    border: `2px solid ${colors.white}`,
  },

  // Stats Section
  statsSection: {
    maxWidth: "1200px",
    margin: "-60px auto 40px",
    padding: "0 20px",
    position: "relative",
    zIndex: 10,
  },
  statsGrid: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    gap: "20px",
  },
  statCard: {
    flex: "1 1 280px",
    backgroundColor: colors.white,
    borderRadius: "16px",
    padding: "30px",
    boxShadow: "0 20px 40px rgba(0, 0, 0, 0.1)",
    transition: "all 0.3s ease",
    textAlign: "center",
  },
  statCardHover: {
    transform: "translateY(-10px)",
    boxShadow: "0 30px 60px rgba(0, 0, 0, 0.25)",
  },
  statNumber: {
    fontSize: "3rem",
    fontWeight: 800,
    color: colors.primary,
    lineHeight: 1,
    marginBottom: "10px",
  },
  statLabel: {
    fontSize: "1.05rem",
    color: colors.grayText,
    fontWeight: 500,
  },

  // Final CTA Banner
  finalCta: {
    maxWidth: "1000px",
    margin: "80px auto 40px",
    padding: "40px 30px",
    background: colors.white,
    borderRadius: "16px",
    textAlign: "center",
    boxShadow: "0 10px 20px rgba(0, 0, 0, 0.05)",
  },
  finalCtaTitle: {
    fontSize: "2rem",
    color: colors.darkText,
    marginBottom: "15px",
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = useState(null);
  const [viewportWidth, setViewportWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => setViewportWidth(window.innerWidth);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const responsiveStyles = getResponsiveStyles(viewportWidth);

  const handleHover = (e, isHovered, isPrimary) => {
    const target = e.currentTarget;
    target.style.transform = isHovered ? "translateY(-3px)" : "translateY(0)";
    target.style.boxShadow = isHovered
      ? "0 8px 20px rgba(0, 0, 0, 0.4)"
      : "0 4px 15px rgba(0, 0, 0, 0.2)";

    if (isPrimary) {
      target.style.backgroundColor = isHovered ? colors.primary : colors.dark;
    } else {
      target.style.backgroundColor = isHovered ? colors.primary : colors.white;
      target.style.color = isHovered ? colors.white : colors.primary;
    }
  };

  // Helper to merge default button style with calculated responsive styles
  const getCtaButtonStyle = (isPrimary) => ({
    ...styles.ctaButton,
    ...(isPrimary ? styles.primaryButton : styles.secondaryButton),
    ...responsiveStyles.ctaButton,
  });

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <div
        style={{
          ...styles.heroWrapper,
          ...responsiveStyles.heroWrapper,
          // Merge dynamic backgroundAttachment
          backgroundAttachment:
            responsiveStyles.heroWrapper.backgroundAttachment,
        }}
      >
        {/* Background Image Overlay for text readability */}
        <div style={styles.heroOverlay} />

        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>Your Immediate Link to Life-Savers.</h1>
          <p style={styles.heroSubtitle}>
            A responsive, secure network connecting those in urgent need with
            verified, nearby blood donors. Fast, reliable, and crucial support
            when it matters most.
          </p>
          <div style={styles.ctaContainer}>
            <Link
              to="/register"
              style={getCtaButtonStyle(true)}
              onMouseOver={(e) => handleHover(e, true, true)}
              onMouseOut={(e) => handleHover(e, false, true)}
            >
              <DropletIcon size={20} color={colors.white} />
              Register to Donate
            </Link>

            <Link
              to="/search"
              style={getCtaButtonStyle(false)}
              onMouseOver={(e) => handleHover(e, true, false)}
              onMouseOut={(e) => handleHover(e, false, false)}
            >
              <UsersIcon size={20} color={colors.primary} />
              Find Donors Instantly
              <ChevronRightIcon size={20} color={colors.primary} />
            </Link>
          </div>
        </div>
      </div>

      {/* --- STATS SECTION (Responsive Grid) --- */}
      <section style={styles.statsSection}>
        <div style={{ ...styles.statsGrid, ...responsiveStyles.statsGrid }}>
          {[
            { number: "24/7", label: "Emergency Support" },
            { number: "15K+", label: "Verified Donors" },
            { number: "98%", label: "Response Rate" },
          ].map((stat, index) => (
            <div
              key={index}
              style={{
                ...styles.statCard,
                ...responsiveStyles.statCard,
                ...(hoveredStat === index ? styles.statCardHover : {}),
              }}
              onMouseOver={() => setHoveredStat(index)}
              onMouseOut={() => setHoveredStat(null)}
            >
              <div style={styles.statNumber}>{stat.number}</div>
              <div style={styles.statLabel}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- FINAL CTA BANNER --- */}
      <section style={styles.finalCta}>
        <h2 style={styles.finalCtaTitle}>Ready to be a Lifeline?</h2>
        <p style={{ color: colors.grayText, marginBottom: "30px" }}>
          Your profile helps save lives. Login or Register to keep your
          availability up to date.
        </p>
        <Link
          to="/login"
          style={{
            ...styles.ctaButton,
            backgroundColor: colors.secondary,
            color: colors.white,
            boxShadow: `0 4px 15px rgba(4, 120, 87, 0.4)`,
            ...responsiveStyles.ctaButton, // Apply responsive sizing here too
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.backgroundColor = colors.primary;
            e.currentTarget.style.boxShadow = `0 8px 20px rgba(16, 185, 129, 0.4)`;
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.backgroundColor = colors.secondary;
            e.currentTarget.style.boxShadow = `0 4px 15px rgba(4, 120, 87, 0.4)`;
          }}
        >
          Access My Profile
        </Link>
      </section>
    </div>
  );
};

export default Home;
