import React, { useState } from "react";
import { registerDonor } from "../services/donorService";
import { FaUser, FaEnvelope, FaLock, FaCity, FaPhone } from "react-icons/fa";
import { GiBloodDrop } from "react-icons/gi";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    bloodGroup: "",
    city: "",
    phone: "",
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = await registerDonor(formData);
      setMessage(`‚úÖ Registration successful!`);
      localStorage.setItem("token", data.token);
    } catch (err) {
      setMessage(err.message || "‚ùå Registration failed");
    }
  };

  return (
    <div
      style={{
        maxWidth: "450px",
        margin: "50px auto",
        padding: "30px",
        borderRadius: "10px",
        boxShadow: "0 0 20px rgba(0,0,0,0.1)",
        backgroundColor: "#fff0f0",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      }}
    >
      <h2 style={{ textAlign: "center", color: "#d32f2f" }}>
        üè• Blood Donation Registration
      </h2>
      <p style={{ textAlign: "center", color: "#555" }}>
        Join our donor community and save lives!
      </p>
      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        {/* Name */}
        <div style={inputContainerStyle}>
          <FaUser style={iconStyle} />
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={formData.name}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        {/* Email */}
        <div style={inputContainerStyle}>
          <FaEnvelope style={iconStyle} />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        {/* Password */}
        <div style={inputContainerStyle}>
          <FaLock style={iconStyle} />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        {/* Blood Group */}
        <div style={inputContainerStyle}>
          <GiBloodDrop style={iconStyle} />
          <input
            type="text"
            name="bloodGroup"
            placeholder="Blood Group (e.g., A+)"
            value={formData.bloodGroup}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        {/* City */}
        <div style={inputContainerStyle}>
          <FaCity style={iconStyle} />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={formData.city}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        {/* Phone */}
        <div style={inputContainerStyle}>
          <FaPhone style={iconStyle} />
          <input
            type="text"
            name="phone"
            placeholder="Phone Number"
            value={formData.phone}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          style={buttonStyle}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#b71c1c")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#d32f2f")}
        >
          Register
        </button>
      </form>

      {/* Message */}
      {message && (
        <p style={{ marginTop: "20px", textAlign: "center", color: "#333" }}>
          {message}
        </p>
      )}
    </div>
  );
};

// Styles
const inputContainerStyle = {
  display: "flex",
  alignItems: "center",
  marginBottom: "15px",
};

const iconStyle = {
  marginRight: "10px",
  color: "#d32f2f",
};

const inputStyle = {
  flex: 1,
  padding: "10px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  fontSize: "14px",
};

const buttonStyle = {
  width: "100%",
  padding: "12px",
  backgroundColor: "#d32f2f",
  color: "#fff",
  fontSize: "16px",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  transition: "background-color 0.3s",
};

export default Register;
