// src/pages/Home.js (ENHANCED WITH BLOOD DONATION THEME & ANIMATIONS)

import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

// Professional Blood Donation Background Images
const BACKGROUND_IMAGES = {
  hero: "https://images.unsplash.com/photo-1582750433449-648ed127bb54?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80", // Hospital interior
  stats:
    "https://images.unsplash.com/photo-1516549655669-df6654e435de?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80", // Blood donation process
  cta: "https://images.unsplash.com/photo-1579684385127-1ef15d508118?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80", // Hands holding blood bag
  pattern:
    "https://images.unsplash.com/photo-1584036561566-baf8f5f1b144?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80", // Medical pattern
};

// Modern Color Palette (Calm & Trustworthy - Green/Gray)
const colors = {
  primary: "#10B981", // Emerald Green (Life, Health)
  secondary: "#047857", // Forest Green (Trust, Stability)
  dark: "#1F2937", // Deep Charcoal Gray
  lightBg: "#F0FDF4", // Very light Mint/Green
  white: "#FFFFFF",
  darkText: "#111827",
  grayText: "#6B7280",
  accent: "#059669", // Slightly brighter green
};

// Animated SVG Icons with hover effects
const DropletIcon = ({
  size = 24,
  color = "currentColor",
  animated = false,
}) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{
      transition: "all 0.3s ease",
      ...(animated && {
        animation: "pulse 2s infinite",
      }),
    }}
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ transition: "all 0.3s ease" }}
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ transition: "all 0.3s ease" }}
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ transition: "all 0.3s ease" }}
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ transition: "all 0.3s ease" }}
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

const PhoneIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
    strokeLinecap="round"
    strokeLinejoin="round"
    style={{ transition: "all 0.3s ease" }}
  >
    <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z" />
  </svg>
);

// Animation styles
const animations = `
  @keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
  }

  @keyframes slideIn {
    from { transform: translateX(-20px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
  }

  @keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.05); }
    100% { transform: scale(1); }
  }

  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  @keyframes shimmer {
    0% { background-position: -1000px 0; }
    100% { background-position: 1000px 0; }
  }
`;

// Add animations to document
if (typeof document !== "undefined") {
  const styleSheet = document.createElement("style");
  styleSheet.textContent = animations;
  document.head.appendChild(styleSheet);
}

// Styles
const styles = {
  container: {
    fontFamily:
      "'Inter', 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    backgroundColor: colors.lightBg,
    color: colors.darkText,
    overflowX: "hidden",
  },

  // Hero Section with Parallax Effect
  heroSection: {
    minHeight: "100vh",
    backgroundImage: `linear-gradient(rgba(31, 41, 55, 0.85), rgba(4, 120, 87, 0.75)), url(${BACKGROUND_IMAGES.hero})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    padding: "100px 20px",
    position: "relative",
    overflow: "hidden",
  },

  floatingElements: {
    position: "absolute",
    width: "100%",
    height: "100%",
    top: 0,
    left: 0,
    pointerEvents: "none",
  },

  floatingDroplet: {
    position: "absolute",
    width: "40px",
    height: "40px",
    color: colors.primary,
    opacity: 0.3,
    animation: "float 6s ease-in-out infinite",
  },

  heroContent: {
    maxWidth: "1200px",
    width: "100%",
    textAlign: "center",
    position: "relative",
    zIndex: 2,
    animation: "fadeIn 1s ease-out",
  },

  heroTitle: {
    fontSize: "clamp(2.8rem, 6vw, 4.5rem)",
    fontWeight: 800,
    color: colors.white,
    marginBottom: "24px",
    lineHeight: 1.1,
    textShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
    animation: "slideIn 0.8s ease-out 0.2s both",
  },

  heroSubtitle: {
    fontSize: "clamp(1.1rem, 2.5vw, 1.6rem)",
    color: "rgba(255, 255, 255, 0.95)",
    maxWidth: "800px",
    margin: "0 auto 48px",
    lineHeight: 1.6,
    textShadow: "0 2px 8px rgba(0, 0, 0, 0.2)",
    animation: "fadeIn 1s ease-out 0.4s both",
  },

  // CTA Buttons
  ctaContainer: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "24px",
    animation: "fadeIn 1s ease-out 0.6s both",
  },

  ctaButton: {
    padding: "18px 40px",
    borderRadius: "50px",
    fontSize: "1.1rem",
    fontWeight: 600,
    textDecoration: "none",
    display: "inline-flex",
    alignItems: "center",
    gap: "12px",
    border: "2px solid transparent",
    transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
    position: "relative",
    overflow: "hidden",
    minWidth: "240px",
    justifyContent: "center",
    boxShadow: "0 8px 25px rgba(0, 0, 0, 0.2)",
  },

  primaryButton: {
    backgroundColor: colors.dark,
    color: colors.white,
    borderColor: colors.dark,
  },

  secondaryButton: {
    backgroundColor: "rgba(255, 255, 255, 0.95)",
    color: colors.dark,
    borderColor: "rgba(255, 255, 255, 0.3)",
    backdropFilter: "blur(10px)",
  },

  buttonShimmer: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background:
      "linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent)",
    transform: "translateX(-100%)",
    transition: "transform 0.6s",
  },

  // Stats Section with Background Pattern
  statsSection: {
    backgroundImage: `linear-gradient(rgba(240, 253, 244, 0.95), rgba(240, 253, 244, 0.95)), url(${BACKGROUND_IMAGES.pattern})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    padding: "80px 20px",
    marginTop: "-60px",
    position: "relative",
  },

  statsContainer: {
    maxWidth: "1400px",
    margin: "0 auto",
    position: "relative",
    zIndex: 2,
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "30px",
  },

  statCard: {
    backgroundColor: colors.white,
    borderRadius: "20px",
    padding: "50px 30px",
    textAlign: "center",
    boxShadow: "0 20px 40px rgba(16, 185, 129, 0.1)",
    border: `1px solid rgba(16, 185, 129, 0.1)`,
    transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
    position: "relative",
    overflow: "hidden",
    animation: "fadeIn 0.8s ease-out",
  },

  statCardHover: {
    transform: "translateY(-15px) scale(1.02)",
    boxShadow: "0 30px 60px rgba(16, 185, 129, 0.2)",
  },

  statIcon: {
    width: "80px",
    height: "80px",
    backgroundColor: `${colors.primary}15`,
    borderRadius: "50%",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto 25px",
    transition: "all 0.4s ease",
  },

  statNumber: {
    fontSize: "3.5rem",
    fontWeight: 800,
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
    lineHeight: 1,
    marginBottom: "12px",
  },

  statLabel: {
    fontSize: "1.2rem",
    color: colors.grayText,
    fontWeight: 500,
  },

  // Blood Status Cards
  bloodStatusSection: {
    padding: "100px 20px",
    backgroundColor: colors.white,
  },

  sectionTitle: {
    fontSize: "clamp(2.2rem, 4vw, 3.2rem)",
    fontWeight: 800,
    textAlign: "center",
    marginBottom: "20px",
    color: colors.darkText,
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  sectionSubtitle: {
    fontSize: "1.2rem",
    textAlign: "center",
    color: colors.grayText,
    maxWidth: "700px",
    margin: "0 auto 60px",
    lineHeight: 1.6,
  },

  bloodGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "30px",
    maxWidth: "1200px",
    margin: "0 auto 60px",
  },

  bloodCard: {
    backgroundColor: colors.lightBg,
    borderRadius: "20px",
    padding: "40px 30px",
    textAlign: "center",
    border: `2px solid ${colors.primary}30`,
    transition: "all 0.4s ease",
    position: "relative",
    overflow: "hidden",
  },

  bloodCardHover: {
    transform: "translateY(-10px)",
    borderColor: colors.primary,
    boxShadow: `0 20px 40px ${colors.primary}20`,
  },

  bloodType: {
    fontSize: "4rem",
    fontWeight: 800,
    color: colors.darkText,
    marginBottom: "15px",
  },

  bloodStatus: {
    fontSize: "1.1rem",
    fontWeight: 600,
    padding: "10px 24px",
    borderRadius: "25px",
    display: "inline-block",
    backgroundColor: `${colors.primary}20`,
    color: colors.primary,
  },

  // Final CTA with Hands & Blood Bag Background
  finalCta: {
    backgroundImage: `linear-gradient(rgba(16, 185, 129, 0.9), rgba(4, 120, 87, 0.9)), url(${BACKGROUND_IMAGES.cta})`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundAttachment: "fixed",
    padding: "120px 20px",
    textAlign: "center",
    color: colors.white,
  },

  ctaContent: {
    maxWidth: "800px",
    margin: "0 auto",
