// src/App.js (Updated)

import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"; // Link is no longer needed here

// Import Components and Pages
import Header from "./components/Header"; // <-- NEW IMPORT
import Register from "./pages/Register";
import DonorProfile from "./pages/DonorProfile";
import Login from "./pages/Login";

const App = () => {
  return (
    <Router>
      {/* 1. Use the Header component */}
      <Header />

      <div style={{ padding: "20px" }}>
        {/* The main heading is now inside the Header component */}

        <Routes>
          {/* Default/Home Route */}
          <Route
            path="/"
            element={
              <div style={{ textAlign: "center", marginTop: "50px" }}>
                <h2>Welcome to the Blood Donation Network</h2>
                <p>
                  Your action saves lives. Use the links above to Register or
                  Login.
                </p>
              </div>
            }
          />

          <Route path="/register" element={<Register />} />
          <Route path="/profile" element={<DonorProfile />} />
          <Route path="/login" element={<Login />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
