// src/pages/Home.js (MODERN DESIGN - No External Dependencies)

import React, { useState } from "react";
import { Link } from "react-router-dom";

// Modern Color Palette - Sophisticated & Trustworthy (No Red)
const colors = {
  primary: "#3B82F6", // Professional Blue
  secondary: "#8B5CF6", // Elegant Purple
  accent: "#10B981", // Life-affirming Emerald
  dark: "#1F2937", // Deep Charcoal
  light: "#F8FAFC", // Clean Off-white
  gray: "#6B7280", // Medium Gray
  gradientStart: "#6366F1",
  gradientEnd: "#3B82F6",
};

// Simple SVG Icons as React Components
const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ShieldIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
  </svg>
);

const ChevronRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="2"
  >
    <polyline points="9 18 15 12 9 6" />
  </svg>
);

// Modern Design System
const styles = {
  container: {
    fontFamily:
      "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
    color: colors.dark,
    overflowX: "hidden",
  },

  // --- HERO SECTION ---
  heroWrapper: {
    background: `linear-gradient(135deg, ${colors.gradientStart} 0%, ${colors.gradientEnd} 100%)`,
    padding: "80px 0",
    position: "relative",
    minHeight: "70vh",
    display: "flex",
    alignItems: "center",
    overflow: "hidden",
  },

  heroPattern: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundImage: `radial-gradient(circle at 25% 25%, rgba(255,255,255,0.1) 2px, transparent 2px)`,
    backgroundSize: "40px 40px",
    opacity: 0.4,
  },

  heroContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    padding: "0 20px",
    position: "relative",
    zIndex: 10,
  },

  heroTitle: {
    fontSize: "clamp(2.5rem, 5vw, 3.5rem)",
    fontWeight: 800,
    lineHeight: 1.1,
    color: "white",
    marginBottom: "20px",
    background: "linear-gradient(to right, #ffffff, #E0F2FE)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  heroSubtitle: {
    fontSize: "clamp(1rem, 2vw, 1.2rem)",
    color: "rgba(255, 255, 255, 0.9)",
    lineHeight: 1.6,
    marginBottom: "40px",
    maxWidth: "600px",
  },

  // CTA Buttons
  ctaContainer: {
    display: "flex",
    flexWrap: "wrap",
    gap: "15px",
    alignItems: "center",
  },

  ctaButton: {
    padding: "16px 32px",
    borderRadius: "12px",
    fontSize: "1rem",
    fontWeight: 600,
    textDecoration: "none",
    transition: "all 0.3s ease",
    display: "inline-flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "10px",
    border: "none",
    cursor: "pointer",
  },

  primaryButton: {
    background: "white",
    color: colors.primary,
    boxShadow: "0 10px 30px rgba(59, 130, 246, 0.3)",
  },

  secondaryButton: {
    background: "transparent",
    color: "white",
    border: "2px solid rgba(255, 255, 255, 0.3)",
  },

  // --- STATS SECTION ---
  statsSection: {
    maxWidth: "1200px",
    margin: "40px auto",
    padding: "0 20px",
  },

  statsGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "20px",
  },

  statCard: {
    background: "white",
    borderRadius: "16px",
    padding: "30px 20px",
    boxShadow: "0 10px 30px rgba(0, 0, 0, 0.08)",
    transition: "all 0.3s ease",
    border: "1px solid rgba(0, 0, 0, 0.05)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    textAlign: "center",
  },

  statIcon: {
    width: "56px",
    height: "56px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "20px",
  },

  iconBg1: { background: "linear-gradient(135deg, #3B82F6, #8B5CF6)" },
  iconBg2: { background: "linear-gradient(135deg, #10B981, #3B82F6)" },
  iconBg3: { background: "linear-gradient(135deg, #8B5CF6, #10B981)" },

  statNumber: {
    fontSize: "2.5rem",
    fontWeight: 800,
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
    lineHeight: 1,
    marginBottom: "5px",
  },

  statLabel: {
    fontSize: "1rem",
    color: colors.gray,
    fontWeight: 500,
    lineHeight: 1.4,
  },

  // --- FEATURES SECTION ---
  featuresSection: {
    maxWidth: "1200px",
    margin: "80px auto",
    padding: "0 20px",
  },

  sectionHeader: {
    textAlign: "center",
    marginBottom: "50px",
  },

  sectionTitle: {
    fontSize: "clamp(1.8rem, 4vw, 2.5rem)",
    fontWeight: 800,
    color: colors.dark,
    marginBottom: "15px",
    background: `linear-gradient(135deg, ${colors.primary}, ${colors.secondary})`,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    backgroundClip: "text",
  },

  sectionSubtitle: {
    fontSize: "1.1rem",
    color: colors.gray,
    maxWidth: "600px",
    margin: "0 auto",
    lineHeight: 1.6,
  },

  featuresGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "30px",
  },

  featureCard: {
    background: colors.light,
    borderRadius: "16px",
    padding: "40px 30px",
    transition: "all 0.3s ease",
    border: "1px solid rgba(0, 0, 0, 0.05)",
  },

  featureIcon: {
    width: "48px",
    height: "48px",
    borderRadius: "12px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginBottom: "25px",
    background: "white",
    boxShadow: "0 8px 20px rgba(0, 0, 0, 0.05)",
  },

  featureTitle: {
    fontSize: "1.4rem",
    fontWeight: 700,
    color: colors.dark,
    marginBottom: "15px",
  },

  featureDescription: {
    fontSize: "1rem",
    color: colors.gray,
    lineHeight: 1.6,
    marginBottom: "20px",
  },

  featureLink: {
    color: colors.primary,
    textDecoration: "none",
    fontWeight: 600,
    display: "inline-flex",
    alignItems: "center",
    gap: "6px",
    fontSize: "0.95rem",
  },

  // --- CTA BANNER ---
  ctaBanner: {
    maxWidth: "1200px",
    margin: "80px auto",
    padding: "0 20px",
  },

  bannerContent: {
    background: `linear-gradient(135deg, ${colors.accent}, ${colors.primary})`,
    borderRadius: "20px",
    padding: "60px 40px",
    textAlign: "center",
    position: "relative",
    overflow: "hidden",
  },

  bannerTitle: {
    fontSize: "clamp(1.8rem, 4vw, 2.5rem)",
    fontWeight: 800,
    color: "white",
    marginBottom: "20px",
  },

  bannerSubtitle: {
    fontSize: "1.1rem",
    color: "rgba(255, 255, 255, 0.9)",
    maxWidth: "600px",
    margin: "0 auto 40px",
    lineHeight: 1.6,
  },
};

const Home = () => {
  const [hoveredStat, setHoveredStat] = useState(null);

  return (
    <div style={styles.container}>
      {/* --- HERO SECTION --- */}
      <div style={styles.heroWrapper}>
        <div style={styles.heroPattern} />
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            Give Life.
            <br />
            <span style={{ display: "block", fontSize: "0.85em" }}>
              Transform Futures.
            </span>
          </h1>

          <p style={styles.heroSubtitle}>
            Join our community of life-savers. Every donation creates a ripple
            effect of hope, healing, and second chances. Your simple act can
            rewrite someone's story.
          </p>

          <div style={styles.ctaContainer}>
            <Link
              to="/register"
              style={{ ...styles.ctaButton, ...styles.primaryButton }}
              onMouseOver={(e) => {
                e.currentTarget.style.transform = "translateY(-4px)";
                e.currentTarget.style.boxShadow =
                  "0 15px 40px rgba(59, 130, 246, 0.4)";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.boxShadow =
                  "0 10px 30px rgba(59, 130, 246, 0.3)";
              }}
            >
              <DropletIcon size={20} color={colors.primary} />
              Become a Donor
              <ChevronRightIcon size={20} color={colors.primary} />
            </Link>

            <Link
              to="/search"
              style={{ ...styles.ctaButton, ...styles.secondaryButton }}
              onMouseOver={(e) => {
                e.currentTarget.style.transform = "translateY(-4px)";
                e.currentTarget.style.boxShadow =
                  "0 15px 40px rgba(255, 255, 255, 0.2)";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.boxShadow = "none";
              }}
            >
              <UsersIcon size={20} color="white" />
              Find Donors
              <ChevronRightIcon size={20} color="white" />
            </Link>
          </div>
        </div>
      </div>

      {/* --- STATS SECTION --- */}
      <section style={styles.statsSection}>
        <div style={styles.statsGrid}>
          {[
            {
              number: "50K+",
              label: "Active Donors",
              icon: <UsersIcon size={24} color="white" />,
              iconBg: styles.iconBg1,
            },
            {
              number: "24/7",
              label: "Emergency Support",
              icon: <HeartIcon size={24} color="white" />,
              iconBg: styles.iconBg2,
            },
            {
              number: "98%",
              label: "Satisfaction Rate",
              icon: <ShieldIcon size={24} color="white" />,
              iconBg: styles.iconBg3,
            },
          ].map((stat, index) => (
            <div
              key={index}
              style={
                hoveredStat === index
                  ? {
                      ...styles.statCard,
                      transform: "translateY(-8px)",
                      boxShadow: "0 20px 50px rgba(0, 0, 0, 0.15)",
                    }
                  : styles.statCard
              }
              onMouseOver={() => setHoveredStat(index)}
              onMouseOut={() => setHoveredStat(null)}
            >
              <div style={{ ...styles.statIcon, ...stat.iconBg }}>
                {stat.icon}
              </div>
              <div style={styles.statNumber}>{stat.number}</div>
              <div style={styles.statLabel}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- FEATURES SECTION --- */}
      <section style={styles.featuresSection}>
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Why Choose Us?</h2>
          <p style={styles.sectionSubtitle}>
            We've reimagined blood donation for the modern worldâ€”making it
            simpler, smarter, and more impactful than ever before.
          </p>
        </div>

        <div style={styles.featuresGrid}>
          {[
            {
              title: "Smart Matching",
              description:
                "Our intelligent algorithm matches donors with recipients based on location, blood type, and urgency.",
              icon: <HeartIcon size={24} color={colors.primary} />,
              link: "/how-it-works",
            },
            {
              title: "Digital Health Tracker",
              description:
                "Monitor your donation history, health metrics, and impact over time with our digital dashboard.",
              icon: <ShieldIcon size={24} color={colors.primary} />,
              link: "/dashboard",
            },
            {
              title: "Community Support",
              description:
                "Join local donor communities, participate in events, and connect with fellow life-savers.",
              icon: <UsersIcon size={24} color={colors.primary} />,
              link: "/community",
            },
          ].map((feature, index) => (
            <div
              key={index}
              style={styles.featureCard}
              onMouseOver={(e) => {
                e.currentTarget.style.transform = "translateY(-6px)";
                e.currentTarget.style.boxShadow =
                  "0 20px 40px rgba(0, 0, 0, 0.1)";
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.boxShadow = "none";
              }}
            >
              <div style={styles.featureIcon}>{feature.icon}</div>
              <h3 style={styles.featureTitle}>{feature.title}</h3>
              <p style={styles.featureDescription}>{feature.description}</p>
              <Link to={feature.link} style={styles.featureLink}>
                Learn more <ChevronRightIcon size={16} color={colors.primary} />
              </Link>
            </div>
          ))}
        </div>
      </section>

      {/* --- FINAL CTA BANNER --- */}
      <section style={styles.ctaBanner}>
        <div style={styles.bannerContent}>
          <h2 style={styles.bannerTitle}>Ready to Make a Difference?</h2>
          <p style={styles.bannerSubtitle}>
            Join thousands of donors who are transforming lives every day. Your
            journey as a life-saver starts with a single click.
          </p>
          <Link
            to="/register"
            style={{
              ...styles.ctaButton,
              ...styles.primaryButton,
              background: "white",
              color: colors.primary,
              fontSize: "1.1rem",
              padding: "18px 40px",
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.transform = "translateY(-6px) scale(1.05)";
              e.currentTarget.style.boxShadow =
                "0 20px 50px rgba(255, 255, 255, 0.3)";
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.transform = "translateY(0) scale(1)";
              e.currentTarget.style.boxShadow =
                "0 10px 30px rgba(255, 255, 255, 0.2)";
            }}
          >
            <DropletIcon size={24} color={colors.primary} />
            Start Your Journey
            <ChevronRightIcon size={24} color={colors.primary} />
          </Link>
        </div>
      </section>
    </div>
  );
};

export default Home;
