import React, { useState } from "react";
import { registerDonor } from "../services/donorService";
import { FaUser, FaEnvelope, FaLock, FaCity, FaPhone } from "react-icons/fa";
import { GiBloodDrop } from "react-icons/gi";

const Register = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    bloodGroup: "",
    city: "",
    phone: "",
  });

  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "success" or "error"
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    const { name, email, password, bloodGroup, city, phone } = formData;
    const bloodRegex = /^(A|B|AB|O)[+-]$/i;
    const phoneRegex = /^[0-9]{10,15}$/;

    if (!bloodRegex.test(bloodGroup)) {
      setMessageType("error");
      setMessage("‚ùå Please enter a valid blood group (e.g., A+).");
      return false;
    }

    if (!phoneRegex.test(phone)) {
      setMessageType("error");
      setMessage("‚ùå Please enter a valid phone number.");
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    try {
      const data = await registerDonor(formData);
      setMessageType("success");
      setMessage("‚úÖ Registration successful!");
      localStorage.setItem("token", data.token);
    } catch (err) {
      setMessageType("error");
      setMessage(err.message || "‚ùå Registration failed");
    }
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>üè• Blood Donation Registration</h2>
      <p style={subtitleStyle}>Join our donor community and save lives!</p>

      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        <InputField
          icon={<FaUser style={iconStyle} />}
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
        />
        <InputField
          icon={<FaEnvelope style={iconStyle} />}
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
        />
        <InputField
          icon={<FaLock style={iconStyle} />}
          type={showPassword ? "text" : "password"}
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          togglePassword={() => setShowPassword(!showPassword)}
          showPassword={showPassword}
        />
        <InputField
          icon={<GiBloodDrop style={iconStyle} />}
          type="text"
          name="bloodGroup"
          placeholder="Blood Group (e.g., A+)"
          value={formData.bloodGroup}
          onChange={handleChange}
        />
        <InputField
          icon={<FaCity style={iconStyle} />}
          type="text"
          name="city"
          placeholder="City"
          value={formData.city}
          onChange={handleChange}
        />
        <InputField
          icon={<FaPhone style={iconStyle} />}
          type="text"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
        />

        <button
          type="submit"
          style={buttonStyle}
          onMouseOver={(e) => (e.target.style.backgroundColor = "#b71c1c")}
          onMouseOut={(e) => (e.target.style.backgroundColor = "#d32f2f")}
        >
          Register
        </button>
      </form>

      {message && (
        <p
          style={{
            marginTop: "20px",
            textAlign: "center",
            color: messageType === "success" ? "green" : "red",
          }}
        >
          {message}
        </p>
      )}
    </div>
  );
};

// Input Field Component
const InputField = ({
  icon,
  type,
  name,
  placeholder,
  value,
  onChange,
  togglePassword,
  showPassword,
}) => (
  <div style={inputContainerStyle}>
    {icon}
    <input
      type={type}
      name={name}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      required
      style={inputStyle}
    />
    {togglePassword && (
      <span
        onClick={togglePassword}
        style={{ cursor: "pointer", marginLeft: "5px" }}
      >
        {showPassword ? "üëÅÔ∏è" : "üôà"}
      </span>
    )}
  </div>
);

// Styles
const containerStyle = {
  maxWidth: "450px",
  margin: "50px auto",
  padding: "30px",
  borderRadius: "10px",
  boxShadow: "0 0 20px rgba(0,0,0,0.1)",
  backgroundColor: "#fff0f0",
  fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
};

const titleStyle = { textAlign: "center", color: "#d32f2f" };
const subtitleStyle = { textAlign: "center", color: "#555" };

const inputContainerStyle = {
  display: "flex",
  alignItems: "center",
  marginBottom: "15px",
};

const iconStyle = {
  marginRight: "10px",
  color: "#d32f2f",
};

const inputStyle = {
  flex: 1,
  padding: "10px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  fontSize: "14px",
};

const buttonStyle = {
  width: "100%",
  padding: "12px",
  backgroundColor: "#d32f2f",
  color: "#fff",
  fontSize: "16px",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
  transition: "background-color 0.3s",
};

export default Register;
