    gap: 16px;
    min-width: 0;
  }

  .donor-card__avatar {
    width: 48px;
    height: 48px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 1.25rem;
    flex-shrink: 0;
  }

  .donor-card__info {
    flex: 1;
    min-width: 0;
  }

  .donor-card__name {
    margin: 0 0 4px 0;
    font-size: 1.125rem;
    font-weight: 600;
    color: #1a1a1a;
    word-break: break-word;
  }

  .donor-card__email {
    margin: 0 0 4px 0;
    color: #666;
    font-size: 0.875rem;
    word-break: break-all;
  }

  .donor-card__content {
    padding: 20px;
    flex: 1;
    min-width: 0;
  }

  .donor-card__details {
    display: grid;
    gap: 16px;
  }

  .donor-card__footer {
    padding: 20px;
    border-top: 1px solid #f0f0f0;
    min-width: 0;
  }

  .donor-card__actions {
    display: flex;
    gap: 12px;
    flex-wrap: wrap;
  }

  /* Action Button */
  .action-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    padding: 12px 24px;
    border: none;
    border-radius: 8px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.875rem;
    white-space: nowrap;
    flex-shrink: 0;
  }

  .action-button--full {
    width: 100%;
  }

  .action-button--small {
    padding: 8px 16px;
    font-size: 0.75rem;
    flex: 1;
    min-width: 100px;
  }

  /* Empty State */
  .empty-state {
    text-align: center;
    padding: 60px 20px;
    width: 100%;
    box-sizing: border-box;
  }

  /* Mobile Optimizations */
  @media (max-width: 639px) {
    .admin-container {
      padding: 16px;
    }

    .admin-header,
    .controls-section,
    .admin-main {
      padding: 20px;
    }

    .admin-header__actions {
      flex-direction: column;
    }

    .action-button {
      width: 100%;
      justify-content: center;
    }

    .controls-section__row {
      gap: 16px;
    }

    .filters-chips {
      justify-content: center;
    }

    .filter-chip {
      flex: 1;
      justify-content: center;
      min-width: 80px;
    }

    .sort-group {
      flex-direction: column;
    }

    .sort-select,
    .sort-order {
      width: 100%;
    }

    .donor-card__actions {
      flex-direction: column;
    }

    .action-button--small {
      min-width: 0;
    }
  }

  /* Tablet Optimizations */
  @media (min-width: 640px) and (max-width: 1023px) {
    .action-button--small {
      min-width: 120px;
    }
  }

  /* Fix for very small screens */
  @media (max-width: 374px) {
    .controls-section {
      padding: 16px;
    }

    .search-input__field {
      padding: 12px 40px 12px 40px;
      font-size: 14px;
    }

    .filter-chip {
      padding: 6px 12px;
      font-size: 12px;
    }

    .stat-card {
      padding: 16px;
    }

    .stat-card__value {
      font-size: 1.75rem;
    }
  }

  /* Prevent horizontal scroll */
  body {
    overflow-x: hidden;
  }

  * {
    max-width: 100%;
  }

  /* Loading Skeleton responsive */
  .loading-skeleton {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
    width: 100%;
  }

  @media (min-width: 640px) {
    .loading-skeleton {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .loading-skeleton {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  /* Access Denied */
  .access-denied {
    background: white;
    border-radius: 16px;
    padding: 48px;
    text-align: center;
    max-width: 400px;
    width: 100%;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
    box-sizing: border-box;
  }

  @media (max-width: 639px) {
    .access-denied {
      padding: 32px 24px;
    }
  }

  /* Animations */
  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }
`;

// Inject styles
const styleSheet = document.createElement("style");
styleSheet.textContent = styles;
document.head.appendChild(styleSheet);
export default AdminDashboard;
