import React, { useState, useEffect, useCallback, useMemo } from "react";
import { useAuth } from "../context/AuthContext";
import {
  getAllDonorsAdmin,
  deleteDonorAdmin,
  toggleVerifyDonorAdmin,
} from "../services/donorService";

// Updated Search Input Component with better responsiveness
const SearchInput = ({ value, onChange, placeholder }) => {
  const [isFocused, setIsFocused] = useState(false);

  return (
    <div className={`search-input ${isFocused ? "search-input--focused" : ""}`}>
      <div className="search-input__wrapper">
        <span className="search-input__icon">🔍</span>
        <input
          type="text"
          value={value}
          onChange={(e) => onChange(e.target.value)}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          placeholder={placeholder}
          className="search-input__field"
          aria-label="Search donors"
        />
        {value && (
          <button
            className="search-input__clear"
            onClick={() => onChange("")}
            aria-label="Clear search"
            type="button"
          >
            <svg width="14" height="14" viewBox="0 0 14 14" fill="none">
              <path
                d="M13 1L1 13M1 1L13 13"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
              />
            </svg>
          </button>
        )}
      </div>
    </div>
  );
};

// Updated Controls Section Component
const ControlsSection = ({
  searchTerm,
  setSearchTerm,
  filter,
  setFilter,
  sortBy,
  setSortBy,
  sortOrder,
  setSortOrder,
  stats,
}) => (
  <section className="controls-section">
    <div className="controls-section__row">
      <div className="controls-section__search-wrapper">
        <SearchInput
          value={searchTerm}
          onChange={setSearchTerm}
          placeholder="Search donors by name, email, or location..."
        />
      </div>
    </div>

    <div className="controls-section__row">
      <div className="controls-section__filters-wrapper">
        <div className="filters-group">
          <div className="filters-label-wrapper">
            <span className="filters-label">Filter:</span>
          </div>
          <div className="filters-chips">
            <button
              className={`filter-chip ${
                filter === "all" ? "filter-chip--active" : ""
              }`}
              onClick={() => setFilter("all")}
              type="button"
            >
              <span className="filter-chip__label">All</span>
              <span className="filter-chip__count">{stats.total}</span>
            </button>
            <button
              className={`filter-chip ${
                filter === "verified" ? "filter-chip--active" : ""
              }`}
              onClick={() => setFilter("verified")}
              type="button"
            >
              <span className="filter-chip__label">Verified</span>
              <span className="filter-chip__count">{stats.verified}</span>
            </button>
            <button
              className={`filter-chip ${
                filter === "pending" ? "filter-chip--active" : ""
              }`}
              onClick={() => setFilter("pending")}
              type="button"
            >
              <span className="filter-chip__label">Pending</span>
              <span className="filter-chip__count">{stats.pending}</span>
            </button>
          </div>
        </div>
      </div>

      <div className="controls-section__sort-wrapper">
        <div className="sort-group">
          <select
            className="sort-select"
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            aria-label="Sort by"
          >
            <option value="name">Sort by Name</option>
            <option value="bloodGroup">Sort by Blood Group</option>
            <option value="city">Sort by City</option>
            <option value="createdAt">Sort by Date</option>
          </select>
          <button
            className="sort-order"
            onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}
            aria-label={`Sort ${
              sortOrder === "asc" ? "descending" : "ascending"
            }`}
            type="button"
          >
            {sortOrder === "asc" ? "↑" : "↓"}
          </button>
        </div>
      </div>
    </div>
  </section>
);

// Rest of the components remain the same as before...
const StatCard = ({ title, value, icon, color, trend }) => (
  <div className="stat-card">
    <div className="stat-card__header">
      <div className={`stat-card__icon ${color}`}>{icon}</div>
      <span className="stat-card__title">{title}</span>
    </div>
    <div className="stat-card__value">{value}</div>
    {trend && <div className="stat-card__trend">{trend}</div>}
  </div>
);

const StatusBadge = ({ status, type = "donor" }) => {
  const statusConfig = {
    verified: { label: "Verified", color: "success", icon: "✓" },
    pending: { label: "Pending", color: "warning", icon: "⏳" },
    blocked: { label: "Blocked", color: "danger", icon: "⛔" },
    active: { label: "Active", color: "info", icon: "●" },
  };

  const config = statusConfig[status] || statusConfig.pending;

  return (
    <span className={`status-badge status-badge--${config.color}`}>
      <span className="status-badge__icon">{config.icon}</span>
      <span className="status-badge__label">{config.label}</span>
    </span>
  );
};

const ActionButton = ({
  children,
  variant = "primary",
  size = "medium",
  icon,
  onClick,
  disabled = false,
  fullWidth = false,
}) => (
  <button
    className={`
      action-button 
      action-button--${variant} 
      action-button--${size}
      ${fullWidth ? "action-button--full" : ""}
      ${disabled ? "action-button--disabled" : ""}
    `}
    onClick={onClick}
    disabled={disabled}
    type="button"
  >
    {icon && <span className="action-button__icon">{icon}</span>}
    <span className="action-button__label">{children}</span>
  </button>
);

const DonorCard = ({ donor, onVerify, onDelete }) => {
  const [showActions, setShowActions] = useState(false);

  return (
    <div className="donor-card">
      <div className="donor-card__header">
        <div className="donor-card__avatar">
          {donor.name.charAt(0).toUpperCase()}
        </div>
        <div className="donor-card__info">
          <h3 className="donor-card__name">{donor.name}</h3>
          <p className="donor-card__email">{donor.email}</p>
          {donor.phone && <p className="donor-card__phone">📞 {donor.phone}</p>}
        </div>
        <button
          className="donor-card__menu"
          onClick={() => setShowActions(!showActions)}
          aria-label="More options"
          type="button"
        >
          ⋮
        </button>
      </div>

      <div className="donor-card__content">
        <div className="donor-card__details">
          <div className="donor-card__detail">
            <span className="donor-card__detail-label">Blood Group</span>
            <span className="donor-card__blood-group">{donor.bloodGroup}</span>
          </div>
          <div className="donor-card__detail">
            <span className="donor-card__detail-label">Location</span>
            <span className="donor-card__location">
              {donor.city}
              {donor.state ? `, ${donor.state}` : ""}
            </span>
          </div>
          <div className="donor-card__detail">
            <span className="donor-card__detail-label">Status</span>
            <StatusBadge status={donor.isVerified ? "verified" : "pending"} />
          </div>
        </div>
      </div>

      <div className="donor-card__footer">
        <div className="donor-card__actions">
          <ActionButton
            variant={donor.isVerified ? "secondary" : "success"}
            size="small"
            icon={donor.isVerified ? "↶" : "✓"}
            onClick={() => onVerify(donor._id, donor.isVerified, donor.name)}
          >
            {donor.isVerified ? "Revoke" : "Verify"}
          </ActionButton>
          {donor.role !== "admin" && (
            <ActionButton
              variant="danger"
              size="small"
              icon="🗑️"
              onClick={() => onDelete(donor._id, donor.name)}
            >
              Delete
            </ActionButton>
          )}
        </div>
      </div>
    </div>
  );
};

const LoadingSkeleton = () => (
  <div className="loading-skeleton">
    {[...Array(3)].map((_, index) => (
      <div key={index} className="donor-card skeleton">
        <div className="skeleton__header">
          <div className="skeleton__avatar"></div>
          <div className="skeleton__info">
            <div className="skeleton__line skeleton__line--title"></div>
            <div className="skeleton__line skeleton__line--subtitle"></div>
          </div>
        </div>
        <div className="skeleton__content">
          <div className="skeleton__detail">
            <div className="skeleton__line skeleton__line--small"></div>
            <div className="skeleton__line skeleton__line--medium"></div>
          </div>
          <div className="skeleton__detail">
            <div className="skeleton__line skeleton__line--small"></div>
            <div className="skeleton__line skeleton__line--medium"></div>
          </div>
        </div>
        <div className="skeleton__footer">
          <div className="skeleton__button"></div>
          <div className="skeleton__button"></div>
        </div>
      </div>
    ))}
  </div>
);

const AdminDashboard = () => {
  const { user, token } = useAuth();
  const [donors, setDonors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filter, setFilter] = useState("all");
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");

  // Load donors on component mount
  useEffect(() => {
    if (token && user?.role === "admin") {
      fetchData();
    }
  }, [token, user]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getAllDonorsAdmin(token);
      setDonors(data);
    } catch (err) {
      console.error("Admin Fetch Error:", err);
      setError(err.message || "Failed to fetch donors list. Please try again.");
    } finally {
      setLoading(false);
    }
  }, [token]);

  const handleDelete = async (id, name) => {
    if (
      window.confirm(
        `Are you sure you want to delete ${name}'s account? This action cannot be undone.`
      )
    ) {
      try {
        await deleteDonorAdmin(id, token);
        setDonors((prev) => prev.filter((d) => d._id !== id));
      } catch (err) {
        alert(`Deletion failed: ${err.message || "Please try again."}`);
      }
    }
  };

  const handleToggleVerify = async (id, currentStatus, name) => {
    const action = currentStatus ? "revoke verification from" : "verify";
    if (window.confirm(`Are you sure you want to ${action} ${name}?`)) {
      try {
        await toggleVerifyDonorAdmin(id, !currentStatus, token);
        setDonors((prev) =>
          prev.map((d) =>
            d._id === id ? { ...d, isVerified: !currentStatus } : d
          )
        );
      } catch (err) {
        alert(`Update failed: ${err.message || "Please try again."}`);
      }
    }
  };

  // Filter and sort donors
  const filteredDonors = useMemo(() => {
    let result = donors.filter((donor) => {
      if (filter === "verified") return donor.isVerified;
      if (filter === "pending") return !donor.isVerified;
      return true;
    });

    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      result = result.filter(
        (donor) =>
          donor.name.toLowerCase().includes(term) ||
          donor.email.toLowerCase().includes(term) ||
          donor.city.toLowerCase().includes(term)
      );
    }

    return [...result].sort((a, b) => {
      let aValue = a[sortBy];
      let bValue = b[sortBy];

      if (sortBy === "name" || sortBy === "city") {
        aValue = (aValue || "").toLowerCase();
        bValue = (bValue || "").toLowerCase();
      }

      if (sortOrder === "asc") {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });
  }, [donors, filter, searchTerm, sortBy, sortOrder]);

  // Statistics
  const stats = useMemo(
    () => ({
      total: donors.length,
      verified: donors.filter((d) => d.isVerified).length,
      pending: donors.filter((d) => !d.isVerified).length,
      uniqueCities: new Set(donors.map((d) => d.city)).size,
    }),
    [donors]
  );

  // Security check
  if (user?.role !== "admin") {
    return (
      <div className="admin-container admin-container--centered">
        <div className="access-denied">
          <div className="access-denied__icon">🚫</div>
          <h1 className="access-denied__title">Access Denied</h1>
          <p className="access-denied__message">
            Administrator privileges required to access this dashboard. Please
            contact your system administrator.
          </p>
          <ActionButton
            variant="primary"
            onClick={() => (window.location.href = "/")}
            fullWidth
          >
            Return to Home
          </ActionButton>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <div className="admin-container">
        {/* Header */}
        <header className="admin-header">
          <div className="admin-header__main">
            <div>
              <h1 className="admin-header__title">Admin Dashboard</h1>
              <p className="admin-header__subtitle">Donor Management System</p>
            </div>
            <div className="admin-header__actions">
              <ActionButton
                variant="secondary"
                icon="↻"
                onClick={fetchData}
                disabled={loading}
              >
                Refresh
              </ActionButton>
              <ActionButton
                variant="primary"
                icon="📊"
                onClick={() => console.log("Export data")}
              >
                Export Data
              </ActionButton>
            </div>
          </div>

          {/* Stats Overview */}
          <div className="stats-grid">
            <StatCard
              title="Total Donors"
              value={stats.total}
              icon="👥"
              color="primary"
            />
            <StatCard
              title="Verified"
              value={stats.verified}
              icon="✅"
              color="success"
            />
            <StatCard
              title="Pending Review"
              value={stats.pending}
              icon="⏳"
              color="warning"
            />
            <StatCard
              title="Cities"
              value={stats.uniqueCities}
              icon="📍"
              color="info"
            />
          </div>
        </header>

        {/* Controls Section - Now fully responsive */}
        <ControlsSection
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
          filter={filter}
          setFilter={setFilter}
          sortBy={sortBy}
          setSortBy={setSortBy}
          sortOrder={sortOrder}
          setSortOrder={setSortOrder}
          stats={stats}
        />

        {/* Main Content */}
        <main className="admin-main">
          {error && (
            <div className="error-alert">
              <div className="error-alert__icon">⚠️</div>
              <div className="error-alert__content">
                <strong>Error Loading Data:</strong> {error}
                <button
                  className="error-alert__retry"
                  onClick={fetchData}
                  type="button"
                >
                  Retry
                </button>
              </div>
            </div>
          )}

          {loading ? (
            <LoadingSkeleton />
          ) : (
            <>
              <div className="results-header">
                <h2 className="results-title">
                  Donor Management
                  <span className="results-count">
                    ({filteredDonors.length})
                  </span>
                </h2>
                <p className="results-description">
                  {searchTerm
                    ? `Search results for "${searchTerm}"`
                    : "All registered donors"}
                </p>
              </div>

              {filteredDonors.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-state__icon">📭</div>
                  <h3 className="empty-state__title">No donors found</h3>
                  <p className="empty-state__message">
                    {searchTerm
                      ? "No donors match your search criteria. Try a different search term."
                      : filter !== "all"
                      ? "No donors match the selected filter."
                      : "No donor accounts have been registered yet."}
                  </p>
                  {(searchTerm || filter !== "all") && (
                    <ActionButton
                      variant="secondary"
                      onClick={() => {
                        setSearchTerm("");
                        setFilter("all");
                      }}
                    >
                      Clear Filters
                    </ActionButton>
                  )}
                </div>
              ) : (
                <div className="donors-grid">
                  {filteredDonors.map((donor) => (
                    <DonorCard
                      key={donor._id}
                      donor={donor}
                      onVerify={handleToggleVerify}
                      onDelete={handleDelete}
                    />
                  ))}
                </div>
              )}
            </>
          )}
        </main>

        {/* Footer */}
        <footer className="admin-footer">
          <p className="admin-footer__text">
            © {new Date().getFullYear()} Blood Donation System. Admin Dashboard
            v1.0
          </p>
          <p className="admin-footer__status">
            Last updated:{" "}
            {new Date().toLocaleTimeString([], {
              hour: "2-digit",
              minute: "2-digit",
            })}
          </p>
        </footer>
      </div>
    </div>
  );
};

// Enhanced CSS with fixed responsive issues
const styles = `
  /* Admin Dashboard Styles - FIXED RESPONSIVENESS */
  .admin-dashboard {
    min-height: 100vh;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
  }

  .admin-container {
    max-width: 1400px;
    margin: 0 auto;
    padding: 24px;
    width: 100%;
    box-sizing: border-box;
  }

  .admin-container--centered {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    padding: 20px;
  }

  /* Header */
  .admin-header {
    background: white;
    border-radius: 16px;
    padding: 32px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    overflow: hidden;
  }

  .admin-header__main {
    display: flex;
    flex-direction: column;
    gap: 24px;
    margin-bottom: 32px;
    width: 100%;
  }

  @media (min-width: 768px) {
    .admin-header__main {
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
    }
  }

  .admin-header__title {
    font-size: clamp(1.5rem, 4vw, 2rem);
    font-weight: 700;
    color: #1a1a1a;
    margin: 0 0 8px 0;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    word-break: break-word;
  }

  .admin-header__subtitle {
    color: #666;
    font-size: clamp(0.875rem, 2vw, 1rem);
    margin: 0;
  }

  .admin-header__actions {
    display: flex;
    gap: 12px;
    flex-wrap: wrap;
    width: 100%;
  }

  @media (min-width: 768px) {
    .admin-header__actions {
      width: auto;
      flex-wrap: nowrap;
    }
  }

  /* Stats Grid */
  .stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(min(240px, 100%), 1fr));
    gap: 20px;
    width: 100%;
  }

  .stat-card {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    padding: 24px;
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    min-width: 0;
    overflow: hidden;
  }

  /* FIXED: Controls Section - COMPLETELY RESPONSIVE */
  .controls-section {
    background: white;
    border-radius: 16px;
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    width: 100%;
    box-sizing: border-box;
  }

  .controls-section__row {
    display: flex;
    flex-direction: column;
    gap: 20px;
    width: 100%;
  }

  @media (min-width: 768px) {
    .controls-section__row {
      flex-direction: row;
      align-items: center;
    }
  }

  .controls-section__search-wrapper {
    flex: 1;
    min-width: 0;
    width: 100%;
  }

  .controls-section__filters-wrapper {
    flex: 1;
    min-width: 0;
    width: 100%;
  }

  .controls-section__sort-wrapper {
    width: 100%;
  }

  @media (min-width: 768px) {
    .controls-section__sort-wrapper {
      width: auto;
      min-width: 200px;
    }
  }

  /* FIXED: Search Input - Now perfectly responsive */
  .search-input {
    width: 100%;
    position: relative;
  }

  .search-input__wrapper {
    position: relative;
    width: 100%;
    display: flex;
    align-items: center;
  }

  .search-input__icon {
    position: absolute;
    left: 16px;
    top: 50%;
    transform: translateY(-50%);
    color: #999;
    z-index: 1;
    pointer-events: none;
  }

  .search-input__field {
    width: 100%;
    padding: 16px 48px 16px 48px;
    border: 2px solid #e0e0e0;
    border-radius: 12px;
    font-size: clamp(0.875rem, 2vw, 1rem);
    transition: all 0.3s ease;
    box-sizing: border-box;
    -webkit-appearance: none;
    appearance: none;
    background: white;
  }

  .search-input__field:focus {
    outline: none;
    border-color: #667eea;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
  }

  .search-input__clear {
    position: absolute;
    right: 16px;
    top: 50%;
    transform: translateY(-50%);
    background: none;
    border: none;
    color: #999;
    cursor: pointer;
    padding: 4px;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 24px;
    height: 24px;
    z-index: 1;
  }

  .search-input__clear:hover {
    color: #333;
  }

  .search-input__clear svg {
    width: 14px;
    height: 14px;
  }

  /* Filters */
  .filters-group {
    display: flex;
    flex-direction: column;
    gap: 12px;
    width: 100%;
  }

  @media (min-width: 640px) {
    .filters-group {
      flex-direction: row;
      align-items: center;
    }
  }

  .filters-label-wrapper {
    flex-shrink: 0;
  }

  .filters-label {
    font-weight: 600;
    color: #333;
    font-size: 0.875rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    white-space: nowrap;
  }

  .filters-chips {
    display: flex;
    gap: 8px;
    flex-wrap: wrap;
    flex: 1;
  }

  .filter-chip {
    padding: 8px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 20px;
    background: white;
    color: #666;
    font-size: 0.875rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    display: inline-flex;
    align-items: center;
    gap: 6px;
    white-space: nowrap;
    flex-shrink: 0;
  }

  .filter-chip:hover {
    border-color: #667eea;
    color: #667eea;
  }

  .filter-chip--active {
    background: #667eea;
    border-color: #667eea;
    color: white;
  }

  .filter-chip__count {
    background: rgba(255, 255, 255, 0.2);
    padding: 2px 8px;
    border-radius: 10px;
    font-size: 0.75rem;
  }

  /* Sort Controls */
  .sort-group {
    display: flex;
    gap: 8px;
    width: 100%;
  }

  .sort-select {
    flex: 1;
    padding: 10px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    background: white;
    color: #333;
    font-size: 0.875rem;
    cursor: pointer;
    min-width: 0;
    box-sizing: border-box;
  }

  .sort-order {
    padding: 10px 16px;
    border: 2px solid #e0e0e0;
    border-radius: 8px;
    background: white;
    color: #333;
    cursor: pointer;
    font-weight: bold;
    flex-shrink: 0;
    width: 48px;
  }

  /* Main Content */
  .admin-main {
    background: white;
    border-radius: 16px;
    padding: 24px;
    margin-bottom: 24px;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    width: 100%;
    box-sizing: border-box;
  }

  .error-alert {
    background: linear-gradient(135deg, #feb2b2 0%, #fed7d7 100%);
    border-left: 4px solid #e53e3e;
    padding: 16px;
    border-radius: 8px;
    margin-bottom: 24px;
    display: flex;
    align-items: flex-start;
    gap: 12px;
    width: 100%;
    box-sizing: border-box;
  }

  .results-header {
    margin-bottom: 32px;
    width: 100%;
  }

  .results-title {
    font-size: clamp(1.25rem, 3vw, 1.5rem);
    font-weight: 600;
    color: #1a1a1a;
    margin: 0 0 8px 0;
    display: flex;
    align-items: center;
    gap: 12px;
    flex-wrap: wrap;
  }

  .results-count {
    background: #667eea;
    color: white;
    padding: 4px 12px;
    border-radius: 20px;
    font-size: 0.875rem;
    font-weight: 500;
    flex-shrink: 0;
  }

  /* Donor Cards Grid - RESPONSIVE */
  .donors-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
    width: 100%;
  }

  @media (min-width: 640px) {
    .donors-grid {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .donors-grid {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  @media (min-width: 1400px) {
    .donors-grid {
      grid-template-columns: repeat(4, 1fr);
    }
  }

  /* Donor Card */
  .donor-card {
    background: white;
    border-radius: 12px;
    border: 1px solid #e0e0e0;
    overflow: hidden;
    transition: all 0.3s ease;
    display: flex;
    flex-direction: column;
    min-width: 0;
    width: 100%;
  }

  .donor-card__header {
    padding: 20px;
    border-bottom: 1px solid #f0f0f0;
    display: flex;
    align-items: flex-start;
    gap: 16px;
    min-width: 0;
  }

  .donor-card__avatar {
    width: 48px;
    height: 48px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 1.25rem;
    flex-shrink: 0;
  }

  .donor-card__info {
    flex: 1;
    min-width: 0;
  }

  .donor-card__name {
    margin: 0 0 4px 0;
    font-size: 1.125rem;
    font-weight: 600;
    color: #1a1a1a;
    word-break: break-word;
  }

  .donor-card__email {
    margin: 0 0 4px 0;
    color: #666;
    font-size: 0.875rem;
    word-break: break-all;
  }

  .donor-card__content {
    padding: 20px;
    flex: 1;
    min-width: 0;
  }

  .donor-card__details {
    display: grid;
    gap: 16px;
  }

  .donor-card__footer {
    padding: 20px;
    border-top: 1px solid #f0f0f0;
    min-width: 0;
  }

  .donor-card__actions {
    display: flex;
    gap: 12px;
    flex-wrap: wrap;
  }

  /* Action Button */
  .action-button {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 8px;
    padding: 12px 24px;
    border: none;
    border-radius: 8px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.875rem;
    white-space: nowrap;
    flex-shrink: 0;
  }

  .action-button--full {
    width: 100%;
  }

  .action-button--small {
    padding: 8px 16px;
    font-size: 0.75rem;
    flex: 1;
    min-width: 100px;
  }

  /* Empty State */
  .empty-state {
    text-align: center;
    padding: 60px 20px;
    width: 100%;
    box-sizing: border-box;
  }

  /* Mobile Optimizations */
  @media (max-width: 639px) {
    .admin-container {
      padding: 16px;
    }

    .admin-header,
    .controls-section,
    .admin-main {
      padding: 20px;
    }

    .admin-header__actions {
      flex-direction: column;
    }

    .action-button {
      width: 100%;
      justify-content: center;
    }

    .controls-section__row {
      gap: 16px;
    }

    .filters-chips {
      justify-content: center;
    }

    .filter-chip {
      flex: 1;
      justify-content: center;
      min-width: 80px;
    }

    .sort-group {
      flex-direction: column;
    }

    .sort-select,
    .sort-order {
      width: 100%;
    }

    .donor-card__actions {
      flex-direction: column;
    }

    .action-button--small {
      min-width: 0;
    }
  }

  /* Tablet Optimizations */
  @media (min-width: 640px) and (max-width: 1023px) {
    .action-button--small {
      min-width: 120px;
    }
  }

  /* Fix for very small screens */
  @media (max-width: 374px) {
    .controls-section {
      padding: 16px;
    }

    .search-input__field {
      padding: 12px 40px 12px 40px;
      font-size: 14px;
    }

    .filter-chip {
      padding: 6px 12px;
      font-size: 12px;
    }

    .stat-card {
      padding: 16px;
    }

    .stat-card__value {
      font-size: 1.75rem;
    }
  }

  /* Prevent horizontal scroll */
  body {
    overflow-x: hidden;
  }

  * {
    max-width: 100%;
  }

  /* Loading Skeleton responsive */
  .loading-skeleton {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
    width: 100%;
  }

  @media (min-width: 640px) {
    .loading-skeleton {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .loading-skeleton {
      grid-template-columns: repeat(3, 1fr);
    }
  }

  /* Access Denied */
  .access-denied {
    background: white;
    border-radius: 16px;
    padding: 48px;
    text-align: center;
    max-width: 400px;
    width: 100%;
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
    box-sizing: border-box;
  }

  @media (max-width: 639px) {
    .access-denied {
      padding: 32px 24px;
    }
  }

  /* Animations */
  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
  }
`;

