// src/pages/Home.js (EMOTIONAL VIDEO BACKGROUND DESIGN)

import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";

// Warm, Emotional Color Palette (No Red/Blue)
const colors = {
  primary: "#FF8A65", // Warm Coral (Life, Warmth)
  secondary: "#4DB6AC", // Teal (Health, Freshness)
  accent: "#FFB74D", // Warm Orange (Hope, Energy)
  lightBg: "#FFF8F6", // Very light warm background
  darkText: "#4A4A4A", // Warm dark gray
  lightText: "#8A8A8A", // Light gray
  success: "#81C784", // Soft green
  gold: "#FFD54F", // Warm gold
  purple: "#BA68C8", // Soft purple for variety
};

// Soft, Emotional Icons
const HeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M20.84 4.61a5.5 5.5 0 00-7.78 0L12 5.67l-1.06-1.06a5.5 5.5 0 00-7.78 7.78l1.06 1.06L12 21.23l7.78-7.78 1.06-1.06a5.5 5.5 0 000-7.78z" />
  </svg>
);

const HandHeartIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M12 5.94a14.08 14.08 0 00-3 9 14.08 14.08 0 003 9 14.08 14.08 0 003-9 14.08 14.08 0 00-3-9z" />
    <path d="M2 12h1M21 12h1M12 2v1M12 21v1" />
  </svg>
);

const DropletIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M12 2.69l5.66 5.66a8 8 0 11-11.31 0z" />
  </svg>
);

const UsersIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M17 21v-2a4 4 0 00-4-4H5a4 4 0 00-4 4v2" />
    <circle cx="9" cy="7" r="4" />
    <path d="M23 21v-2a4 4 0 00-3-3.87" />
    <path d="M16 3.13a4 4 0 010 7.75" />
  </svg>
);

const ArrowRightIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M5 12h14M12 5l7 7-7 7" />
  </svg>
);

const PhoneIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z" />
  </svg>
);

const CheckCircleIcon = ({ size = 24, color = "currentColor" }) => (
  <svg
    width={size}
    height={size}
    viewBox="0 0 24 24"
    fill="none"
    stroke={color}
    strokeWidth="1.5"
  >
    <path d="M22 11.08V12a10 10 0 11-5.93-9.14" />
    <path d="M22 4L12 14.01l-3-3" />
  </svg>
);

const styles = {
  container: {
    fontFamily: "'Poppins', 'Playfair Display', serif",
    color: colors.darkText,
    overflowX: "hidden",
  },

  // Video Hero Section
  videoHero: {
    position: "relative",
    height: "100vh",
    overflow: "hidden",
  },

  backgroundVideo: {
    position: "absolute",
    top: "50%",
    left: "50%",
    minWidth: "100%",
    minHeight: "100%",
    width: "auto",
    height: "auto",
    transform: "translate(-50%, -50%)",
    zIndex: 1,
    objectFit: "cover",
  },

  videoOverlay: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: "linear-gradient(to bottom, rgba(0,0,0,0.3), rgba(0,0,0,0.6))",
    zIndex: 2,
  },

  heroContent: {
    position: "relative",
    zIndex: 3,
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
    padding: "0 20px",
    maxWidth: "900px",
    margin: "0 auto",
  },

  heroTitle: {
    fontSize: "clamp(2.8rem, 7vw, 5rem)",
    fontWeight: "700",
    color: colors.white,
    marginBottom: "30px",
    lineHeight: "1.1",
    textShadow: "0 2px 10px rgba(0,0,0,0.3)",
    fontFamily: "'Playfair Display', serif",
  },

  heroSubtitle: {
    fontSize: "clamp(1.2rem, 3vw, 1.8rem)",
    color: "rgba(255,255,255,0.9)",
    marginBottom: "50px",
    lineHeight: "1.6",
    maxWidth: "700px",
  },

  // Floating Cards Section
  floatingCards: {
    position: "relative",
    marginTop: "-100px",
    padding: "0 20px",
    zIndex: 4,
  },

  cardsContainer: {
    maxWidth: "1400px",
    margin: "0 auto",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
    gap: "30px",
  },

  floatingCard: {
    backgroundColor: colors.white,
    borderRadius: "20px",
    padding: "40px 30px",
    boxShadow: "0 20px 60px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
    transition: "all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275)",
    transform: "translateY(0)",
  },

  // Emotional Story Section
  storySection: {
    backgroundImage: `linear-gradient(rgba(255, 255, 255, 0.95), rgba(255, 255, 255, 0.95)), url('https://images.unsplash.com/photo-1519494026892-80bbd2d6fd0d?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80')`,
    backgroundSize: "cover",
    backgroundAttachment: "fixed",
    backgroundPosition: "center",
    padding: "150px 20px",
    marginTop: "100px",
    clipPath: "polygon(0 5%, 100% 0, 100% 95%, 0 100%)",
