// src/pages/Home.js (FULL CODE - Redesigned Homepage)

import React from "react";
import { Link } from "react-router-dom";

// --- Homepage Styles ---
const styles = {
  container: {
    maxWidth: "1000px",
    margin: "50px auto",
    padding: "40px 30px",
    textAlign: "center",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
  },
  heroSection: {
    backgroundColor: "#fff3e0", // Light, warm background
    padding: "60px 30px",
    borderRadius: "15px",
    boxShadow: "0 8px 25px rgba(0, 0, 0, 0.1)",
    marginBottom: "40px",
  },
  heroTitle: {
    fontSize: "3em",
    color: "#d32f2f", // Primary Red
    marginBottom: "15px",
    fontWeight: 700,
    letterSpacing: "1px",
  },
  heroSubtitle: {
    fontSize: "1.4em",
    color: "#546e7a",
    marginBottom: "30px",
    lineHeight: 1.5,
  },
  ctaContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "30px",
  },
  ctaButton: {
    padding: "15px 30px",
    borderRadius: "8px",
    fontSize: "1.1em",
    fontWeight: "bold",
    textDecoration: "none",
    transition: "background-color 0.3s, transform 0.2s",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    minWidth: "200px",
  },
  donateButton: {
    backgroundColor: "#d32f2f", // Red
    color: "white",
  },
  findButton: {
    backgroundColor: "#00796b", // Teal/Green
    color: "white",
  },
  statsSection: {
    display: "flex",
    justifyContent: "space-around",
    marginTop: "60px",
    padding: "30px 0",
    borderTop: "1px solid #e0e0e0",
  },
  statItem: {
    flex: 1,
  },
  statNumber: {
    fontSize: "2.5em",
    color: "#00796b",
    fontWeight: 600,
  },
  statLabel: {
    color: "#546e7a",
    fontSize: "1em",
    marginTop: "5px",
  },
};

const Home = () => {
  return (
    <div style={styles.container}>
      <section style={styles.heroSection}>
        <h1 style={styles.heroTitle}>Every Drop Counts ðŸ©¸</h1>
        <p style={styles.heroSubtitle}>
          Join the BloodConnect Network. Register to donate or find the nearest
          life-saver in your city instantly.
        </p>
        <div style={styles.ctaContainer}>
          <Link
            to="/register"
            style={{ ...styles.ctaButton, ...styles.donateButton }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#c62828")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#d32f2f")}
          >
            Register to Donate
          </Link>
          <Link
            to="/search"
            style={{ ...styles.ctaButton, ...styles.findButton }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#00695c")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#00796b")}
          >
            Find a Donor
          </Link>
        </div>
      </section>

      <section style={styles.statsSection}>
        <div style={styles.statItem}>
          <div style={styles.statNumber}>1.5M</div>
          <div style={styles.statLabel}>Lives Touched Annually</div>
        </div>
        <div style={styles.statItem}>
          <div style={styles.statNumber}>80%</div>
          <div style={styles.statLabel}>Response Rate (Within 1 Hour)</div>
        </div>
        <div style={styles.statItem}>
          <div style={styles.statNumber}>10k+</div>
          <div style={styles.statLabel}>Active Donors Registered</div>
        </div>
      </section>
    </div>
  );
};

export default Home;
