import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const colors = {
  primary: "#10B981",
  secondary: "#047857",
  dark: "#1F2937",
  white: "#FFFFFF",
  darkText: "#111827",
  accent: "#FBBF24", // Gold color for Admin link
};

const styles = {
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "15px 30px",
    backgroundColor: colors.dark,
    color: colors.white,
    boxShadow: "0 2px 10px rgba(0,0,0,0.3)",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
  },
  logo: {
    margin: 0,
    fontSize: "1.8em",
    fontWeight: 600,
    letterSpacing: "1px",
    color: colors.white,
    textDecoration: "none",
  },
  logoAccent: {
    color: colors.primary,
  },
  navLinks: {
    display: "flex",
    gap: "20px",
    alignItems: "center",
  },
  navLink: {
    color: colors.white,
    textDecoration: "none",
    fontSize: "1em",
    padding: "5px 10px",
    borderRadius: "4px",
    transition: "background-color 0.2s",
  },
  adminLink: {
    color: colors.accent, // Gold color to stand out
    textDecoration: "none",
    fontSize: "1em",
    fontWeight: "bold",
    padding: "5px 10px",
    borderRadius: "4px",
    border: `1px solid ${colors.accent}`,
  },
  navLinkHover: {
    backgroundColor: colors.secondary,
  },
  logoutButton: {
    backgroundColor: colors.primary,
    color: colors.darkText,
    border: "none",
    padding: "8px 15px",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "0.9em",
    fontWeight: "bold",
    transition: "background-color 0.2s",
  },
  logoutButtonHover: {
    backgroundColor: colors.secondary,
    color: colors.white,
  },
};

const Header = () => {
  const { isAuthenticated, user, logout } = useAuth(); // Added 'user' from context

  const handleLogout = () => {
    logout();
  };

  const linkProps = (to, customStyle = styles.navLink) => ({
    to: to,
    style: customStyle,
    onMouseOver: (e) =>
      (e.target.style.backgroundColor = styles.navLinkHover.backgroundColor),
    onMouseOut: (e) => (e.target.style.backgroundColor = "transparent"),
  });

  const logoutButtonProps = {
    onClick: handleLogout,
    style: styles.logoutButton,
    onMouseOver: (e) => {
      e.target.style.backgroundColor = styles.logoutButtonHover.backgroundColor;
      e.target.style.color = styles.logoutButtonHover.color;
    },
    onMouseOut: (e) => {
      e.target.style.backgroundColor = styles.logoutButton.backgroundColor;
      e.target.style.color = styles.logoutButton.color;
    },
  };

  return (
    <header style={styles.header}>
      <Link to="/" style={styles.logo}>
        BloodConnect <span style={styles.logoAccent}>ü©∏</span>
      </Link>
      <nav style={styles.navLinks}>
        {/* Public Search Link */}
        <Link {...linkProps("/search")}>Find Donor</Link>

        {isAuthenticated ? (
          <>
            {/* Show Admin Panel only if user role is admin */}
            {user?.role === "admin" && (
              <Link {...linkProps("/admin", styles.adminLink)}>
                Admin Panel ‚≠ê
              </Link>
            )}

            <Link {...linkProps("/profile")}>Profile</Link>
            <button {...logoutButtonProps}>Logout</button>
          </>
        ) : (
          <>
            <Link {...linkProps("/register")}>Register</Link>
            <Link {...linkProps("/login")}>Login</Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
