// src/components/Footer.js (FULL CODE)

import React from "react";
import { Link } from "react-router-dom";

// --- Footer Component Styles ---
const styles = {
  footer: {
    backgroundColor: "#263238", // Dark gray/Blue (Professional, Muted)
    color: "#b0bec5", // Light gray text
    padding: "40px 30px",
    marginTop: "60px", // Space between content and footer
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    borderTop: "5px solid #d32f2f", // Red accent line
  },
  footerContent: {
    maxWidth: "1200px",
    margin: "0 auto",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
    flexWrap: "wrap",
    gap: "30px",
  },
  section: {
    flex: 1,
    minWidth: "200px",
    marginBottom: "20px",
  },
  logo: {
    fontSize: "1.8em",
    fontWeight: 600,
    color: "white",
    marginBottom: "10px",
    textDecoration: "none",
  },
  sectionTitle: {
    fontSize: "1.2em",
    color: "#ffffff",
    marginBottom: "15px",
    borderBottom: "1px solid #37474f",
    paddingBottom: "5px",
  },
  linkList: {
    listStyle: "none",
    padding: 0,
    margin: 0,
  },
  linkItem: {
    marginBottom: "8px",
  },
  link: {
    color: "#b0bec5",
    textDecoration: "none",
    fontSize: "0.95em",
    transition: "color 0.2s",
  },
  linkHover: {
    color: "#d32f2f", // Primary Red on hover
  },
  copyright: {
    textAlign: "center",
    paddingTop: "20px",
    marginTop: "30px",
    borderTop: "1px solid #37474f",
    fontSize: "0.9em",
    color: "#78909c",
  },
};

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <div style={styles.footerContent}>
        {/* 1. Logo and Mission */}
        <div style={styles.section}>
          <Link to="/" style={styles.logo}>
            BloodConnect ðŸ©¸
          </Link>
          <p style={{ marginTop: "10px" }}>
            Connecting donors and recipients instantly to save lives across the
            nation. Your voluntary action makes a difference.
          </p>
        </div>

        {/* 2. Quick Links */}
        <div style={styles.section}>
          <h4 style={styles.sectionTitle}>Quick Links</h4>
          <ul style={styles.linkList}>
            <li style={styles.linkItem}>
              <Link
                to="/search"
                style={styles.link}
                onMouseOver={(e) =>
                  (e.target.style.color = styles.linkHover.color)
                }
                onMouseOut={(e) => (e.target.style.color = styles.link.color)}
              >
                Find Donor
              </Link>
            </li>
            <li style={styles.linkItem}>
              <Link
                to="/register"
                style={styles.link}
                onMouseOver={(e) =>
                  (e.target.style.color = styles.linkHover.color)
                }
                onMouseOut={(e) => (e.target.style.color = styles.link.color)}
              >
                Register to Donate
              </Link>
            </li>
            <li style={styles.linkItem}>
              <Link
                to="/profile"
                style={styles.link}
                onMouseOver={(e) =>
                  (e.target.style.color = styles.linkHover.color)
                }
                onMouseOut={(e) => (e.target.style.color = styles.link.color)}
              >
                My Profile
              </Link>
            </li>
          </ul>
        </div>

        {/* 3. Contact Info */}
        <div style={styles.section}>
          <h4 style={styles.sectionTitle}>Contact & Support</h4>
          <p>Email: support@bloodconnect.org</p>
          <p>Phone: +1 (555) 123-4567</p>
          <p style={{ marginTop: "15px" }}>Location: , Bharatpur</p>
        </div>
      </div>
      <div style={styles.copyright}>
        &copy; {new Date().getFullYear()} BloodConnect Network. All rights
        reserved.
      </div>
    </footer>
  );
};

export default Footer;
