// src/pages/Login.js (FULL CODE - Refactored with FormInput)

import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { loginDonor } from "../services/donorService";
import { useAuth } from "../context/AuthContext";
import FormInput from "../components/FormInput"; // <-- NEW IMPORT

// --- Login Page Styles ---
const styles = {
  container: {
    maxWidth: "400px",
    margin: "80px auto 40px",
    padding: "30px",
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
    fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
    borderTop: "5px solid #00796b",
    textAlign: "center",
  },
  heading: {
    color: "#d32f2f",
    marginBottom: "25px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  button: {
    padding: "12px 20px",
    backgroundColor: "#d32f2f",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background-color 0.3s",
    textTransform: "uppercase",
    letterSpacing: "1px",
    marginTop: "10px",
  },
  registerLink: {
    marginTop: "20px",
    fontSize: "0.9em",
  },
  linkStyle: {
    color: "#00796b",
    textDecoration: "none",
    fontWeight: "bold",
  },
  message: {
    marginTop: "20px",
    padding: "10px",
    borderRadius: "6px",
    backgroundColor: "#fff3e0",
    color: "#ff9800",
  },
};

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Logging in...");
    setIsSubmitting(true);

    try {
      const { token, donor } = await loginDonor(formData);

      login(token, donor);

      setMessage("Login successful! Redirecting to profile...");

      setTimeout(() => {
        navigate("/profile");
      }, 1000);
    } catch (err) {
      const errorMsg =
        err.message || err.error || "Login failed. Check your credentials.";
      setMessage(errorMsg);
      console.error("Login Error:", err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Donor Login ðŸ”‘</h2>

      {message && <p style={styles.message}>{message}</p>}

      <form style={styles.form} onSubmit={handleSubmit}>
        <FormInput
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <FormInput
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit" style={styles.button} disabled={isSubmitting}>
          {isSubmitting ? "Logging In..." : "Log In"}
        </button>
      </form>

      <p style={styles.registerLink}>
        New Donor?{" "}
        <Link to="/register" style={styles.linkStyle}>
          Register Here
        </Link>
      </p>
    </div>
  );
};

export default Login;
