// src/pages/DonorProfile.js
import React, { useState } from "react";
import axios from "axios";
import { useAuth } from "../context/AuthContext";
import {
  FiUser,
  FiMail,
  FiDroplet,
  FiMapPin,
  FiPhone,
  FiSave,
  FiRefreshCw,
  FiCheckCircle,
  FiAlertCircle,
} from "react-icons/fi";

// --- Modern Professional Styles ---
const styles = {
  container: {
    maxWidth: "900px",
    margin: "40px auto",
    padding: "40px",
    backgroundColor: "#ffffff",
    borderRadius: "20px",
    boxShadow: "0 10px 40px rgba(0, 0, 0, 0.08)",
    fontFamily:
      "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif",
    border: "1px solid #f0f0f0",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "40px",
    paddingBottom: "25px",
    borderBottom: "1px solid #f0f0f0",
  },
  headerContent: {
    flex: 1,
  },
  title: {
    fontSize: "28px",
    fontWeight: "700",
    color: "#1a1a1a",
    marginBottom: "8px",
    display: "flex",
    alignItems: "center",
    gap: "12px",
  },
  subtitle: {
    fontSize: "14px",
    color: "#666",
    fontWeight: "400",
    display: "flex",
    alignItems: "center",
    gap: "8px",
  },
  statusBadge: {
    backgroundColor: "#e6f7ff",
    color: "#1890ff",
    padding: "6px 16px",
    borderRadius: "20px",
    fontSize: "12px",
    fontWeight: "600",
    display: "flex",
    alignItems: "center",
    gap: "6px",
  },
  profileSection: {
    backgroundColor: "#fafafa",
    borderRadius: "16px",
    padding: "30px",
    marginBottom: "30px",
  },
  sectionTitle: {
    fontSize: "18px",
    fontWeight: "600",
    color: "#1a1a1a",
    marginBottom: "25px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  infoGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
    gap: "20px",
  },
  infoCard: {
    backgroundColor: "white",
    padding: "20px",
    borderRadius: "12px",
    border: "1px solid #f0f0f0",
    transition: "transform 0.2s ease, box-shadow 0.2s ease",
    cursor: "pointer",
  },
  infoCardHover: {
    transform: "translateY(-2px)",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
  },
  infoLabel: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    fontSize: "14px",
    color: "#666",
    marginBottom: "8px",
    fontWeight: "500",
  },
  infoValue: {
    fontSize: "16px",
    color: "#1a1a1a",
    fontWeight: "500",
    marginLeft: "24px",
  },
  bloodGroupDisplay: {
    display: "inline-flex",
    alignItems: "center",
    gap: "8px",
    backgroundColor: "#fff2f0",
    color: "#cf1322",
    padding: "8px 20px",
    borderRadius: "25px",
    fontWeight: "700",
    fontSize: "16px",
    border: "2px solid #ffccc7",
  },
  updateSection: {
    backgroundColor: "#f6ffed",
    border: "1px solid #b7eb8f",
    borderRadius: "16px",
    padding: "30px",
    marginTop: "40px",
  },
  updateHeader: {
    display: "flex",
    alignItems: "center",
    gap: "12px",
    marginBottom: "25px",
  },
  updateTitle: {
    fontSize: "20px",
    fontWeight: "600",
    color: "#389e0d",
  },
  updateDescription: {
    fontSize: "14px",
    color: "#8c8c8c",
    marginBottom: "25px",
  },
  inputGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "20px",
    marginBottom: "30px",
  },
  inputGroup: {
    display: "flex",
    flexDirection: "column",
    gap: "8px",
  },
  inputLabel: {
    fontSize: "14px",
    color: "#595959",
    fontWeight: "500",
    display: "flex",
    alignItems: "center",
    gap: "8px",
  },
  input: {
    padding: "14px 16px",
    border: "2px solid #e8e8e8",
    borderRadius: "10px",
    fontSize: "15px",
    fontWeight: "500",
    transition: "all 0.3s ease",
    backgroundColor: "white",
  },
  inputFocus: {
    borderColor: "#1890ff",
    boxShadow: "0 0 0 3px rgba(24, 144, 255, 0.1)",
  },
  buttonGroup: {
    display: "flex",
    gap: "15px",
    alignItems: "center",
  },
  saveButton: {
    padding: "14px 32px",
    backgroundColor: "#1890ff",
    color: "white",
    border: "none",
    borderRadius: "10px",
    cursor: "pointer",
    fontSize: "15px",
    fontWeight: "600",
    transition: "all 0.3s ease",
    display: "flex",
    alignItems: "center",
    gap: "10px",
    boxShadow: "0 4px 14px rgba(24, 144, 255, 0.4)",
  },
  saveButtonHover: {
    backgroundColor: "#096dd9",
    transform: "translateY(-2px)",
    boxShadow: "0 6px 20px rgba(24, 144, 255, 0.5)",
  },
  saveButtonDisabled: {
    opacity: 0.6,
    cursor: "not-allowed",
  },
  resetButton: {
    padding: "14px 24px",
    backgroundColor: "transparent",
    color: "#666",
    border: "2px solid #e8e8e8",
    borderRadius: "10px",
    cursor: "pointer",
    fontSize: "15px",
    fontWeight: "500",
    transition: "all 0.3s ease",
  },
  resetButtonHover: {
    borderColor: "#1890ff",
    color: "#1890ff",
  },
  message: {
    padding: "16px 20px",
    borderRadius: "10px",
    marginTop: "25px",
    display: "flex",
    alignItems: "center",
    gap: "12px",
    fontSize: "15px",
    fontWeight: "500",
  },
  successMessage: {
    backgroundColor: "#f6ffed",
    color: "#389e0d",
    border: "1px solid #b7eb8f",
  },
  errorMessage: {
    backgroundColor: "#fff2f0",
    color: "#cf1322",
    border: "1px solid #ffccc7",
  },
  infoMessage: {
    backgroundColor: "#e6f7ff",
    color: "#1890ff",
    border: "1px solid #91d5ff",
  },
};

const API_URL = "http://localhost:5000/api/donors";

const DonorProfile = () => {
  const { user, token, isAuthenticated, updateProfile } = useAuth();
  const [updates, setUpdates] = useState({});
  const [message, setMessage] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [hoveredCard, setHoveredCard] = useState(null);
  const [focusedInput, setFocusedInput] = useState(null);

  const handleChange = (e) => {
    setUpdates({ ...updates, [e.target.name]: e.target.value });
  };

  const handleReset = () => {
    setUpdates({});
    setMessage("");
  };

  const handleUpdate = async () => {
    if (Object.keys(updates).length === 0) {
      setMessage("No changes detected.");
      return;
    }

    setIsSubmitting(true);
    setMessage("");

    try {
      const res = await axios.put(`${API_URL}/profile`, updates, {
        headers: { Authorization: `Bearer ${token}` },
      });

      updateProfile(res.data);
      setUpdates({});
      setMessage(
        "Profile updated successfully! Changes synchronized globally."
      );

      // Auto-clear success message after 5 seconds
      setTimeout(() => {
        setMessage("");
      }, 5000);
    } catch (err) {
      setMessage(
        err.response?.data?.message ||
          "Error updating profile. Please try again."
      );
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!isAuthenticated || !user) {
    return (
      <div
        style={{
          ...styles.container,
          textAlign: "center",
          padding: "60px 40px",
          backgroundColor: "#fff",
        }}
      >
        <FiAlertCircle
          style={{ fontSize: "48px", color: "#ff4d4f", marginBottom: "20px" }}
        />
        <h3 style={{ color: "#1a1a1a", marginBottom: "12px" }}>
          Profile Not Available
        </h3>
        <p style={{ color: "#666", maxWidth: "400px", margin: "0 auto" }}>
          Please log in to access your donor profile.
        </p>
      </div>
    );
  }

  const hasChanges = Object.keys(updates).length > 0;

  return (
    <div style={styles.container}>
      {/* Header Section */}
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <h1 style={styles.title}>
            <FiUser size={32} /> Donor Profile
          </h1>
          <p style={styles.subtitle}>
            <FiRefreshCw size={14} />
            Real-time synchronization with global state
          </p>
        </div>
        <div style={styles.statusBadge}>
          <FiCheckCircle size={14} />
          Active Donor
        </div>
      </div>

      {/* Profile Information Section */}
      <div style={styles.profileSection}>
        <h2 style={styles.sectionTitle}>Personal Information</h2>
        <div style={styles.infoGrid}>
          {/* Name Card */}
          <div
            style={{
              ...styles.infoCard,
              ...(hoveredCard === "name" && styles.infoCardHover),
            }}
            onMouseEnter={() => setHoveredCard("name")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.infoLabel}>
              <FiUser size={18} />
              Full Name
            </div>
            <div style={styles.infoValue}>{user.name}</div>
          </div>

          {/* Email Card */}
          <div
            style={{
              ...styles.infoCard,
              ...(hoveredCard === "email" && styles.infoCardHover),
            }}
            onMouseEnter={() => setHoveredCard("email")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.infoLabel}>
              <FiMail size={18} />
              Email Address
            </div>
            <div style={styles.infoValue}>{user.email}</div>
          </div>

          {/* Blood Group Card */}
          <div
            style={{
              ...styles.infoCard,
              ...(hoveredCard === "blood" && styles.infoCardHover),
            }}
            onMouseEnter={() => setHoveredCard("blood")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.infoLabel}>
              <FiDroplet size={18} />
              Blood Group
            </div>
            <div style={styles.infoValue}>
              <span style={styles.bloodGroupDisplay}>
                <FiDroplet size={16} />
                {user.bloodGroup}
              </span>
            </div>
          </div>

          {/* Location Card */}
          <div
            style={{
              ...styles.infoCard,
              ...(hoveredCard === "location" && styles.infoCardHover),
            }}
            onMouseEnter={() => setHoveredCard("location")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.infoLabel}>
              <FiMapPin size={18} />
              Location
            </div>
            <div style={styles.infoValue}>{user.city}</div>
          </div>

          {/* Contact Card */}
          <div
            style={{
              ...styles.infoCard,
              ...(hoveredCard === "contact" && styles.infoCardHover),
            }}
            onMouseEnter={() => setHoveredCard("contact")}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.infoLabel}>
              <FiPhone size={18} />
              Contact Number
            </div>
            <div style={styles.infoValue}>{user.phone}</div>
          </div>
        </div>
      </div>

      {/* Update Section */}
      <div style={styles.updateSection}>
        <div style={styles.updateHeader}>
          <FiSave size={24} color="#389e0d" />
          <h3 style={styles.updateTitle}>Update Profile Information</h3>
        </div>

        <p style={styles.updateDescription}>
          Update your contact details and location information below. All
          changes are synchronized instantly.
        </p>

        <div style={styles.inputGrid}>
          {/* City Input */}
          <div style={styles.inputGroup}>
            <label style={styles.inputLabel}>
              <FiMapPin size={16} />
              City
            </label>
            <input
              type="text"
              name="city"
              placeholder={`Current: ${user.city}`}
              onChange={handleChange}
              onFocus={() => setFocusedInput("city")}
              onBlur={() => setFocusedInput(null)}
              style={{
                ...styles.input,
                ...(focusedInput === "city" && styles.inputFocus),
              }}
              value={updates.city || ""}
            />
          </div>

          {/* Phone Input */}
          <div style={styles.inputGroup}>
            <label style={styles.inputLabel}>
              <FiPhone size={16} />
              Phone Number
            </label>
            <input
              type="tel"
              name="phone"
              placeholder={`Current: ${user.phone}`}
              onChange={handleChange}
              onFocus={() => setFocusedInput("phone")}
              onBlur={() => setFocusedInput(null)}
              style={{
                ...styles.input,
                ...(focusedInput === "phone" && styles.inputFocus),
              }}
              value={updates.phone || ""}
            />
          </div>

          {/* Blood Group Input */}
          <div style={styles.inputGroup}>
            <label style={styles.inputLabel}>
              <FiDroplet size={16} />
              Blood Group
            </label>
            <input
              type="text"
              name="bloodGroup"
              placeholder={`Current: ${user.bloodGroup}`}
              onChange={handleChange}
              onFocus={() => setFocusedInput("bloodGroup")}
              onBlur={() => setFocusedInput(null)}
              style={{
                ...styles.input,
                ...(focusedInput === "bloodGroup" && styles.inputFocus),
              }}
              value={updates.bloodGroup || ""}
            />
          </div>
        </div>

        <div style={styles.buttonGroup}>
          <button
            onClick={handleUpdate}
            style={{
              ...styles.saveButton,
              ...(hasChanges && !isSubmitting && styles.saveButtonHover),
              ...(isSubmitting && styles.saveButtonDisabled),
            }}
            disabled={isSubmitting || !hasChanges}
            onMouseEnter={(e) => {
              if (hasChanges && !isSubmitting) {
                e.currentTarget.style.transform = "translateY(-2px)";
                e.currentTarget.style.boxShadow =
                  "0 6px 20px rgba(24, 144, 255, 0.5)";
              }
            }}
            onMouseLeave={(e) => {
              if (hasChanges && !isSubmitting) {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.boxShadow =
                  "0 4px 14px rgba(24, 144, 255, 0.4)";
              }
            }}
          >
            {isSubmitting ? (
              <>
                <FiRefreshCw className="spin" />
                Updating...
              </>
            ) : (
              <>
                <FiSave />
                Save Changes
              </>
            )}
          </button>

          {hasChanges && (
            <button
              onClick={handleReset}
              style={styles.resetButton}
              onMouseEnter={(e) => {
                e.currentTarget.style.borderColor = "#1890ff";
                e.currentTarget.style.color = "#1890ff";
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.borderColor = "#e8e8e8";
                e.currentTarget.style.color = "#666";
              }}
            >
              Reset Changes
            </button>
          )}
        </div>

        {/* Message Display */}
        {message && (
          <div
            style={{
              ...styles.message,
              ...(message.includes("successfully")
                ? styles.successMessage
                : message.includes("Error")
                ? styles.errorMessage
                : styles.infoMessage),
            }}
          >
            {message.includes("successfully") ? (
              <FiCheckCircle size={20} />
            ) : message.includes("Error") ? (
              <FiAlertCircle size={20} />
            ) : (
              <FiAlertCircle size={20} />
            )}
            {message}
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes spin {
          0% {
            transform: rotate(0deg);
          }
          100% {
            transform: rotate(360deg);
          }
        }
        .spin {
          animation: spin 1s linear infinite;
        }
      `}</style>
    </div>
  );
};

export default DonorProfile;
